gameforge.main_quest_lv94._290_sendLetter = "Weaken the Red Thieves "
gameforge.main_quest_lv94._300_say = "Admiral Angmur is looking for you. "
gameforge.main_quest_lv94._310_say = "A direct attack seems to be too risky to me at[ENTER]this time. Before we go on the offensive, we[ENTER]should weaken the enemy's troops. "
gameforge.main_quest_lv94._320_say = "The following groups live in the Thunder[ENTER]Mountains: the Giants, the Cyclops and the Red[ENTER]Thieves.[ENTER]We'll attack the Red Thieves first! "
gameforge.main_quest_lv94._330_say = "What weaknesses do the Red Thieves have? "
gameforge.main_quest_lv94._340_say = "The Red Chief always wears a special cape, a[ENTER]symbol of strength and invincibility. Rip him out[ENTER]of this cape - that will shatter the Red Thief[ENTER]Gang's motivation! "
gameforge.main_quest_lv94._350_say = "The Red Chief? That won't be an easy task... "
gameforge.main_quest_lv94._360_say = "You are right. But if there is anyone that is a[ENTER]match for him, it's you. Good luck! "
gameforge.main_quest_lv94._370_sayTitle = "The Red Chief "
gameforge.main_quest_lv94._380_say = "Defeat the Red Chief and rip his cape from him. "
gameforge.main_quest_lv94._390_say = "... failed? No, I don't believe it. Go take on[ENTER]the Red Chief again. I'm sure you'll beat him[ENTER]this time. "
gameforge.main_quest_lv94._400_1_notice = "You have defeated the Red Chief. "
gameforge.main_quest_lv94._400_2_notice = "Take his cape and bring it to Admiral Angmur. "
gameforge.main_quest_lv94._410_say = "Admiral Angmur is waiting for you. "
gameforge.main_quest_lv94._420_say = "The cape! I knew you would do it. We'll hang it[ENTER]up near to their camp so that they can see it [ENTER]that'll unnerve the Red Thief Gang.[ENTER]My people will take care of it. That way you can[ENTER]recover. "
gameforge.main_quest_lv94._430_sendLetter = "Weaken the Cyclops "
gameforge.main_quest_lv94._440_say = "Admiral Angmur would like to talk to you. "
gameforge.main_quest_lv94._450_say = "Our plan is working: when the Red Thieves saw the[ENTER]cape a great amount of disruption broke out. In[ENTER]the end, the demons and humans ended up fighting[ENTER]each other. Now this gang is completely[ENTER]disorganised.[ENTER]Next up for us are the Cyclops. "
gameforge.main_quest_lv94._460_say = "How are we to proceed? "
gameforge.main_quest_lv94._470_say = "The Cyclops worship their leader. If we eliminate[ENTER]the leadership squad, the Cyclops will break out[ENTER]in panic and scatter. Defeat General Outis %d[ENTER]times, Arges %d times and Polyphemos %d times. "
gameforge.main_quest_lv94._480_say = "It will be a pleasure. "
gameforge.main_quest_lv94._490_say = "Be careful. "
gameforge.main_quest_lv94._500_sayTitle = "Fight the Cyclops! "
gameforge.main_quest_lv94._510_say = "Defeat General Outis %d times, Arges %d times and[ENTER]Polyphemos %d times. "
gameforge.main_quest_lv94._520 = "I can hardly believe that you are having second[ENTER]thoughts about the Cyclops, of all creatures! Go![ENTER]I know that you can do it! "
gameforge.main_quest_lv94._530_1_notice = "You have defeated the leaders of the Cyclops. "
gameforge.main_quest_lv94._530_2_notice = "Report back to Admiral Angmur. "
gameforge.main_quest_lv94._540_say = "Go to Admiral Angmur. "
gameforge.main_quest_lv94._550_say = "The news of your deeds precedes you! The Cyclops[ENTER]are on the run.[ENTER]All that remain are the Giants. I will gather[ENTER]information on their weaknesses and then call for[ENTER]you. "
gameforge.main_quest_lv94._560_sendLetter = "Weaken the Giants "
gameforge.main_quest_lv94._570_say = "Admiral Angmur is calling for you. "
gameforge.main_quest_lv94._580_say = "My scouts have uncovered the Giants' weakness. "
gameforge.main_quest_lv94._590_say = "Tell me! "
gameforge.main_quest_lv94._600_say = "The Giants possess a totem that has great[ENTER]spiritual value. If we take it away from them, we[ENTER]will be able to break their will. "
gameforge.main_quest_lv94._610_say = "Where can I find this totem? "
gameforge.main_quest_lv94._620_say = "The totem has the form of a large piece of amber.[ENTER]To protect it from thieves, it was split into %d[ENTER]parts. King Wubba and his Officers carry these[ENTER]with them. "
gameforge.main_quest_lv94._630_say = "Understood. "
