gameforge.main_quest_lv97._250_say = {}
gameforge.main_quest_lv97._260_say = "Of course. "
gameforge.main_quest_lv97._270_sayTitle = "Fossil Earth "
gameforge.main_quest_lv97._280_say = "Go to the alchemist and ask him about the Fossil[ENTER]Earth. "
gameforge.main_quest_lv97._290_say = "Have you not forgotten something? You need to[ENTER]bring me the Fossil Earth. "
gameforge.main_quest_lv97._300_notice = "The alchemist has given you the Fossil Earth. Now[ENTER]return to Yak-Hwan. "
gameforge.main_quest_lv97._310_say = "Ah, the Fossil Earth. Very good, I shall get to[ENTER]work. I will report what I find out to the[ENTER]Admiral. "
gameforge.main_quest_lv97._320_sendLetter = "Through the back door "
gameforge.main_quest_lv97._330_say = "Admiral Angmur is looking for you. "
gameforge.main_quest_lv97._340_say = "We now know where the Temple Servant Yah-Ki is[ENTER]residing. But the base resembles a fortress [ENTER]guard troops patrol there around the clock. A[ENTER]frontal attack at this time would be pure[ENTER]suicide. "
gameforge.main_quest_lv97._350_say = "For this reason, we want to attack in an ambush.[ENTER]My men are searching for a suitable location.[ENTER]Distract the guards to provide us with more time. "
gameforge.main_quest_lv97._360_say = {}
gameforge.main_quest_lv97._370_say = "The greatest danger comes from a troop of guards[ENTER]who call themselves the Brigade of Black Death.[ENTER]This brigade consists of Moghu, Carnifex and[ENTER]General Albus. Their leader is Skeletos.[ENTER]Eliminate them, before they uncover my scouting[ENTER]troop. "
gameforge.main_quest_lv97._380_say = "Right you are. "
gameforge.main_quest_lv97._390_sayTitle = "Brigade of Black Death "
gameforge.main_quest_lv97._400_say = "Eliminate the Brigade of Black Death, comprising[ENTER]of Moghu, Carnifex, General Albus and Skeletos. "
gameforge.main_quest_lv97._410_say = "Defeat the members of the Brigade of Black Death[ENTER]before they uncover the scouting troop. "
gameforge.main_quest_lv97._420_say = "Report back to Admiral Angmur. "
gameforge.main_quest_lv97._430_say = "Our plan is ready to go! Whilst you distracted[ENTER]the Brigade of Black Death, my men have located a[ENTER]suitable position for an ambush. As soon as our[ENTER]attack strategy is ready, I will call for you. "
gameforge.main_quest_lv97._440_sendLetter = "The Temple Servant "
gameforge.main_quest_lv97._450_say = "Admiral Angmur needs your help! "
gameforge.main_quest_lv97._460_say = "Good, you are here. Skeletos, leader of the[ENTER]Brigade of Black Death, has survived and attacked[ENTER]my men just as they planned to storm the base.[ENTER]Nobody could get past him. "
gameforge.main_quest_lv97._470_say = {}
gameforge.main_quest_lv97._480_say = "You have defeated him once, please help my people[ENTER]on Gautama Cliff! Go up against Skeletos.[ENTER]Afterwards, storm the base and kill the Temple[ENTER]Servant Yah-Ki. "
gameforge.main_quest_lv97._490_say = "Understood. "
gameforge.main_quest_lv97._500_say = {}
gameforge.main_quest_lv97._510_sayTitle = "Defeat Yah-Ki "
gameforge.main_quest_lv97._520_say = "Defeat the Temple Servant Yah-Ki and stop her[ENTER]from summoning the army of the dead. "
gameforge.main_quest_lv97._530_say = "The decisive day has arrived. If we fall, the[ENTER]world falls with us. But if we are victorious,[ENTER]our deeds will still be sung in songs one hundred[ENTER]years from now. Stop the Servants of Darkness.[ENTER]Defeat the Temple Servant Yah-Ki! "
gameforge.main_quest_lv97._540_say = "Why are you here? The battle is not yet won![ENTER]Defeat the Temple Servant Yah-Ki. All our hopes[ENTER]rest on you. "
gameforge.main_quest_lv97._550_1_notice = "You have brought your opponent to their knees and[ENTER]victory is yours! "
gameforge.main_quest_lv97._550_2_notice = "There's no time for relaxing  the Temple Servant[ENTER]Yah-Ki is waiting for you. "
gameforge.main_quest_lv97._560_notice = "You have won! With a bloodcurdling scream, Yah-Ki[ENTER]sinks to the ground. "
gameforge.main_quest_lv97._570_say = "Aaargh! This cannot be! Defeated by a mortal...[ENTER]oh, Banshee, forgive me! I have failed. I could[ENTER]not complete my ta... "
gameforge.main_quest_lv97._580_say = "You search amongst her lifeless body and find a[ENTER]sealed letter. Return with it to Admiral Angmur. "
gameforge.main_quest_lv97._590_say = "Aye, praise be to the Dragon God! You are back,[ENTER]unharmed too! Did you defeat the Temple Servant? "
gameforge.main_quest_lv97._600_say = "Yes, I sent her back to the Hell that she crawled[ENTER]out from. At the point of death she begged the[ENTER]Banshee for forgiveness for her failure. She was[ENTER]carrying this. "
gameforge.main_quest_lv97._610_say = "A Sealed Letter. Show me! "
gameforge.main_quest_lv97._620_sayReward = "My Mistress, They swallowed our bait whole and[ENTER]your plan is working. The summoning of your[ENTER]undead army is moving forward and nobody will be[ENTER]able to stop us now. Once the soulless bodies[ENTER]start to march, we will attack Cape Dragon Fire[ENTER]and bury its population beneath us. We will then[ENTER]head across to the mainland and meet you in[ENTER]Fireland. The world will burn under our[ENTER]footsteps![ENTER]I bow deeply to you, Your eternal servant "
gameforge.main_quest_lv97._630_say = "A message to the banshee! Aye, the damnable witch[ENTER]is now waiting in vain in Fireland on her army of[ENTER]darkness.[ENTER]I thank you. Without your help, Cape Dragon Fire[ENTER]and its people would have been levelled to the[ENTER]ground by the army of the undead. You have not[ENTER]only saved us, you have also restored peace to[ENTER]the souls of our ancestors. I present this reward[ENTER]to you for all you have done. "
gameforge.main_quest_lv97._640_sayTitle = "The adventure continues as soon as you have[ENTER]reached level 98. "
gameforge.main_quest_lv97._650_say = "We needed a lot of time to locate the base.[ENTER]Please, we cannot lose any more time! "
gameforge.main_quest_lv97._660_say = "Yak-Hwan needs Fossil Earth? It's very fortunate[ENTER]that I haven't used it all yet. Here, you're[ENTER]welcome. Now bring it to him and give him my[ENTER]warmest greetings. "
