gameforge.new_quest_lv42._065_say = "You can get a Diamond Stone by chipping at the[ENTER]Vein of Diamond Ore with a Pickaxe. "
gameforge.new_quest_lv42._066_say = "The Blacksmith needs a Diamond Stone to make[ENTER]special spears for hunting whales. Look for Veins[ENTER]of Diamond Ore, so that you can mine Diamond[ENTER]Stone. If you don't have a Pickaxe for mining,[ENTER]you can buy one from Deokbae. "
gameforge.new_quest_lv42._067_say = "The Blacksmith is in %s. "
gameforge.new_quest_lv42._068_say = "We need another %s x Diamond Stones before we can[ENTER]start working on the spears. "
gameforge.new_quest_lv42._070_set_title = "The spears "
