gameforge.locale.monster_chat = {}
gameforge.locale.monster_chat_1001_1 = "Jeje, ¡todo el mundo morirá! ¡Kyaaaa! "
gameforge.locale.monster_chat_1001_2 = "¡Trozos, trozos, trozos, trozos! "
gameforge.locale.monster_chat_1001_3 = "¡No hay forma de escapar! "
gameforge.locale.monster_chat_1001_4 = "El suelo quedará impregnado con tu sangre. ¡Muere! "
gameforge.locale.monster_chat_1001_5 = "¡Puedes suplicar, pero eso no te hará ningún bien! "
gameforge.locale.monster_chat_1001_6 = "¡No puedes esconder tu miedo de mí! "
gameforge.locale.monster_chat_1002_1 = "¡Muerte! Muerte a todo el mundo... "
gameforge.locale.monster_chat_1002_2 = "¡Cerdos miserables! "
gameforge.locale.monster_chat_1002_3 = "Estás empezando a hacerme enfadar... "
gameforge.locale.monster_chat_1002_4 = {}
gameforge.locale.monster_chat_1002_5 = "Habrá sangre, humana. "
gameforge.locale.monster_chat_1003_1 = "¡Muerte y Perdición...! "
gameforge.locale.monster_chat_1003_2 = "¿Has visto las heridas que puede hacer una lanza en tu piel? "
gameforge.locale.monster_chat_1003_3 = "Sí, vamos, ¡márchate! Veremos hasta donde llegas... "
gameforge.locale.monster_chat_1003_4 = "Sólo mirar tus manos temblorosas hace que este rato merezca la pena completamente... "
gameforge.locale.monster_chat_1003_5 = "¿Tú, pedazo de escoria, te atreves a desafiarme? "
gameforge.locale.monster_chat_1003_6 = "¡La muerte será tu única acompañante en el futuro! "
gameforge.locale.monster_chat_1004_1 = "¡Tu alma será mía, humano! "
gameforge.locale.monster_chat_1004_2 = "Ah, ¡sangre fresca! "
gameforge.locale.monster_chat_1004_3 = "¡Parece que otra persona va a encontrar su muerte aquí! "
gameforge.locale.monster_chat_1004_4 = "Tienes que sufrir y sacar toda tu angustia... "
gameforge.locale.monster_chat_1004_5 = "¿Realmente piensas que esa ridícula arma de tu mano será capaz de protegerte? "
gameforge.locale.monster_chat_1004_6 = "Tu coraje te trajo hasta mí. Y ahora va a dejarte... "
gameforge.locale.monster_chat_1035_1 = "Si muero, ¡mi venganza te seguirá por toda la eternidad! "
gameforge.locale.monster_chat_1035_2 = "Haré que mueras y sufras despacio, muy despacio... "
gameforge.locale.monster_chat_1035_3 = "Mi placer será hacerte sufrir. "
gameforge.locale.monster_chat_1035_4 = "¡Tu cuerpo está temblando como el de una vieja bruja senil! "
gameforge.locale.monster_chat_1035_5 = "Puedo oler tu miedo, pequeño. "
gameforge.locale.monster_chat_1035_6 = "Creo que es tiempo de liberarte de unas cuantas partes de tu cuerpo. "
gameforge.locale.monster_chat_1036_1 = "¡Voy a mandarte a tu propio infierno personal! "
gameforge.locale.monster_chat_1036_2 = "Tu dolor te volverá loco, incluso antes de que mueras... "
gameforge.locale.monster_chat_1036_3 = "¡Puedes sollozar! ¡Puedes suplicar! Pero eso no te hará ningún bien. "
gameforge.locale.monster_chat_1036_4 = "Vas a experimentar la peor muerte que puedas imaginar... "
gameforge.locale.monster_chat_1036_5 = {}
gameforge.locale.monster_chat_1036_6 = "Tu poder se está debilitando. Ya estás al borde de la muerte... "
gameforge.locale.monster_chat_1037_1 = "¿Estás preparado para caminar hacia los pasillos de la muerte por la eternidad? "
gameforge.locale.monster_chat_1037_2 = "Deberías acostumbrarte a la idea de recibir oscuridad eterna... "
gameforge.locale.monster_chat_1037_3 = "Bienvenido al infierno (risotada) "
gameforge.locale.monster_chat_1037_4 = "¡Grita tanto como quieras! ¡Nadie va a oirte! "
gameforge.locale.monster_chat_1037_5 = "Si crees que puedes matarme así... ¡eres más ingenuo de lo que pensaba! "
gameforge.locale.monster_chat_1037_6 = "No está mal, pero eso aún no podrá salvarte de tu muerte. "
gameforge.locale.monster_chat_1038_1 = "Esto es el infierno "
gameforge.locale.monster_chat_1038_2 = "Voy a despellejar la piel de tus huesos. "
gameforge.locale.monster_chat_1038_3 = "Quiero oirte lloriquear antes de que mueras. "
gameforge.locale.monster_chat_1038_4 = "Sabes que no tiene sentido tener esperanza, ¿no? "
gameforge.locale.monster_chat_1038_5 = {}
gameforge.locale.monster_chat_1038_6 = "No, aún no vas a morir. Primero quiero que sepas cuantos golpes de espada puede soportar tu cuerpo. "
gameforge.locale.monster_chat_1039_1 = "Hasta que venga alguien nuevo... "
gameforge.locale.monster_chat_1039_2 = "¿Realmente piensas que tienes la más mínima posibilidad de escapar? "
gameforge.locale.monster_chat_1039_3 = "No es el suelo lo que se mueve... ¡son tus rodillas que están estremeciéndose de miedo! "
gameforge.locale.monster_chat_1039_4 = "¡Bienvenido al lugar en el que encontrarás tu muerte! "
gameforge.locale.monster_chat_1039_5 = "Puedo ver el pánico en tus ojos. "
gameforge.locale.monster_chat_1039_6 = "Oh, tan jóven y tan indefenso... "
gameforge.locale.monster_chat_1040_1 = "Vemos todos tus movimientos. "
gameforge.locale.monster_chat_1040_2 = "¿Apresurado? ¿Realmente quieres morir? "
gameforge.locale.monster_chat_1040_3 = "La muerte ya ha ocupado su lugar a tu lado... "
gameforge.locale.monster_chat_1040_4 = "¿Puedes oir mi campana repicando? ¡Será la última cosa que oigas! "
gameforge.locale.monster_chat_1040_5 = "Si esta herida no deja de sangrar, será peligroso para ti... "
gameforge.locale.monster_chat_1040_6 = "Puedes luchar... o puedes dejar que la muerte te abrace... "
gameforge.locale.monster_chat_1041_1 = "¿Por qué estás aquí si quieres vivir? "
gameforge.locale.monster_chat_1041_2 = "Ah, ¡cuerpos nuevos para destruir! "
gameforge.locale.monster_chat_1041_3 = "¡Fue tu decisión el venir aquí, humano! Y ahora tendrás que vivir con las consecuencias... "
gameforge.locale.monster_chat_1041_4 = "Resucitamos. ¡No podemos morir! "
gameforge.locale.monster_chat_1041_5 = "¡No tiene sentido atacarnos! (risotada) "
gameforge.locale.monster_chat_1041_6 = "Qué conseguirás: ¿agotamiento o muerte? "
gameforge.locale.monster_chat_1091_1 = "Has llegado hasta aquí... y ahora voy a mandarte a tu muerte. "
gameforge.locale.monster_chat_1091_2 = "¡Sacrifica tu alma y conviértete en uno de nosotros! "
gameforge.locale.monster_chat_1091_3 = "¡Mírate! Débil y lento... "
gameforge.locale.monster_chat_1091_4 = "¡Tu cuerpo y tu alma son míos! (risotada) "
gameforge.locale.monster_chat_1091_5 = "¿Agotado? Tus piernas están temblando... "
gameforge.locale.monster_chat_1091_6 = "¿Cuánto tiempo eres capaz de seguir así? (risotada) "
gameforge.locale.monster_chat_1093_1 = "¿Quién hubiera imaginado que hay algo peor que la muerte? "
gameforge.locale.monster_chat_1093_2 = "Bienvenido, hambriento de muerte. "
gameforge.locale.monster_chat_1093_3 = "Pagarás por tu indiscreción. Con sangre y tu alma. "
gameforge.locale.monster_chat_1093_4 = "Después de haber destruido tu cuerpo, ¡tu alma será mía! "
gameforge.locale.monster_chat_1093_5 = "¿Cuánto tiempo puedes aguantar? ¿Uh? "
gameforge.locale.monster_chat_1093_6 = "Eres divertido. Tu miedo y tu prisa me divierten... "
gameforge.locale.monster_chat_11000_1 = "¡Todos aquellos preocupados por su desarrollo deben escuchar! "
gameforge.locale.monster_chat_11000_10 = "Si quieres controlar el poder de la Naturaleza y el rayo, ¡recomiendo el entrenamiento de la Fuerza Curación! "
gameforge.locale.monster_chat_11000_11 = "¡Cada guerrero es diferente en su modo de pelear! ¡Céntrate en endurecer tu cuerpo! "
gameforge.locale.monster_chat_11000_12 = "La fuerza del Sura es la magia. ¡Trabaja en tu inteligencia, velocidad y fuerza corporal! "
gameforge.locale.monster_chat_11000_13 = "¡Usa las habilidades del ninja! ¡Céntrate en la velocidad y la fuerza corporal! "
gameforge.locale.monster_chat_11000_14 = "Los chamanes usan hechizos de regeneración, apoyo y combate. ¡La inteligencia es vital! "
gameforge.locale.monster_chat_11000_15 = "¡No olvides tu aprendizaje! "
gameforge.locale.monster_chat_11000_16 = "¡Conviértete en aprendiz si quieres entrenar tus habilidades! "
gameforge.locale.monster_chat_11000_17 = "¿Habilidades? Puedes ascender al rango de un maestro, se extiende aleatoriamente entre los niveles 17 y 21. "
gameforge.locale.monster_chat_11000_18 = "¿Quieres convertirte en un gran maestro? ¡Entonces necesitas un Manual de Habilidad especial! "
gameforge.locale.monster_chat_11000_19 = "¡En cuanto hayas terminado de leer el Manual de Habilidad, éste desaparece! "
gameforge.locale.monster_chat_11000_2 = "¡Todos los guerreros a partir del nivel 5, escuchadme! "
gameforge.locale.monster_chat_11000_20 = "¿Quieres leer el Manual de Habilidad otra vez? Entonces espera un día. "
gameforge.locale.monster_chat_11000_21 = "¡Aumenta tu inteligencia para mejorar tus habilidades Sura! "
gameforge.locale.monster_chat_11000_22 = "¡Experimenta los grandes poderes de un Sura en combate! "
gameforge.locale.monster_chat_11000_23 = "¿Sabes dónde está el templo de la secta misteriosa? ¡Está en medio del Valle de Seungryong! "
gameforge.locale.monster_chat_11000_24 = "¡Las cuevas de arañas han aparecido otra vez! ¡Están en el desierto Yongbi! "
gameforge.locale.monster_chat_11000_25 = "¡Finaliza tus misiones y utiliza las recompensas para mejorar! "
gameforge.locale.monster_chat_11000_26 = "¿Pesca? ¡Suena genial! ¿Ya has conseguido una caña y cebo de un pescador? "
gameforge.locale.monster_chat_11000_27 = "¿Quieres encontrar algo más fuerte para cazar? ¡Busca un portal! ¡Ve a un área de caza apropiada para tu nivel! "
gameforge.locale.monster_chat_11000_28 = "Ten cuidado de los enemigos de otros reinos. Nunca se sabe cuando te van a dar una puñalada por la espalda. "
gameforge.locale.monster_chat_11000_29 = "¿Quieres restaurar tus habilidades? ¡Ve con la anciana! ¡Asegúrate de llevar suficiente Yang! "
gameforge.locale.monster_chat_11000_3 = "¡Ve a entrenar con el maestro asesino si quieres ser conocido como un poderoso guerrero ofensivo! "
gameforge.locale.monster_chat_11000_30 = "¡Aspira al rango de maestro perfecto! ¡Es el único modo en el que puedes convertirte en un héroe verdadero! "
gameforge.locale.monster_chat_11000_31 = "Si quieres ir a otro campo de batalla o ciudad, busca a los ancianos en el continente. "
gameforge.locale.monster_chat_11000_4 = "Si quieres ser un experto en las habilidades del guerrero, ve y aprende del maestro del arte de la guerra. "
gameforge.locale.monster_chat_11000_5 = "Si quieres ser un Sura capaz de usar espadas mágicas, ¡elige entrenamiento de armamento! "
gameforge.locale.monster_chat_11000_6 = "¡Elige el entrenamiento de magia negra si quieres ser experto usando hechizos negros! "
gameforge.locale.monster_chat_11000_7 = "¡El entrenamiento arquero está reservado para ninjas que han alcanzado el nivel 5! "
gameforge.locale.monster_chat_11000_8 = "Si quieres convertirte en un ninja secreto, elige el entrenamiento de asesino. "
gameforge.locale.monster_chat_11000_9 = "Si quieres controlar batallas como el guerrero del Dios Dragón, ¡elige las enseñanzas del Dios Dragón! "
gameforge.locale.monster_chat_11002_1 = "¡Escuchad, todos aquellos que os preocupáis por vuestra salud! "
gameforge.locale.monster_chat_11002_10 = "Si quieres controlar el poder de la naturaleza y el rayo, ¡entra en entrenamiento de la Fuerza Curación! "
gameforge.locale.monster_chat_11002_11 = "Cada Guerrero es diferente en su modo de luchar. ¡Fortalece tu cuerpo! "
gameforge.locale.monster_chat_11002_12 = "La fuerza del Sura es la magia. Trabaja en tu inteligencia, velocidad y fuerza corporal. "
gameforge.locale.monster_chat_11002_13 = "¡Lleva las habilidades del ninja a un nivel superior! ¡Céntrate en la velocidad y fuerza corporal! "
gameforge.locale.monster_chat_11002_14 = "Los chamanes usan hechizos de regeneración, apoyo y combate. ¡La inteligencia es vital para ello! "
gameforge.locale.monster_chat_11002_15 = "¡No olvides el entrenamiento! "
gameforge.locale.monster_chat_11002_16 = "¡Conviértete en aprendiz si quieres entrenar tus habilidades! "
gameforge.locale.monster_chat_11002_17 = "¿Habilidades? Puedes ascender al rango de un maestro, se extiende aleatoriamente entre los niveles 17 y 21. "
gameforge.locale.monster_chat_11002_18 = "¿Quieres convertirte en un gran maestro? ¡Entonces necesitas un Manual de Habilidad especial! "
gameforge.locale.monster_chat_11002_19 = "¡En cuanto hayas terminado de leer el Manual de Habilidad, éste desaparece! "
gameforge.locale.monster_chat_11002_2 = "¡Todos los guerreros a partir del nivel 5, escuchadme! "
gameforge.locale.monster_chat_11002_20 = "¿Quieres leer el Manual de Habilidad otra vez? Entonces espera un día. "
gameforge.locale.monster_chat_11002_21 = "¡Aumenta tu inteligencia para mejorar tus habilidades Sura! "
gameforge.locale.monster_chat_11002_22 = "¡Experimenta los grandes poderes de un Sura en combate! "
gameforge.locale.monster_chat_11002_23 = "¿Sabes dónde está el templo de la secta misteriosa? ¡Está en medio del Valle de Seungryong! "
gameforge.locale.monster_chat_11002_24 = "¡Las cuevas de arañas han aparecido otra vez! ¡Están en el desierto Yongbi! "
gameforge.locale.monster_chat_11002_25 = "¡Finaliza tus misiones y utiliza las recompensas para mejorar! "
gameforge.locale.monster_chat_11002_26 = "¿Pesca? ¡Suena genial! ¿Ya has conseguido una caña y cebo de un pescador? "
gameforge.locale.monster_chat_11002_27 = "¿Quieres encontrar algo más fuerte para cazar? ¡Busca un portal! ¡Ve a un área de caza apropiada para tu nivel! "
