gameforge.locale.monster_chat_1105_1 = "El estado helado es bueno. "
gameforge.locale.monster_chat_1105_2 = "La montaña Sohan es sin duda la mejor. "
gameforge.locale.monster_chat_1105_3 = "Qué buen tiempo. "
gameforge.locale.monster_chat_1105_4 = "Serás exactamente como yo. "
gameforge.locale.monster_chat_1105_5 = "Puedes vivir para siempre, al menos hasta que el hielo se derrita. "
gameforge.locale.monster_chat_1105_6 = "¿Cómo crees que se sentirá, cuando lentamente te conviertas en un pedazo de hielo? "
gameforge.locale.monster_chat_1106_1 = "Humanos... ¡¿aquí?! "
gameforge.locale.monster_chat_1106_2 = "Cansado... pero suficientemente despierto para ti... "
gameforge.locale.monster_chat_1106_3 = "¿Alguien aquí, en mi colina?  "
gameforge.locale.monster_chat_1106_4 = "¡No tienes sitio aquí! "
gameforge.locale.monster_chat_1106_5 = "¡Mejor retrocede! "
gameforge.locale.monster_chat_1106_6 = "Extraño... ¿tan solitario? "
gameforge.locale.monster_chat_1191_1 = "Ha pasado mucho tiempo desde que vimos a un luchador experimentado aparecer por aquí. "
gameforge.locale.monster_chat_1191_2 = "Nada más que hacer salvo esperar y abrillantar armas... "
gameforge.locale.monster_chat_1191_3 = "¿Tengo que trabajar en ellos? "
gameforge.locale.monster_chat_1191_4 = {}
gameforge.locale.monster_chat_1191_5 = "Falta algo. No estoy entretenida. "
gameforge.locale.monster_chat_1191_6 = "¡Voy a acabar con el último dolor que has sentido nunca! "
gameforge.locale.monster_chat_1301_1 = "¡Un cuellolargo...! "
gameforge.locale.monster_chat_1301_2 = "Tu pálida piel va a palidecer aun más en un minuto. "
gameforge.locale.monster_chat_1301_3 = "¿Quieres pelear conmibo? ¡Bien! "
gameforge.locale.monster_chat_1301_4 = "¡Me has atacado! ¡Pweck! ¡No se te puede permitir sobrevivir! "
gameforge.locale.monster_chat_1301_5 = "¡Débil, humano débil! "
gameforge.locale.monster_chat_1301_6 = "¡Veamos cuánto puedes aguantar! "
gameforge.locale.monster_chat_1302_1 = "¿Irte? Ni lo pienses. "
gameforge.locale.monster_chat_1302_2 = "Bien... Acércate... ¡Pwiehk! "
gameforge.locale.monster_chat_1302_3 = "La espada está agitándose en mi mano. ¡Pwiehk! "
gameforge.locale.monster_chat_1302_4 = "¡No sobrevivirás a este encuentro! "
gameforge.locale.monster_chat_1302_5 = "¡Aquí va mi espada! "
gameforge.locale.monster_chat_1302_6 = "Doloroso, ¿no? (risotada) "
gameforge.locale.monster_chat_1303_1 = "Incluso la hierba sobre la que estabas parado, puede matar accidentalmente. "
gameforge.locale.monster_chat_1303_2 = "Ten cuidado con la hierba. "
gameforge.locale.monster_chat_1303_3 = "Bien. Ven a mí. (risotada) "
gameforge.locale.monster_chat_1303_4 = "¿Osas acercarte a mí? Bien.. ¡entonces puedes vivir con las consecuencias! "
gameforge.locale.monster_chat_1303_5 = "¡Muere! ¡Muere! "
gameforge.locale.monster_chat_1303_6 = "¿Te arrepientes de haber venido? "
gameforge.locale.monster_chat_1305_1 = "¿Quién me derrotará? "
gameforge.locale.monster_chat_1305_2 = "¿Tú? ¿Quieres pelear? "
gameforge.locale.monster_chat_1305_3 = "¡Ven aquí si quieres! "
gameforge.locale.monster_chat_1305_4 = "No estás pensando en huir, ¿no? "
gameforge.locale.monster_chat_1305_5 = "Has infravalorado el alcance de mi espada. "
gameforge.locale.monster_chat_1305_6 = "¿Asustado? (risotada) "
gameforge.locale.monster_chat_1901_1 = "¿A quién transformaré hoy? "
gameforge.locale.monster_chat_1901_2 = "Oh, me gustas... "
gameforge.locale.monster_chat_1901_3 = "¿Qué te parece? Mi querida diversión... "
gameforge.locale.monster_chat_1901_4 = "¡Eh! Hazlo bien y no corras como un cobarde. "
gameforge.locale.monster_chat_1901_5 = "¿La visión de mí ha hecho ya que un escalofrío recorra tu espina dorsal? "
gameforge.locale.monster_chat_1901_6 = "¿No quieres gritar un poco? (risotada) "
gameforge.locale.monster_chat_20001_1 = "Pareces nervioso... Note preocupes que no explotará. "
gameforge.locale.monster_chat_20001_2 = "A veces, sólo quiero dar un paseo. "
gameforge.locale.monster_chat_20001_3 = "Es tan aburrido. Incluso mis nietos ya no me visitan. "
gameforge.locale.monster_chat_20001_4 = "Cuando te haces viejo, necesitas a otras personas. "
gameforge.locale.monster_chat_20001_5 = "Mis mejores días se han ido. "
gameforge.locale.monster_chat_20002_1 = "El sol es tan cálido. "
gameforge.locale.monster_chat_20002_2 = "Magnífico tiempo para conseguir que un trapo mojado se seque, ¿no? "
gameforge.locale.monster_chat_20002_3 = "¿Dónde está ese perezoso bueno para nada de marido? "
gameforge.locale.monster_chat_20002_4 = "Se dice a sí mismo padre y no se avergüenza de sus malos hábitos, ¡incluso delante de sus niños! "
gameforge.locale.monster_chat_20002_5 = "Los libros son libros. No gana dinero. Sólo lee libros. "
gameforge.locale.monster_chat_20003_1 = "Oooh, dududu, bebé. ¿Has dormido bien? "
gameforge.locale.monster_chat_20003_2 = "Sonríes como un pequeño ángel. "
gameforge.locale.monster_chat_20003_3 = "Tu mamá es tan feliz porque estás sano. "
gameforge.locale.monster_chat_20003_4 = "¿Otra vez quieres algo de atención? ¿Tienes hambre? "
gameforge.locale.monster_chat_20003_5 = "Buen chico... Duerme, mi niño, duerme. "
gameforge.locale.monster_chat_20004_1 = "Un día cualquiera. "
gameforge.locale.monster_chat_20004_2 = "¿Dónde voy a conseguir algo de comida hoy? "
gameforge.locale.monster_chat_20004_3 = "Es siempre lo mismo con esos portadores de espada. "
gameforge.locale.monster_chat_20004_4 = "Veamos. Quizá debería ir al restaurante de Octavio. "
gameforge.locale.monster_chat_20004_5 = "Oh, por cierto, parece que que no hay más guerra... "
gameforge.locale.monster_chat_20005_1 = "El alma del maestro alfarero corre por las yemas de sus dedos. "
gameforge.locale.monster_chat_20005_2 = "Aún cuando el país era un caos, se podían encontrar luchadores por todas partes. "
gameforge.locale.monster_chat_20005_3 = "Todo el mundo quiere luchar. "
gameforge.locale.monster_chat_20005_4 = "Rezo por un mundo tranquilo y pacífico. "
gameforge.locale.monster_chat_20005_5 = "Eh, tú, tómate un minuto para relajarte y pensar... "
gameforge.locale.monster_chat_20006_1 = "Oh Dios mío, ¿qué debo hacer? "
gameforge.locale.monster_chat_20006_2 = "No puedo encontrar el más mínimo indicio de mi hermano, pero él prometío que estaría aquí. "
gameforge.locale.monster_chat_20006_3 = "Y ahora, todo lo que veo aquí son luchadores con espadas. "
gameforge.locale.monster_chat_20006_4 = "Echo de menos a mi hermano. "
gameforge.locale.monster_chat_20006_5 = "¿Alguien ha visto a mi hermano? "
gameforge.locale.monster_chat_20007_1 = "Hm, ¡es una carne realmente fresca! "
gameforge.locale.monster_chat_20007_2 = "Es bueno que la gente coma más carne ahora. "
gameforge.locale.monster_chat_20007_3 = "Los negocios van bien, pero la carne aún es poco frecuente. "
gameforge.locale.monster_chat_20007_4 = "¿Cuándo va a llegar la carne que pedimos? "
gameforge.locale.monster_chat_20007_5 = "Si no llega a tiempo, tendremos que ir a cazar. "
gameforge.locale.monster_chat_20008_1 = "Espero que los clientes vengan en tropel ya que el tiempo es tan bueno... "
gameforge.locale.monster_chat_20008_2 = "Seguro que el perro vago vendrá pronto, y querrá algo de comer. "
gameforge.locale.monster_chat_20008_3 = "Oh Dios mío, ¡qué hombre tan pobre! "
gameforge.locale.monster_chat_20008_4 = "Solía ser un gran guerrero. "
gameforge.locale.monster_chat_20008_5 = "Me pregunto por qué no lucha más. "
gameforge.locale.monster_chat_20009_1 = "¡Estos jovenzuelos... tan animados y llenos de dinamismo! "
gameforge.locale.monster_chat_20009_2 = "Los guerreros se ven mucho mejor cuando llevan armas. "
gameforge.locale.monster_chat_20009_3 = "Esta mente asesina es algo que todo el mundo tiene hoy en día. "
gameforge.locale.monster_chat_20009_4 = "Controla tu corazón, enfoca tus pensamientos y tranquiliza tu alma. "
gameforge.locale.monster_chat_20009_5 = "No existe ninguna receta infalible para la felicidad, pero sí algunas normas simples que puedes seguir. "
gameforge.locale.monster_chat_20010_1 = "¡Te traje un gran objeto! "
gameforge.locale.monster_chat_20010_2 = "¡Mercancías de países lejanos, piezas extraordinarias! "
gameforge.locale.monster_chat_20010_3 = "¡Venid aquí! Todo el mundo, ¡venid! "
gameforge.locale.monster_chat_20010_4 = "Vamos, gente, ¡comprad! ¡No tendréis la oportunidad otra vez! "
gameforge.locale.monster_chat_20010_5 = "Argh, ¿por qué hay tantos ladrones aquí? "
gameforge.locale.monster_chat_20011_1 = "Son tan secos estos días... "
gameforge.locale.monster_chat_20011_2 = "La cosecha del cereal no es tan buena como solía ser. "
gameforge.locale.monster_chat_20011_3 = "¿Por qué no florecen más los campos? "
gameforge.locale.monster_chat_20011_4 = "El grano está seco y sabe a podrido. "
gameforge.locale.monster_chat_20011_5 = "Es una mala señal. La labranza es la base de nuestra vida. "
gameforge.locale.monster_chat_20012_1 = "¡Pastel de arroz! ¡Delicioso pastel de arroz! "
gameforge.locale.monster_chat_20012_2 = "¡Vendo pastel de arroz dulce! "
gameforge.locale.monster_chat_20012_3 = "Sí, los negocios han sido bastante malos últimamente. "
gameforge.locale.monster_chat_20012_4 = "Quizá deba intentarlo en otra ciudad. "
gameforge.locale.monster_chat_20012_5 = "Pero hay tantos tigres... "
gameforge.locale.monster_chat_20013_1 = "En días como esos, estar en un barco es lo mejor que puede pasarte. "
gameforge.locale.monster_chat_20013_2 = "¡Tiempo perfecto para pescar! "
gameforge.locale.monster_chat_20013_3 = "Ahora solo falta un nudo... ¡Listo! Ningún pez será capaz de morder este anzuelo. "
gameforge.locale.monster_chat_20013_4 = "¡El maldito oso arruinó mi caña! "
gameforge.locale.monster_chat_20013_5 = "Debería remendar otra vez mis redes. "
gameforge.locale.monster_chat_20014_1 = "¡Uh! "
gameforge.locale.monster_chat_20014_2 = "No te preocupes por mí; así es como soy. "
gameforge.locale.monster_chat_20014_3 = "¡Jaaaaa! "
gameforge.locale.monster_chat_20014_4 = "Qué debo hacer para que ella... Oh no, ¿qué acabo de decir? "
gameforge.locale.monster_chat_20014_5 = "Al fin y al cabo, no puedo ser valiente. "
gameforge.locale.monster_chat_20015_1 = "Este hacha me ha servido bien durante 20 años. "
gameforge.locale.monster_chat_20015_2 = "Aún está afilada, aunque quizá no es tan buena como solía ser. "
gameforge.locale.monster_chat_20015_3 = "Quiero usarla y reforzarla. "
gameforge.locale.monster_chat_20015_4 = "Pero necesito piedras llenas de minerales para eso. "
gameforge.locale.monster_chat_20015_5 = "¿Pero dónde debería conseguirlas? "
gameforge.locale.monster_chat_20016_1 = "Los clientes vienen en tropel. "
gameforge.locale.monster_chat_20016_2 = "Espero estar listo a tiempo. "
gameforge.locale.monster_chat_20016_3 = "Golpe tras golpe y no se queja... "
gameforge.locale.monster_chat_20016_4 = "¡Así debe sonar un martillo! "
gameforge.locale.monster_chat_20016_5 = "Qué calor hace aquí. "
gameforge.locale.monster_chat_20017_1 = "No tengo ganas de tocar un instrumento. "
gameforge.locale.monster_chat_20017_2 = "El estado de mi instrumento es realmente deplorable. "
gameforge.locale.monster_chat_20017_3 = "¿Quizás debería compararme otro instrumento? "
gameforge.locale.monster_chat_20017_4 = "Mi instrumento está viejo y usado... ¡Adoro las cosas viejas! "
gameforge.locale.monster_chat_20017_5 = "Tengo que encontrar urgentemente un remplazo para las cuerdas rotas. "
gameforge.locale.monster_chat_20018_1 = "¡Estoy tan ocupado! "
gameforge.locale.monster_chat_20018_2 = "Cada día, tenemos nueva gente herida. "
gameforge.locale.monster_chat_20018_3 = "¡No hay tiempo para la diversión personal! "
gameforge.locale.monster_chat_20018_4 = "Y nos estamos quedando sin medicina. "
gameforge.locale.monster_chat_20018_5 = "Espero que alguien pueda ir y conseguir algunos ingredientes para la medicina. "
gameforge.locale.monster_chat_20019_1 = "Durante 30 años, he estado vagando por las montañas con mi arco. "
gameforge.locale.monster_chat_20019_2 = "Con esto, mantuve a mi esposa y crié tres hijos. "
gameforge.locale.monster_chat_20019_3 = "Es una vida dura, pero vale la pena. "
gameforge.locale.monster_chat_20019_4 = "Pero ahora, la cuerda del arco se rompió y no puedo cazar más. ¡No puedo alimentar así a mi familia! "
gameforge.locale.monster_chat_20019_5 = "Si no puedo reparar este arco, tendré que comprar uno nuevo. Pero no tengo suficiente dinero para hacerlo. "
gameforge.locale.monster_chat_20020_1 = "Eh, ¿qué estás mirando? "
gameforge.locale.monster_chat_20020_2 = "¡Vete de aquí! "
gameforge.locale.monster_chat_20020_3 = "Oh, ¿durante cuánto tendré que esconderme? "
gameforge.locale.monster_chat_20020_4 = "¿Moriré olvidado en algún sitio? "
gameforge.locale.monster_chat_20020_5 = "Aaaah, espero que alguien me ayude... "
gameforge.locale.monster_chat_20021_1 = "El día de hoy va a acabar exactamente como otro día cualquiera. "
gameforge.locale.monster_chat_20021_2 = "¿Qué pudo hacer ahora, uh? "
gameforge.locale.monster_chat_20021_3 = "Nunca pensé que acabaría así. "
gameforge.locale.monster_chat_20021_4 = "Aún no puedo creer que ya no esté aquí nunca más. "
gameforge.locale.monster_chat_20021_5 = "No se merecía eso. "
gameforge.locale.monster_chat_20022_1 = "Otro gran día. "
gameforge.locale.monster_chat_20022_2 = "El marido de Ariyoung ha desparecido. "
gameforge.locale.monster_chat_20022_3 = "Quise ayudar, pero esta tenacidad... "
gameforge.locale.monster_chat_20022_4 = "Si se hubiera casado conmigo, podría haber dejado atrás su aburrida vida. "
gameforge.locale.monster_chat_20022_5 = "Quizá debería haber expresado mejor mis sentimientos por ella. "
gameforge.locale.monster_chat_20023_1 = "¡Oh no! ¿Qué haré si mi mujer lo descubre? "
gameforge.locale.monster_chat_20023_2 = "¡Si tan sólo pudiera vivir de leer libros! "
gameforge.locale.monster_chat_20023_3 = "Los libros son tan misteriosos. "
gameforge.locale.monster_chat_20023_4 = "No puedo vivir así para siempre, pero... "
gameforge.locale.monster_chat_20023_5 = "Espero que pueda hacer algo por mi mujer, pero... "
gameforge.locale.monster_chat_20024_1 = "¡Vamos, pon algo de esfuerzo en ello! "
gameforge.locale.monster_chat_20024_2 = "¿Qué estás haciendo? ¿No puedes darte prisa? "
gameforge.locale.monster_chat_20024_3 = "¡Imagínate como un guerrero maneja su espada y hazlo exactamente igual! "
gameforge.locale.monster_chat_20024_4 = "¿Dónde está este idiota? ¿Dónde está ahora? "
gameforge.locale.monster_chat_20024_5 = "¡Este perro! ¡No eres hombre! ¡Ja! "
gameforge.locale.monster_chat_2091_1 = "Corta, corta... pelo, cuellos... "
gameforge.locale.monster_chat_2091_2 = "Tengo algo para ti: ¡el suelo eterno! "
gameforge.locale.monster_chat_2091_3 = "Respira profundamente. ¡No te preocupes, que es solo veneno! "
gameforge.locale.monster_chat_2091_4 = "¡Voy a dejar solo tu cara! Ésta será un bonito adorno... "
gameforge.locale.monster_chat_2091_5 = {}
gameforge.locale.monster_chat_2091_6 = "Envuelto en seda serás un aperitivo hermoso para más tarde... "
gameforge.locale.monster_chat_2092_1 = "Ya no quedan muchos de nosotros. "
gameforge.locale.monster_chat_2092_2 = "No son las criaturas bípedas que se supone que gobiernan aquí. "
gameforge.locale.monster_chat_2092_3 = "Oh, parece que alguien fue atrapado en la tela de araña. "
gameforge.locale.monster_chat_2092_4 = "Soy pacífico. Pero no me gusta ser molestado. "
gameforge.locale.monster_chat_2092_5 = "¿Eso es todo lo que puedes ofrecer? "
gameforge.locale.monster_chat_2092_6 = "¡Nadie pude medirse conmigo! "
gameforge.locale.monster_chat_2104_1 = "¿Por qué los humanos son tan húmedos en su interior? Esto hace que mi espada se oxide. "
gameforge.locale.monster_chat_2104_2 = "¿Se ha secado ya el veneno de tu aguijón? "
gameforge.locale.monster_chat_2104_3 = "¿Qué te parece luchar? "
gameforge.locale.monster_chat_2104_4 = "¿Agotado ya por el calor? "
gameforge.locale.monster_chat_2104_5 = "Me gusta dejar a la gente atrás en el desierto. "
gameforge.locale.monster_chat_2104_6 = "Pobre de tí, enterrado así en el desierto. "
gameforge.locale.monster_chat_2105_1 = "Voy a contarte un secreto - ¡Odio la suciedad! "
gameforge.locale.monster_chat_2105_2 = "Hace mucho calor, ¿no? "
gameforge.locale.monster_chat_2105_3 = "Este lugar es peligroso. "
gameforge.locale.monster_chat_2105_4 = {}
gameforge.locale.monster_chat_2105_5 = "¡Voy a golpearte entre los ojos! "
gameforge.locale.monster_chat_2106_1 = "Parece que hay algo por aquí que puedo acribillar. "
gameforge.locale.monster_chat_2106_2 = "Oh sí, puedo ver a mi siguiente presa. "
gameforge.locale.monster_chat_2106_3 = "(Risotada) Me gusta; ¡me gusta mucho! "
gameforge.locale.monster_chat_2106_4 = "Ese de allí está bien gordito. ¡Es mío! "
gameforge.locale.monster_chat_2106_5 = {}
gameforge.locale.monster_chat_2106_6 = "Me gusta el olor de la sangre. "
gameforge.locale.monster_chat_2107_1 = "(Balanceo) No hay mucho que hacer estos días. (balanceo) "
gameforge.locale.monster_chat_2107_2 = "Eh, ¡esos están realmente gordos! "
gameforge.locale.monster_chat_2107_3 = "Esto le gustará al líder... "
gameforge.locale.monster_chat_2107_4 = {}
gameforge.locale.monster_chat_2107_5 = "Movámos las piernas. "
gameforge.locale.monster_chat_2107_6 = "Esta flecha es sólo para ti. "
gameforge.locale.monster_chat_2108_1 = "Si consigo uno, yo... "
gameforge.locale.monster_chat_2108_2 = "Eh, ¿por qué miras tras de tí? "
gameforge.locale.monster_chat_2108_3 = "¿Crees que soy fácil de derrotar? ¿Hm? "
gameforge.locale.monster_chat_2108_4 = "¡Muy mal que te hayas tenido que topar conmigo en el desierto! "
gameforge.locale.monster_chat_2108_5 = "Ah, este sentimiento; ¡lo disfrutaré para siempre! "
gameforge.locale.monster_chat_2108_6 = "¡Gracias! "
gameforge.locale.monster_chat_2191_1 = "Huelo humanos - ¡taan sabrosos! "
gameforge.locale.monster_chat_2191_2 = "Bien. Ven a mí, ven a mí... "
gameforge.locale.monster_chat_2191_3 = {}
gameforge.locale.monster_chat_2191_4 = "¡Soy tan afortunado de que te hayas perdido y hayas acabado aquí! "
gameforge.locale.monster_chat_2191_5 = "¡Párate! ¡Kkkkkk! "
gameforge.locale.monster_chat_2191_6 = "Poco a poco voy a rasgarlo de tu carne... "
gameforge.locale.monster_chat_2202_1 = "Acércate más para que pueda abrasar tu piel. "
gameforge.locale.monster_chat_2202_2 = "¿Sabes que tipo de dolor puede provocar tanta cantidad de calor? "
gameforge.locale.monster_chat_2202_3 = "Quizá pueda enfriar mis llamas con algo de sangre fresca. "
gameforge.locale.monster_chat_2202_4 = "Tu sangre... déjame probarla. "
gameforge.locale.monster_chat_2202_5 = "No tiene sentido soportar una lucha. "
gameforge.locale.monster_chat_2202_6 = "¡Sé como yo! Sólo llamas y calor. "
gameforge.locale.monster_chat_2204_1 = "Mientras esté yo aquí, ¡nadie entrará! "
gameforge.locale.monster_chat_2204_2 = "Por alguna razón, estas empezando a molestarme. "
gameforge.locale.monster_chat_2204_3 = {}
gameforge.locale.monster_chat_2204_4 = "Kwuuh, ¿sabes dónde estás? "
gameforge.locale.monster_chat_2204_5 = {}
gameforge.locale.monster_chat_2204_6 = "¡Eres un oponente bastante patético para mí! "
gameforge.locale.monster_chat_2205_1 = "Una espada sólo es útil a manos de un guerrero. "
gameforge.locale.monster_chat_2205_2 = "Conserva la espada en tu mano. "
gameforge.locale.monster_chat_2205_3 = "La guerra se avecina. "
gameforge.locale.monster_chat_2205_4 = "Y ahora, ¡luchamos hasta que alguien muera! "
gameforge.locale.monster_chat_2205_5 = "¿Quieres resistirte a la muerte mucho más? "
gameforge.locale.monster_chat_2205_6 = "Sí, no deberías morir demasiado rápido. "
gameforge.locale.monster_chat_2206_1 = "La lava es el elixir de la vida. "
gameforge.locale.monster_chat_2206_2 = "¡La lava me mantiene vivo y será tu muerte! "
gameforge.locale.monster_chat_2206_3 = "¿No es maravilloso este mundo de lava resplandeciente? "
gameforge.locale.monster_chat_2206_4 = "¿Osas molestarme? "
gameforge.locale.monster_chat_2206_5 = "Deberías irte si valoras tu vida. "
gameforge.locale.monster_chat_2206_6 = "Puede que lamentes mi presencia aquí. "
gameforge.locale.monster_chat_2291_1 = "Hace un bonito día. Todo lo que quiero es tener algo de paz y tranquilidad. "
gameforge.locale.monster_chat_2291_2 = "Nada es comparable al país de Doyyumhwan. "
gameforge.locale.monster_chat_2291_3 = "Un poco de sangre fresca sería un buen cambio. "
gameforge.locale.monster_chat_2291_4 = "Parece que buscas tu muerte, de otro modo no estarías molestándome ahora.  "
gameforge.locale.monster_chat_2291_5 = "¿Ya te estás arrepintiendo de haber aparecido? "
gameforge.locale.monster_chat_2291_6 = "Es demasiado tarde para escapar. Afronta tu destino. "
gameforge.locale.monster_chat_301_1 = "Estos momentos tensos me estan poniendo nervioso. "
gameforge.locale.monster_chat_301_2 = "Hace dos días, una chica joven fue apaleada por animales salvajes en el área. "
gameforge.locale.monster_chat_301_3 = "Los animales salvajes se están volviendo más y más agresivos. Deberíamos intentarlo y matar los más posibles. "
gameforge.locale.monster_chat_301_4 = "No estoy de buen humor hoy. Los extranjeros deberían mantenerse alejados de mi camino. "
gameforge.locale.monster_chat_301_5 = "El último guerrero que maté, suplicó por su vida. "
gameforge.locale.monster_chat_302_1 = "Alguien tiene que hacer algo con esas piedras. "
gameforge.locale.monster_chat_302_2 = "No me fio de la tranquilidad de la mañana. "
gameforge.locale.monster_chat_302_3 = "La tensión en el aire me incomoda. "
gameforge.locale.monster_chat_302_4 = "Estas puntas de flecha han sido muy bien forjadas. "
gameforge.locale.monster_chat_302_5 = "Si fuese tú, tendría cuidado. Esa flecha está apuntando directamente a tu corazón. "
gameforge.locale.monster_chat_302_6 = "Debo ir ahí y matar a una de esas bestias. "
gameforge.locale.monster_chat_303_1 = "Un buen entrenamiento en artes marciales es esencial en estos tiempos. "
gameforge.locale.monster_chat_303_2 = "¿Qué pasa si de repente irrumpen soldados? "
gameforge.locale.monster_chat_303_3 = "¿Por qué me estás mirando así? "
gameforge.locale.monster_chat_303_4 = "Los cobardes tienen una pequeña posibilidad de sobrevivir. "
gameforge.locale.monster_chat_303_5 = "No te sobreestimes. "
gameforge.locale.monster_chat_303_6 = "Parece que alguien te ha entrenado muy bien. "
gameforge.locale.monster_chat_304_1 = "Incluso mis ancestros consiguieron defenderse en la batalla en estas áreas. "
gameforge.locale.monster_chat_304_2 = "Tienes que estar alerta y diestro ahí fuera. "
gameforge.locale.monster_chat_304_3 = "Ni pienses en hacer perder mi tiempo. "
gameforge.locale.monster_chat_304_4 = "Entonces, ¿cómo se siente el saber que tu vida está en peligro? "
gameforge.locale.monster_chat_304_5 = "¡Oh no! ¿Ya abandonas? "
gameforge.locale.monster_chat_304_6 = "El sufrimiento llega rápido. "
gameforge.locale.monster_chat_305_1 = "¡Oh tío! Esa espada parece fantástica. "
gameforge.locale.monster_chat_305_2 = "¡Sé más persistente e incrementa tu poder! "
gameforge.locale.monster_chat_305_3 = "¿Crees que puedes ser tan fuerte como yo? "
gameforge.locale.monster_chat_305_4 = "Bien. Pongámonos serios. "
gameforge.locale.monster_chat_305_5 = "¡Oh Dios! ¡Soy sólo una débil mujer! "
gameforge.locale.monster_chat_305_6 = "Pareces cansado. ¿Por qué no abandonas ya? Jeje. "
gameforge.locale.monster_chat_306_1 = "¿Qué va ha hacer Serangyi hoy, hm? "
gameforge.locale.monster_chat_306_2 = "Estoy tan aburrido. ¡Juega conmigo! "
gameforge.locale.monster_chat_306_3 = "Tengo que decir que me gusta más el Sura. "
gameforge.locale.monster_chat_306_4 = "¡Eh! ¿Qué me dices a una lucha? "
gameforge.locale.monster_chat_306_5 = "¡No me subestimes solo porque soy una mujer! "
gameforge.locale.monster_chat_306_6 = "El dolor sólo dura un momento. "
gameforge.locale.monster_chat_307_1 = "Quiero salir de aquí a toda costa. "
gameforge.locale.monster_chat_307_2 = "¿Cuánta seguridad tienen las casas de los miembros solteros? "
gameforge.locale.monster_chat_307_3 = "¡Calla, galones! "
gameforge.locale.monster_chat_307_4 = "Déjame derrotarte de un solo golpe. "
gameforge.locale.monster_chat_307_5 = "¡Ni pienses en escapar! "
gameforge.locale.monster_chat_307_6 = "Así pues, ¿por qué no empezamos? "
gameforge.locale.monster_chat_394_1 = "No lo olvides: todos los hombres son enemigos. "
gameforge.locale.monster_chat_394_2 = "Si una persona tiene una cara bonita, podemos ser menos estrictos. "
gameforge.locale.monster_chat_394_3 = "¿Qué, quieres luchar? "
gameforge.locale.monster_chat_394_4 = "No durará mucho, pero dolerá. "
gameforge.locale.monster_chat_394_5 = "Oh, realmente pareces dolorido. "
gameforge.locale.monster_chat_394_6 = "Adiós; ¡vete al infierno! "
gameforge.locale.monster_chat_401_1 = "Así pues, ¿qué haces en el Juramento Blanco? "
gameforge.locale.monster_chat_401_2 = "¿Dónde vas? "
gameforge.locale.monster_chat_401_3 = "Eh, ¿quieres luchar? "
gameforge.locale.monster_chat_401_4 = "Jeje, esquivas mis ataques. "
gameforge.locale.monster_chat_401_5 = "¿Crees que puedes escapar de mí? "
gameforge.locale.monster_chat_401_6 = "¡Aquí esta mi golpe! "
gameforge.locale.monster_chat_402_1 = "Dios, ¡estoy tan cansado! "
gameforge.locale.monster_chat_402_2 = "Eh, ¡bonita armadura! ¿Cuánto costó? "
gameforge.locale.monster_chat_402_3 = "Pero tenemos buen tiempo. "
gameforge.locale.monster_chat_402_4 = "Oh, ¡has entrenado mucho! ¡Genial! "
gameforge.locale.monster_chat_402_5 = "¿Qué piensas de mis habilidades? Buenas, ¿eh? Jeje. "
gameforge.locale.monster_chat_402_6 = "¿Qué pasa? Por supuesto, ¡es la velocidad insana! "
gameforge.locale.monster_chat_403_1 = "¿Por qué no voy a cazar? "
gameforge.locale.monster_chat_403_2 = "Nadie está atacando nuestra aldea hoy. "
gameforge.locale.monster_chat_403_3 = "Si quires luchar, entonces lucha, ¡bastardo! "
gameforge.locale.monster_chat_403_4 = "Jeje, eres sólo un principiante. "
gameforge.locale.monster_chat_403_5 = "¿Qué debería golpear primero, tu brazo o tu pierna? "
gameforge.locale.monster_chat_403_6 = "¡Aquí viene mi flecha! "
gameforge.locale.monster_chat_404_1 = "Pronto tengo que ir a la aldea y entrenar un tiempo. "
gameforge.locale.monster_chat_404_2 = "Me siento tan entumecido. "
gameforge.locale.monster_chat_404_3 = "¿Qué hay hoy para cenar? "
gameforge.locale.monster_chat_404_4 = "Estoy mirando, así que grita tanto como quieras. "
gameforge.locale.monster_chat_404_5 = "¿Has esperado demasiado? ¡Qué me dices a balancear esa espada tuya primero! "
gameforge.locale.monster_chat_404_6 = "¡La espada suena apasionante! "
gameforge.locale.monster_chat_405_1 = "¡Esta espada está lista! "
gameforge.locale.monster_chat_405_2 = "Veamos quién será asesinado primero por esta espada. "
gameforge.locale.monster_chat_405_3 = "Eh, ¿qué estás mirando? "
gameforge.locale.monster_chat_405_4 = "Primero, ¡tu barriga! "
gameforge.locale.monster_chat_405_5 = "Me gustaría usarte de vaina. "
gameforge.locale.monster_chat_405_6 = "Me gustaría usarte de vaina. "
gameforge.locale.monster_chat_406_1 = "Me muero de hambre. "
gameforge.locale.monster_chat_406_2 = "¿Hay algo para comer ahí? "
gameforge.locale.monster_chat_406_3 = "¿Por qué estoy todavía hambriento después de toda esta comida? "
gameforge.locale.monster_chat_406_4 = "Aaargh, ¡muere! "
gameforge.locale.monster_chat_406_5 = "¡Mi estómago determinará esta lucha! "
gameforge.locale.monster_chat_406_6 = "¡Es la elección entre cielo e infierno! "
gameforge.locale.monster_chat_491_1 = "¡No puedo soportarlo más! Me pica el cuerpo. "
gameforge.locale.monster_chat_491_2 = "Oh sí. Puedo ver algunos cerdos. "
gameforge.locale.monster_chat_491_3 = "¿En el suelo? "
gameforge.locale.monster_chat_491_4 = "Acabaré contigo de un golpe. "
gameforge.locale.monster_chat_491_5 = "¡Será un bonito recuerdo! "
gameforge.locale.monster_chat_491_6 = "¡Muere! "
gameforge.locale.monster_chat_492_1 = "Mi cuerpo se ha ablandado. "
gameforge.locale.monster_chat_492_2 = "¿Por qué no lo ejercito más? "
gameforge.locale.monster_chat_492_3 = "Asustado, ¿eh? "
gameforge.locale.monster_chat_492_4 = "Ah, sólo una nueva víctima para mi arma giratoria. "
gameforge.locale.monster_chat_492_5 = "¿Puedes ver el patrón de mi ataque? ¿No? ¡Tu pérdida! "
gameforge.locale.monster_chat_492_6 = "¿Lo sabías? Incluso el corte más pequeño no es bueno para tu salud. "
gameforge.locale.monster_chat_493_1 = "¡Es tan incómodo tener una mano como esta! "
gameforge.locale.monster_chat_493_2 = "¿Qué estás mirando? ¿No habías visto nunca a una persona con una mano como esta? "
gameforge.locale.monster_chat_493_3 = "Verdaderamente me irritáis. "
gameforge.locale.monster_chat_493_4 = "¿Quieres que te pegue una paliza? Si no, huye. ¿Entendido? "
gameforge.locale.monster_chat_493_5 = "¡Te mostraré mi gran golpe! "
gameforge.locale.monster_chat_493_6 = "¡Sé que estas mirando mi mano! "
gameforge.locale.monster_chat_494_1 = "Eh tú, si quieres luchar, ¡ven aquí! "
gameforge.locale.monster_chat_494_2 = "Creo que soy bastante impresionante. "
gameforge.locale.monster_chat_494_3 = "¿Por qué no planeamos algo? "
gameforge.locale.monster_chat_494_4 = "No, no puedes irte todavía. ¡Lucha! "
gameforge.locale.monster_chat_494_5 = "¡Aquí tienes! "
gameforge.locale.monster_chat_494_6 = "¡Estás casi muerto! "
gameforge.locale.monster_chat_501_1 = "¡Juega conmigo! ¡No te vayas! "
gameforge.locale.monster_chat_501_2 = "Yo podría esculpir algo bonito de tus huesos... "
gameforge.locale.monster_chat_501_3 = "Aún no hemos ejercitado. "
gameforge.locale.monster_chat_501_4 = "¡Qué gran armadura! Quiero tenerla. "
gameforge.locale.monster_chat_501_5 = "Desde ahora, encontrarás difícil ignorar mis ataques. "
gameforge.locale.monster_chat_501_6 = "Jeje, ¿sorprendido? "
gameforge.locale.monster_chat_502_1 = "La belleza no lo es todo. "
gameforge.locale.monster_chat_502_2 = "No mires, ¡lucha! "
gameforge.locale.monster_chat_502_3 = "No me gusta el modo en el que me estás mirando. ¡Lucha! "
gameforge.locale.monster_chat_502_4 = "¡Es un honor morir por mi espada! "
gameforge.locale.monster_chat_502_5 = {}
gameforge.locale.monster_chat_502_6 = "¡Excelente! "
gameforge.locale.monster_chat_503_1 = "Eh, ¡no toques! "
gameforge.locale.monster_chat_503_2 = "Parece que la cuerda de mi arco está cortada. "
gameforge.locale.monster_chat_503_3 = "Maldición, esos idiotas estuvieron aquí. "
gameforge.locale.monster_chat_503_4 = "No está mal. ¡Pero vas a morir de todos modos! "
gameforge.locale.monster_chat_503_5 = "¿Te parece bien? ¡Es estupendo! "
gameforge.locale.monster_chat_503_6 = "¡Aquí viene la flecha! "
gameforge.locale.monster_chat_504_1 = "Todo el mundo, ¡venid aquí! "
gameforge.locale.monster_chat_504_2 = "La, la, la, estoy silbando una canción... "
gameforge.locale.monster_chat_504_3 = "¡Qué gran tiempo! "
gameforge.locale.monster_chat_504_4 = {}
gameforge.locale.monster_chat_504_5 = "Bien, ¡quiero que te decidas a luchar! "
gameforge.locale.monster_chat_504_6 = "¡Aquí! ¡Venid! "
gameforge.locale.monster_chat_601_1 = "¡Me muero de hambre! "
gameforge.locale.monster_chat_601_2 = "Quiero comer algo de fruta. "
gameforge.locale.monster_chat_601_3 = "Bien, el buen pescado lo hará. ¡Al ataque! "
gameforge.locale.monster_chat_601_4 = "¡No corras! ¡Quédate aquí! "
gameforge.locale.monster_chat_601_5 = "Gané. ¡Es obvio! "
gameforge.locale.monster_chat_601_6 = "¿Ves? ¡Fallaste! "
gameforge.locale.monster_chat_601_7 = "Deberías tener cuidado, ¡Jaja! "
gameforge.locale.monster_chat_602_1 = "¿Dónde estoy? "
gameforge.locale.monster_chat_602_2 = "Buen tiempo. "
gameforge.locale.monster_chat_602_3 = "¿Vienes a darme caza? ¡Absurdo! "
gameforge.locale.monster_chat_602_4 = "Eres mi presa. "
gameforge.locale.monster_chat_602_5 = "Qué tortura, ¿no? Bien, acabaré contigo en un instante. "
gameforge.locale.monster_chat_602_6 = "Mira lo fuerte que soy: ¡puedo balancear este enorme garrote! "
gameforge.locale.monster_chat_603_1 = "¡Aburrido! "
gameforge.locale.monster_chat_603_2 = "Me pica el cuerpo entero. "
gameforge.locale.monster_chat_603_3 = "Quienquiera que sea el primero. "
gameforge.locale.monster_chat_603_4 = "¿Me golpeas primero? ¡Voy a sacarte de fuera de aquí! "
gameforge.locale.monster_chat_603_5 = "Una vez que entras, nunca saldrás. ¡No hay escapatoria! "
gameforge.locale.monster_chat_603_6 = "Este será el último golpe. ¡Acabaré contigo! "
gameforge.locale.monster_chat_604_1 = "He hecho un sabroso veneno nuevo. ¿Quieres probarlo? "
gameforge.locale.monster_chat_604_2 = "¡Quiero ver cómo funciona! Toma, ¡cógelo! "
gameforge.locale.monster_chat_604_3 = "¡Eh tú! Ven aquí, ¿quieres ser mi conejillo de indias? "
gameforge.locale.monster_chat_604_4 = "¡Te envenenaré! "
gameforge.locale.monster_chat_604_5 = "Oh genial, ¡un conejillo de indias! "
gameforge.locale.monster_chat_604_6 = "¡No hay diversión si te rindes tan rápidamente! Vamos, ¡defiéndete! "
gameforge.locale.monster_chat_631_1 = "Un momento. Estoy buscando algo para cazar. "
gameforge.locale.monster_chat_631_2 = "No sirve para nada que te muevas tan lentamente. ¡Muévete! "
gameforge.locale.monster_chat_631_3 = "¡Tengo tanta hambre! "
gameforge.locale.monster_chat_631_4 = "¡Es genial haber encontrado algo para cazar! "
gameforge.locale.monster_chat_631_5 = "¡Deja ir toda esperanza de supervivencia! ¡No sobrevivirás! "
gameforge.locale.monster_chat_631_6 = "¡Ahora averiguarás cómo sienta el dolor verdadero! "
gameforge.locale.monster_chat_632_1 = "¡Aburriiiiiiiido! "
gameforge.locale.monster_chat_632_2 = "¿Hay algo utilizable como cebo? "
gameforge.locale.monster_chat_632_3 = "¡Ven y lucha si quieres! "
gameforge.locale.monster_chat_632_4 = "¿Quieres ser un guerrero? ¡Entonces muéstrame lo que tienes! "
gameforge.locale.monster_chat_632_5 = {}
gameforge.locale.monster_chat_632_6 = "¡Escapa de la aldea si quieres vivir! "
gameforge.locale.monster_chat_633_1 = "Es doloroso morir de hambre. "
gameforge.locale.monster_chat_633_2 = "Oh, ¡están viniendo justo hacia nosotros! "
gameforge.locale.monster_chat_633_3 = "¿Qué me dices de un duelo? "
gameforge.locale.monster_chat_633_4 = "¡No me compares con esos bastardos! "
gameforge.locale.monster_chat_633_5 = "¡Aquí viene mi golpe de trueno! ¡Ja! "
gameforge.locale.monster_chat_633_6 = "¡Aquellos que escapan ahora no son verdaderos guerreros! "
gameforge.locale.monster_chat_634_1 = "Esta vez, ¡el veneno debería ser mejor! "
gameforge.locale.monster_chat_634_2 = "Si tienes hambre, los murciélagos son realmente sabrosos. "
gameforge.locale.monster_chat_634_3 = "¿Qué me dices a una lucha? "
gameforge.locale.monster_chat_634_4 = "Una vez que mi veneno entre en tu cuerpo, no hay modo alguno de curarlo. "
gameforge.locale.monster_chat_634_5 = "Mi magia consumirá todo en llamas, ¡jejeje! "
gameforge.locale.monster_chat_634_6 = "¡Arde! ¡Ja, ja! "
gameforge.locale.monster_chat_635_1 = "¡Nunca olvides afilar tu espada! La siguiente lucha está más próxima de lo que crees. "
gameforge.locale.monster_chat_635_2 = "¿Oscuro? "
gameforge.locale.monster_chat_635_3 = "¡Muere y despierta en la otra vida! "
gameforge.locale.monster_chat_635_4 = "¿Derrotarme? ¿De verdad? "
gameforge.locale.monster_chat_635_5 = "¡Lucha como un guerrero! (balanceo) "
gameforge.locale.monster_chat_635_6 = "Vamos, sólo un poco más. "
gameforge.locale.monster_chat_636_1 = "Ja, estoy buscando una buena pelea. "
gameforge.locale.monster_chat_636_2 = "Pelearse es divertido. (risotada) "
gameforge.locale.monster_chat_636_3 = "¡Quiero pelear a todas horas! "
gameforge.locale.monster_chat_636_4 = "¡Ja, muy bien! ¡Inténtalo otra vez! "
gameforge.locale.monster_chat_636_5 = "¡Mira mi espada girando! "
gameforge.locale.monster_chat_636_6 = "¡Te arrepentirás de golpearme! "
gameforge.locale.monster_chat_636_7 = "¡Tan débil! ¡Que ridículo! "
gameforge.locale.monster_chat_637_1 = "No quiero ser molestado. "
gameforge.locale.monster_chat_637_2 = "Ve a dormir. ¡Para siempre! "
gameforge.locale.monster_chat_637_3 = "¿Aún no estás dormido? "
gameforge.locale.monster_chat_637_4 = "¡Hay mucho que hacer! ¡Sigamos! "
gameforge.locale.monster_chat_637_5 = "¡No te quedes perplejo por mi poder! "
gameforge.locale.monster_chat_637_6 = "¡Te pulverizaré! "
gameforge.locale.monster_chat_691_1 = "Bien, ¡los oponentes fuertes duran más! ¡Defiéndete! "
gameforge.locale.monster_chat_691_2 = "¿Qué? ¿Qué parezco? "
gameforge.locale.monster_chat_691_3 = "¡No me compares con ese principiante! "
gameforge.locale.monster_chat_691_4 = "¡A él no le gustas y a mí tampoco! "
gameforge.locale.monster_chat_691_5 = "Luchemos. "
gameforge.locale.monster_chat_691_6 = "¡Hasta la muerte! "
gameforge.locale.monster_chat_701_1 = "Mira, ¡una mariposa! ¡Que bonita! "
gameforge.locale.monster_chat_701_2 = "¿No ves la verdad del cielo y la tierra? "
gameforge.locale.monster_chat_701_3 = "Más tarde, será demasiado tarde para creer. ¡Cree ahora! "
gameforge.locale.monster_chat_701_4 = "¡Cree en la doctrina de los esotéricos! "
gameforge.locale.monster_chat_701_5 = "¡Aquellos que duden serán asesinados! "
gameforge.locale.monster_chat_701_6 = "¡Sé inteligente abrazando a la muerte! "
gameforge.locale.monster_chat_702_1 = "La verdad de la creencia esotérica está en el entendimiento de la orden del cielo. "
gameforge.locale.monster_chat_702_2 = "¡La filosofía de la creencia esotérica es la orden celestial! "
gameforge.locale.monster_chat_702_3 = "Verdad esotérica; verdad del Dios Tigre; Sabaha Sabaha. "
gameforge.locale.monster_chat_702_4 = "¿Osas dudar de la verdad del cielo y la tierra? "
gameforge.locale.monster_chat_702_5 = {}
gameforge.locale.monster_chat_702_6 = "¡Siente mis garras! "
gameforge.locale.monster_chat_703_1 = "Cuando la verdad del Dios Tigre suene por toda la eternidad, todo el sufrimiento acabará. "
gameforge.locale.monster_chat_703_2 = "La verdad de la creencia esotérica está en el entendimiento de la orden del cielo. "
gameforge.locale.monster_chat_703_3 = "Sabaha Sabaha. ¡Danos conocimiento! "
gameforge.locale.monster_chat_703_4 = "¡No lo dudes! "
gameforge.locale.monster_chat_703_5 = "¡Recibe el trueno del cielo! "
gameforge.locale.monster_chat_703_6 = "¡Golpe del Juicio! "
gameforge.locale.monster_chat_704_1 = "La verdad de la creencia esotérica está en el entendimiento de la orden del cielo. "
gameforge.locale.monster_chat_704_2 = "Verdad esotérica; verdad del Dios Tigre; Sabaha Sabaha. "
gameforge.locale.monster_chat_704_3 = "Piensas que soy un payaso, ¿no? ¡No por mucho! "
gameforge.locale.monster_chat_704_4 = "¡El líder de esta secta es un santo y está por encima de todos los seres vivos! "
gameforge.locale.monster_chat_704_5 = "¡Recibe las llamas del infierno! "
gameforge.locale.monster_chat_704_6 = "Apuesto a que nunca habías visto un diapasón tan grande como este antes, ¿eh? "
gameforge.locale.monster_chat_705_1 = "Verdad esotérica; verdad del Dios Tigre; Sabaha Sabaha. "
gameforge.locale.monster_chat_705_2 = "La verdad de la creencia esotérica está en el entendimiento de la orden del cielo. "
gameforge.locale.monster_chat_705_3 = "¡Sólo porque soy más pequeño que mi hermano, no significa que voy a aguantar de todo! "
gameforge.locale.monster_chat_705_4 = "¡Dolor eterno y oscuridad te aguardan! "
gameforge.locale.monster_chat_705_5 = {}
gameforge.locale.monster_chat_705_6 = "Este no es el fin. ¡Sólo va mejor! "
gameforge.locale.monster_chat_706_1 = "¡Pedimos paz y privacidad! ¡Por favor! "
gameforge.locale.monster_chat_706_2 = "¡Si reina el miedo, la verdad del Dios Tigre ayuda! "
gameforge.locale.monster_chat_706_3 = "La verdad de la fe esotérica es el orden y entendimiento del cielo. "
gameforge.locale.monster_chat_706_4 = "¡Vamos todos al infierno! "
gameforge.locale.monster_chat_706_5 = "¡No te dejaré ir! "
gameforge.locale.monster_chat_706_6 = "Te daré todo lo que tengo. "
gameforge.locale.monster_chat_707_1 = "Te lo ruego, ¡por favor, sálvanos! "
gameforge.locale.monster_chat_707_2 = "La verdad de la creencia esotérica está en el entendimiento de la orden del cielo. "
gameforge.locale.monster_chat_707_3 = "Verdad esotérica; verdad del Dios Tigre; Sabaha Sabaha. "
gameforge.locale.monster_chat_707_4 = "(Sonora carcajada) Las puertas del infierno están abiertas. ¡Muere! "
gameforge.locale.monster_chat_707_5 = "Te lo advierto: ¡No me obligues a usar la tabla! "
gameforge.locale.monster_chat_707_6 = "¡Ya he visto tu muerte! "
gameforge.locale.monster_chat_791_1 = "¿Cómo puedo contarte en unas pocas palabras sobre la verdad del Dios Tigre? "
gameforge.locale.monster_chat_791_2 = "Oigo los cánticos de los creyentes. "
gameforge.locale.monster_chat_791_3 = "¡Está bien! ¡Si tienes remordimientos, serás rescatado! "
gameforge.locale.monster_chat_791_4 = "¡Sufres un dolor eterno, porque cuestionas la verdad! "
gameforge.locale.monster_chat_791_5 = "Déjame enseñarte. "
gameforge.locale.monster_chat_791_6 = "No lo consideres como un dolor - es la instrucción. "
gameforge.locale.monster_chat_901_1 = "Puede que no sea guapo, pero me divierto mucho, ¡jijiji! "
gameforge.locale.monster_chat_901_2 = "¡Tienes que ser como nosotros! "
gameforge.locale.monster_chat_901_3 = "Reúnete con nosotros, ¡jiji! "
gameforge.locale.monster_chat_901_4 = "¡Jaaa, jaja! "
gameforge.locale.monster_chat_901_5 = "¡Allá vamos! "
gameforge.locale.monster_chat_903_1 = "¡Esto es el infierno! "
gameforge.locale.monster_chat_903_2 = "¡Ahhhhh! "
gameforge.locale.monster_chat_903_3 = "¡No podemos hacer esto solos! ¡No! "
gameforge.locale.monster_chat_903_4 = "¡Tienes que probar el dolor, también! "
gameforge.locale.monster_chat_903_5 = "¿Qué tal un intercambio? ¡Tú me das tu alma y yo te daré mi sufrimiento! "
gameforge.locale.monster_chat_903_6 = "¡Pronto encontrarás paz eterna! "
gameforge.locale.monster_chat_904_1 = "¡El dolor! ¡El dolor! "
gameforge.locale.monster_chat_904_2 = "¡Aargghh, maldición! ¡Mi brazo está ardiendo! "
gameforge.locale.monster_chat_904_3 = "¿Por qué tenemos que sufrir? Oh, ¿por qué? "
gameforge.locale.monster_chat_904_4 = "¡Sufrir esta enfermedad misteriosa no es tan malo! "
gameforge.locale.monster_chat_904_5 = {}
gameforge.locale.monster_chat_904_6 = "¡Ahhhh! "
gameforge.locale.monster_chat_905_1 = "¡Ahhhhh! "
gameforge.locale.monster_chat_905_2 = "¡Te maaaaaaaaato! "
gameforge.locale.monster_chat_905_3 = "¡No mires simplemente! ¡Únete! "
gameforge.locale.monster_chat_905_4 = "¡Seamos amigos! "
gameforge.locale.monster_chat_905_5 = "Ya estás infectado. "
gameforge.locale.monster_chat_905_6 = "Ahora eres mi amigo. "
gameforge.locale.monster_chat_906_1 = "¡Jijiji! "
gameforge.locale.monster_chat_906_2 = "¡Ven y siente mi flecha! "
gameforge.locale.monster_chat_906_3 = "¡Eres realmente molesto! "
gameforge.locale.monster_chat_906_4 = "Voy a infectarte con la enfermedad misteriosa. "
gameforge.locale.monster_chat_906_5 = "Buen olor. "
gameforge.locale.monster_chat_906_6 = "¡Muere! "
gameforge.locale.monster_chat_907_1 = "Y si encuentro otro factor de enfermedad... "
gameforge.locale.monster_chat_907_2 = "(Risotada) ¡Será divertido! "
gameforge.locale.monster_chat_907_3 = "Hmmm, ¿látigo o pinza...? "
gameforge.locale.monster_chat_907_4 = "Ahhhhh, ¡siiiiiiiii! "
gameforge.locale.monster_chat_907_5 = "¡Déjame contagiarte la enfermedad! "
gameforge.locale.monster_chat_907_6 = "¡Esta causa tenaz de la enfermedad misteriosa! "
