gameforge.locale.old_pirate.baekgo_order_find_virus_text_2 = "Mata a todo aquel que haya sido infectado con la[ENTER]enfermedad misteriosa y llévale una muestra del[ENTER]patógeno a Baek-Go. Él hará la medicina para el[ENTER]pirata viejo. "
gameforge.locale.old_pirate.baekgo_order_too_late = "Baek-Go:[ENTER]Otro ya ha entregado una muestra del patógeno.[ENTER]Envié la medicina al pirata viejo; él debe estar[ENTER]bien ahora. "
gameforge.locale.old_pirate.baekgo_start_say = "Baek-Go:[ENTER]¿Él tuvo contacto con una persona infectada sólo[ENTER]una vez y su herida empezó a picar y supurar? "
gameforge.locale.old_pirate.baekgo_start_say2 = "Baek-Go:[ENTER]¡Oh Dios! ¡Estoy bastante seguro de que está[ENTER]infectado con esta enfermedad misteriosa! ¡Si no[ENTER]es tratado, la enfermedad se extenderá por toda[ENTER]la aldea! "
gameforge.locale.old_pirate.baekgo_start_say3 = "Baek-Go:[ENTER]Él no debe contagiar a nadie. Debes curarlo de[ENTER]inmediato. Para ello, necesitamos una muestra del[ENTER]germen patógeno de esta enfermedad misteriosa.[ENTER]Puedes obtener una muestra de las personas[ENTER]infectadas que viven en Monte Sohan, en el norte. "
gameforge.locale.old_pirate.baekgo_success_say = "Baek-Go:[ENTER]¡Lo hiciste! Espera un momento, haré la medicina[ENTER]ahora mismo. [DELAY value;1000] [/DELAY][ENTER] Tenía[ENTER]prisa, así que no sé si realmente funcionará,[ENTER]pero al menos debería sentirse mejor.[ENTER]Llévale la medicina lo antes posible. "
gameforge.locale.old_pirate.baekgo_talk = "El pirata viejo me envía. "
gameforge.locale.old_pirate.baekgo_talk_about_old_pirate = "La enfermedad del pirata viejo "
gameforge.locale.old_pirate.complete_say = "Pirata viejo:[ENTER]Todavía pica. Pero me siento mejor ahora. Supongo[ENTER]que podré vivir así. "
gameforge.locale.old_pirate.do_you_get_the_medicine = "Aún no tengo la medicina. "
gameforge.locale.old_pirate.do_you_mean_this_medicine = "¡Aquí está la cura! "
gameforge.locale.old_pirate.etc_shop_start_say = "Vendedora:[ENTER]¿El pirata viejo? ¡Por supuesto que le conozco![ENTER]Estás hablando sobre ese tipo que maldice[ENTER]constantemente y se rasca, ¿no? Viene de vez en[ENTER]cuando para comprar medicamento líquido. Gasta[ENTER]varios miles de Yang en medicina roja. "
gameforge.locale.old_pirate.etc_shop_start_say2 = "Vendedora:[ENTER]¿De dónde demonios saca tanto Yang si ni siquiera[ENTER]trabaja? Ya le he dicho varias veces que vaya a[ENTER]ver a un médico, pero no me escucha.[ENTER]La medicina roja alivia solo los síntomas, pero[ENTER]no cura la enfermedad. Me temo que esto no[ENTER]acabará bien. "
gameforge.locale.old_pirate.etc_shop_talk_about_old_pirate = "¿Conoces al pirata viejo? "
gameforge.locale.old_pirate.order_accept = "Te ayudaré "
gameforge.locale.old_pirate.order_accept_answer = "Pirata viejo:[ENTER]¡Genial! ¡Buena suerte![ENTER]El médico estará muy probablemente en la ciudad. "
