gameforge.locale.old_pirate.order_find_medicine_text_2 = "Ve a ver a Baek-Go en la ciudad y pregúntale por[ENTER]la medicina para el pirata viejo. "
gameforge.locale.old_pirate.order_refuse = "Lo siento, no puedo hacerlo. "
gameforge.locale.old_pirate.order_refuse_answer = "Pirata viejo:[ENTER]Bien, entonces no tengo nada más que decirte. "
gameforge.locale.old_pirate.order_say1 = "Pirata viejo:[ENTER]Maldición, ¿qué quieres de mí? ¡Majadero! La[ENTER]vieja bruja del almacén general te habló sobre[ENTER]mí, ¿no?[ENTER]Sí, todo lo que dice es cierto. Pero no tengo[ENTER]tiempo para ir a ver al médico. ¡Tengo tantos[ENTER]enemigos! "
gameforge.locale.old_pirate.order_say2 = "Pirata viejo:[ENTER]¡Nunca se sabe si uno de esos bastardos aparecerá[ENTER]delante tuyo y te apuñalará! Pero el picor es tan[ENTER]terrible; ¡no puedo aguantarlo más! ¿Qué dices?[ENTER]¿Puedes ir al médico y conseguir la medicina? Si[ENTER]la consigues, te recompensaré bien. "
gameforge.locale.old_pirate.order_too_late = "Pirata viejo:[ENTER]Otro ya trajo la medicina. Bueno, ¡eso significa[ENTER]que no hay recompensa para ti! "
