gameforge.main_quest_lv47._50_sayTitle = "Quinta página: "
gameforge.main_quest_lv47._510_say = "El diario describe el intento de sacrificar[ENTER]Espadachines Serpiente y Arqueros Serpiente para[ENTER]llevar a cabo brujería oscura. Ve al Desierto[ENTER]Yongbi y mata Espadachines Serpiente. Busca más[ENTER]información. "
