gameforge.main_quest_lv78._50_say = "¡Lo has conseguido! Por fin tenemos todas las %d[ENTER]unidades del Cuarzo Rojo Infernal que[ENTER]necesitamos. ¡Tu valentía y tu fuerza no dejan de[ENTER]sorprenderme! "
