gameforge.new_quest_premium_lv4._290_say_reward = "Vuelve con %s Almizcle(s). "
gameforge.new_quest_premium_lv4._300_say = "Tengo que llevar algo de Almizcle al Vendedor de[ENTER]la Tienda de Armas. Se supone que debo dejarme[ENTER]guiar por la moneda que gira, encargar un raro[ENTER]aceite de perfume y recogerlo del Almacenero. "
gameforge.new_quest_premium_lv4._30_say = "Echo de menos a mi amor, el Vendedor de la Tienda[ENTER]de Armas. Durante semanas ha estado cantando bajo[ENTER]mi ventana cada noche y ahora no he sabido nada[ENTER]de él durante días. Quizá tenga miedo de mi[ENTER]padre. ¿Puedes averiguar qué le pasa? "
gameforge.new_quest_premium_lv4._310_say_title = "El Vendedor de la Tienda de Armas "
gameforge.new_quest_premium_lv4._320_say = "Oh, gracias. ¿Así que dices que simplemente se ha[ENTER]retrasado? Que alivio. Voy a esperarle esta[ENTER]noche. "
