gameforge.locale.monster_chat = {}
gameforge.locale.monster_chat_1001_1 = "Haha, tout le monde mourra! Kayaaaa! "
gameforge.locale.monster_chat_1001_2 = "En tout petits morceaux .... "
gameforge.locale.monster_chat_1001_3 = "Hehehehe "
gameforge.locale.monster_chat_1001_4 = "Kukieh! Meurs! Haha. "
gameforge.locale.monster_chat_1001_5 = "hehehe "
gameforge.locale.monster_chat_1001_6 = "T'as une sale tête, déformée par la peur! Héhéhé. "
gameforge.locale.monster_chat_1002_1 = "Haha! "
gameforge.locale.monster_chat_1002_2 = "Porcs! "
gameforge.locale.monster_chat_1002_3 = "Ils me tapent vraiment sur les nerfs. "
gameforge.locale.monster_chat_1002_4 = {}
gameforge.locale.monster_chat_1002_5 = "Leur sang coule, héhé. "
gameforge.locale.monster_chat_1003_1 = "Héhé! (rire tonitruant) "
gameforge.locale.monster_chat_1003_2 = "La lance est plus forte que l'épée, hein? "
gameforge.locale.monster_chat_1003_3 = "Hé, ne pars pas! Viens par là! "
gameforge.locale.monster_chat_1003_4 = "Ta seule vue me fais saliver... "
gameforge.locale.monster_chat_1003_5 = "Tu me cherches! "
gameforge.locale.monster_chat_1003_6 = "Tu vas mouriiiiiiir! "
gameforge.locale.monster_chat_1004_1 = "Tuer, tuuuuer, tuuuuuuuuuer! "
gameforge.locale.monster_chat_1004_2 = "De jolis échantillons sont arrivés. "
gameforge.locale.monster_chat_1004_3 = "On va bien s'amuser! "
gameforge.locale.monster_chat_1004_4 = "Pas tout de suite, ne meurs pas trop vite, attends! Héhéhé. "
gameforge.locale.monster_chat_1004_5 = "Alors que t'arrive-t-il? Tes mains tremblent! "
gameforge.locale.monster_chat_1004_6 = "Courageux de venir nous voir, n'aie pas peur. "
gameforge.locale.monster_chat_1035_1 = "J'aurai ma vengeance avant de mourir. "
gameforge.locale.monster_chat_1035_2 = "Vas y, tout doucement... "
gameforge.locale.monster_chat_1035_3 = "Tu es notre joie. "
gameforge.locale.monster_chat_1035_4 = "Ton corps tremble de peur. (Rire) "
gameforge.locale.monster_chat_1035_5 = "Tu as peur, hein? (Rire) "
gameforge.locale.monster_chat_1035_6 = "Je pense qu'il est temps de te délester de quelques parties de ton corps. "
gameforge.locale.monster_chat_1036_1 = "Avec qui veux-tu aller en enfer? "
gameforge.locale.monster_chat_1036_2 = "Sales chiens! Je vais vous rendre fous! "
gameforge.locale.monster_chat_1036_3 = "(Rire) "
gameforge.locale.monster_chat_1036_4 = "Vends ton âme si tu veux sauver ta vie. "
gameforge.locale.monster_chat_1036_5 = {}
gameforge.locale.monster_chat_1036_6 = "Tu vois trouble, tu ne distingues même plus ta gauche de ta droite (Rire). "
gameforge.locale.monster_chat_1037_1 = "Pourquoi ne pas errer ainsi pour toujours? "
gameforge.locale.monster_chat_1037_2 = "Quoi? Tu as peur du noir? "
gameforge.locale.monster_chat_1037_3 = "Bienvenue en enfer (rire). "
gameforge.locale.monster_chat_1037_4 = "Mais oui, mais oui. Tu peux crier! "
gameforge.locale.monster_chat_1037_5 = "(Rire) Ce n'est pas comme ça que tu me tueras. "
gameforge.locale.monster_chat_1037_6 = "Ouiiiii, voilà qui est mieux! "
gameforge.locale.monster_chat_1038_1 = "C'est ici l'enfer "
gameforge.locale.monster_chat_1038_2 = "Bienvenue en enfer. "
gameforge.locale.monster_chat_1038_3 = "Si tu pouvais mourir lentement... "
gameforge.locale.monster_chat_1038_4 = "Ça n'a pas de sens de m'attaquer! (Rire) "
gameforge.locale.monster_chat_1038_5 = {}
gameforge.locale.monster_chat_1038_6 = "Ce n'est pas bien de mourir rapidement, nous voulons nous amuser avec vous... "
gameforge.locale.monster_chat_1039_1 = "Jusqu'à ce qu'un autre arrive... "
gameforge.locale.monster_chat_1039_2 = "Tu crois vraiment pouvoir nous échapper? "
gameforge.locale.monster_chat_1039_3 = "Ici tout est effrayant, très effrayant. "
gameforge.locale.monster_chat_1039_4 = "Bienvenue chez nous (Rire) "
gameforge.locale.monster_chat_1039_5 = "Tu vois? Tu l'as raté... "
gameforge.locale.monster_chat_1039_6 = "Hmm, si frais... "
gameforge.locale.monster_chat_1040_1 = "Nous voyons tes moindres mouvements. "
gameforge.locale.monster_chat_1040_2 = "Vas-y doucement. "
gameforge.locale.monster_chat_1040_3 = "Si tu touches quelqu'un tu es mort. (rire) "
gameforge.locale.monster_chat_1040_4 = "Tu l'entends ma cloche ??? Ce sera la dernière chose que tu entendras !!! "
gameforge.locale.monster_chat_1040_5 = "Si cette blessure continue de saigner ça deviendra dangereux pour toi... "
gameforge.locale.monster_chat_1040_6 = "Oui! Allez, bats-toi! (rire) "
gameforge.locale.monster_chat_1041_1 = "(Rire)(Rire)(Rire) "
gameforge.locale.monster_chat_1041_2 = "Les nouveaux corps sont là. "
gameforge.locale.monster_chat_1041_3 = "Oui, il est temps d'entrer dans un nouveau corps. "
gameforge.locale.monster_chat_1041_4 = "Nous ressuscitons. Nous ne mourons jamais! "
gameforge.locale.monster_chat_1041_5 = "C'est inutile de nous attaquer. (Rire) "
gameforge.locale.monster_chat_1041_6 = "De quoi mourras-tu? d'épuisement ou de tes blessures? "
gameforge.locale.monster_chat_1091_1 = "Qui peut voler et attaquer mieux que moi? "
gameforge.locale.monster_chat_1091_2 = "Tu peux devenir comme moi. "
gameforge.locale.monster_chat_1091_3 = "Ne veux-tu pas devenir pareil à moi? "
gameforge.locale.monster_chat_1091_4 = "Tu es à moi corps et âme! (rire) "
gameforge.locale.monster_chat_1091_5 = "Fatigué? Tes jambes tremblent... "
gameforge.locale.monster_chat_1091_6 = "Combien de temps crois-tu pouvoir résister? (rire) "
gameforge.locale.monster_chat_1093_1 = "Qui aurait cru qu'il y a pire que la mort? "
gameforge.locale.monster_chat_1093_2 = "Bienvenue. "
gameforge.locale.monster_chat_1093_3 = "Tu seras ma proie! "
gameforge.locale.monster_chat_1093_4 = "Je dévorerai ton corps et ton âme avec! "
gameforge.locale.monster_chat_1093_5 = "Combien de temps résisteras-tu? Hein? "
gameforge.locale.monster_chat_1093_6 = "La mort rit dans ton dos. (rire) "
gameforge.locale.monster_chat_11000_1 = "Tous ceux qui sont inquiets pour leur développement devraient écouter! "
gameforge.locale.monster_chat_11000_10 = "Si vous voulez contrôler le pouvoir de la Nature et de la foudre, choisissez l'apprentissage des Soins! "
gameforge.locale.monster_chat_11000_11 = "Chaque guerrier combat différemment. L'important est d'endurcir votre corps! "
gameforge.locale.monster_chat_11000_12 = "Le point fort d'un Sura est la magie. Travaillez votre intelligence et votre rapidité, sans négliger votre force ! "
gameforge.locale.monster_chat_11000_13 = "Utilisez les compétences de Ninja! Concentrez-vous sur la vitesse et la force de votre corps! "
gameforge.locale.monster_chat_11000_14 = "Les Chamanes utilisent la régénération, l'assistance et les sorts à distance, pour lesquels l'intelligence est indispensable ! "
gameforge.locale.monster_chat_11000_15 = "N'oubliez pas votre apprentissage! "
gameforge.locale.monster_chat_11000_16 = "Suivez un apprentissage pour augmenter vos compétences! "
gameforge.locale.monster_chat_11000_17 = "Compétences? Vous pouvez vous élever au rang de maître, attribué aléatoirement entre les niveaux 17 et 21! "
gameforge.locale.monster_chat_11000_18 = "Vous voulez devenir un grand maître? Vous aurez donc besoin d'un livre de compétence particulier ! "
gameforge.locale.monster_chat_11000_19 = "Dès que vous aurez lu le livre de compétence, il disparaîtra ! "
gameforge.locale.monster_chat_11000_2 = "Guerriers jusqu'au niveau 5, écoutez-moi ! "
gameforge.locale.monster_chat_11000_20 = "Vous voulez lire un autre livre de compétence ? Attendez une journée ! "
gameforge.locale.monster_chat_11000_21 = "Augmentez votre intelligence pour améliorer vos compétences de Sura ! "
gameforge.locale.monster_chat_11000_22 = "Utilisez les grands pouvoirs d'un Sura en combat! "
gameforge.locale.monster_chat_11000_23 = "Vous savez où se trouve le temple de la secte secrète ? Dans la vallée de Seungryong ! "
gameforge.locale.monster_chat_11000_24 = "Les caves aux araignées ont réapparu. Elles sont dans le désert de Yongbi. "
gameforge.locale.monster_chat_11000_25 = "Accomplissez des quêtes et utilisez vos récompenses pour faire des améliorations ! "
gameforge.locale.monster_chat_11000_26 = "Pêcher? Ça me parait bien! Vous êtes-vous déjà procuré une canne et des appâts chez un pêcheur? "
gameforge.locale.monster_chat_11000_27 = "Vous voulez avoir quelque chose de plus dur à chasser? Cherchez un portail! Allez dans un terrain de chasse approprié à votre niveau! "
gameforge.locale.monster_chat_11000_28 = "Prenez garde aux ennemis des autres empires. On ne sait jamais quand ils s'apprêtent à vous poignarder dans le dos. "
gameforge.locale.monster_chat_11000_29 = "Voulez-vous réinitialiser vos compétences? Allez voir la Vieille dame. N'oubliez pas d'apporter assez de yangs! "
gameforge.locale.monster_chat_11000_3 = "Allez voir l'instructeur de combat au corps à corps si vous voulez être connu comme un guerrier puissant en attaque! "
gameforge.locale.monster_chat_11000_30 = "Visez le niveau de Maître parfait, il n'y a que comme ça que vous deviendrez un véritable héros! "
gameforge.locale.monster_chat_11000_31 = "Si vous voulez aller vers une autre ville ou un autre champ de bataille, cherchez les vieillards sur le continent. "
gameforge.locale.monster_chat_11000_4 = "Si vous voulez être puissant dans les compétences de guerrier, choisissez l'apprentissage mental! "
gameforge.locale.monster_chat_11000_5 = "Si vous voulez devenir un Sura sachant manier l'épée magique, choisissez l'apprentissage des armes magiques! "
gameforge.locale.monster_chat_11000_6 = "Choisissez la magie noire si vous voulez maîtriser l'utilisation des sorts! "
gameforge.locale.monster_chat_11000_7 = "L'entrainement à l'arc est réservé aux ninjas de niveau 5 et plus. "
gameforge.locale.monster_chat_11000_8 = "Si vous voulez devenir un Ninja secret, choisissez l'apprentissage du combat au corps à corps! "
gameforge.locale.monster_chat_11000_9 = "Si vous voulez devenir un guerrier de la force du Dragon, choisissez la doctrine du Dieu dragon! "
