gameforge.locale.monster_chat_11004_7 = "L'entrainement à l'arc est réservé aux ninjas de niveau 5 et plus. "
gameforge.locale.monster_chat_11004_8 = "Si vous voulez devenir un Ninja furtif, choisissez l'apprentissage du combat au corps à corps! "
gameforge.locale.monster_chat_11004_9 = "Si vous voulez être un guerrier de la force du Dragon, choisissez la doctrine du Dieu dragon! "
gameforge.locale.monster_chat_1105_1 = "L'état de glace est bon. "
gameforge.locale.monster_chat_1105_2 = "Le mont Sohan est sûrement le mieux. "
gameforge.locale.monster_chat_1105_3 = "Quel temps magnifique. "
gameforge.locale.monster_chat_1105_4 = "Tu seras exactement comme moi. "
gameforge.locale.monster_chat_1105_5 = "Tu peux vivre pour toujours, du moins jusqu'à ce que la glace fonde. "
gameforge.locale.monster_chat_1105_6 = "Combien de temps ça prendra avant que tu ne deviennes de glace? "
gameforge.locale.monster_chat_1106_1 = "Des humains... ici?! "
gameforge.locale.monster_chat_1106_2 = "Fatigué... mais assez réveillé pour toi... "
gameforge.locale.monster_chat_1106_3 = "Quelqu'un ici, sur ma colline?  "
gameforge.locale.monster_chat_1106_4 = "Tu n'as rien à faire ici! "
gameforge.locale.monster_chat_1106_5 = "Tu ferais mieux de retourner sur tes pas! "
gameforge.locale.monster_chat_1106_6 = "Etranger... tu te sens si seul? "
gameforge.locale.monster_chat_1191_1 = "Voyons si de beaux hommes passent par ici. "
gameforge.locale.monster_chat_1191_2 = "Si froid...si seule...ou est mon futur mari? "
gameforge.locale.monster_chat_1191_3 = "Devrais-je y travailler? "
gameforge.locale.monster_chat_1191_4 = {}
gameforge.locale.monster_chat_1191_5 = "Quelque chose manque. Je ne suis pas amusé. "
gameforge.locale.monster_chat_1191_6 = "Je vais mettre un terme a tes souffrances! "
gameforge.locale.monster_chat_1301_1 = "Un dragon ! "
gameforge.locale.monster_chat_1301_2 = "Ta peau pâle va devenir encore plus pâle dans quelques minutes. "
gameforge.locale.monster_chat_1301_3 = "Tu veux me défier ? Pas de souci ! "
gameforge.locale.monster_chat_1301_4 = "Tu m'attaques ? Côah ! Tu n'y survivras pas ! "
gameforge.locale.monster_chat_1301_5 = "Quel humain faible, faible ! "
gameforge.locale.monster_chat_1301_6 = "Voyons comment tu encaisses ! "
gameforge.locale.monster_chat_1302_1 = "T'en tirer ? N'y pense même pas. "
gameforge.locale.monster_chat_1302_2 = "Bien... Approche... Côah ! "
gameforge.locale.monster_chat_1302_3 = "L'épée tremble dans ma main. Côah ! "
gameforge.locale.monster_chat_1302_4 = "Tu ne survivras pas à cette rencontre ! "
gameforge.locale.monster_chat_1302_5 = "Voici mon épée ! "
gameforge.locale.monster_chat_1302_6 = "Douloureux, n'est-ce pas? (rire) "
gameforge.locale.monster_chat_1303_1 = "Même l'herbe sur laquelle tu as marché par mégarde peut te tuer. "
gameforge.locale.monster_chat_1303_2 = "Fais attention à l'herbe. "
gameforge.locale.monster_chat_1303_3 = "Bien. Viens à moi. (rire) "
gameforge.locale.monster_chat_1303_4 = "Espèce de chien! Puisque tu es venu je vais te tuer maintenant. "
gameforge.locale.monster_chat_1303_5 = "Meurs! Meurs! "
gameforge.locale.monster_chat_1303_6 = "Regrettes-tu d'être venu? "
gameforge.locale.monster_chat_1305_1 = "Qui pourra me vaincre? "
gameforge.locale.monster_chat_1305_2 = "Toi? Tu veux te battre? "
gameforge.locale.monster_chat_1305_3 = "Viens ici si tu veux! "
gameforge.locale.monster_chat_1305_4 = "Faisons cela comme il faut! Ne t'en vas pas! "
gameforge.locale.monster_chat_1305_5 = "Comment trouves-tu mon épée? "
gameforge.locale.monster_chat_1305_6 = "Tu as peur? (rire) "
gameforge.locale.monster_chat_1901_1 = "Je pense que tu vas mourir aujourd'hui. "
gameforge.locale.monster_chat_1901_2 = "Je t'aime bien, toi... "
gameforge.locale.monster_chat_1901_3 = "Alors qu'en penses-tu? Ma chère distraction... "
gameforge.locale.monster_chat_1901_4 = "Hé! Fais ça bien et ne t'enfuis pas comme un poltron. "
gameforge.locale.monster_chat_1901_5 = "Déjà en mon pouvoir? Hmmm? "
gameforge.locale.monster_chat_1901_6 = "Tu ne veux pas hurler un peu? (rire) "
gameforge.locale.monster_chat_20001_1 = "Vous semblez nerveux... Ne vous inquiétez pas, ça ne risque pas exploser. "
gameforge.locale.monster_chat_20001_2 = "Parfois, j'ai juste envie d'une promenade. "
gameforge.locale.monster_chat_20001_3 = "Je m'ennuie tellement. Même mes petits-enfants ne viennent plus me rendre visite. "
gameforge.locale.monster_chat_20001_4 = "Quant on devient vieux on a besoin des autres. "
gameforge.locale.monster_chat_20001_5 = "Mes belles années sont derrière moi. "
gameforge.locale.monster_chat_20002_1 = "Le soleil est si chaud. "
gameforge.locale.monster_chat_20002_2 = "Temps magnifique pour sécher un linge mouillé, n'est-ce pas? "
gameforge.locale.monster_chat_20002_3 = "Où est mon bon à rien de mari? "
gameforge.locale.monster_chat_20002_4 = "Il se fait appeler père et n'a même pas honte face à ses enfants. "
gameforge.locale.monster_chat_20002_5 = "Les livres sont des livres. Il ne gagne pas d'argent. Il ne fait que lire. "
gameforge.locale.monster_chat_20003_1 = "Oooh, doudou, bébé. Tu as bien dormi? "
gameforge.locale.monster_chat_20003_2 = "Tu souris comme un petit ange. "
gameforge.locale.monster_chat_20003_3 = "Maman est si contente que tu sois en bonne santé. "
gameforge.locale.monster_chat_20003_4 = "Tu veux qu'on s'occupe de toi? Tu as faim? "
gameforge.locale.monster_chat_20003_5 = "Bon petit... Dors mon bébé, dors. "
gameforge.locale.monster_chat_20004_1 = "La routine. "
gameforge.locale.monster_chat_20004_2 = "Où vais-je trouver à manger aujourd'hui? "
gameforge.locale.monster_chat_20004_3 = "Ces gens en arme sont si ennuyeux. "
gameforge.locale.monster_chat_20004_4 = "Voyons voir. Peut-être devrais-je aller au restaurant d'Octavio. "
gameforge.locale.monster_chat_20004_5 = "Oh, au fait, on dirait que les guerres sont finies... "
gameforge.locale.monster_chat_20005_1 = "L'esprit du Maître-potier coule jusqu'au bout de ses doigts. "
gameforge.locale.monster_chat_20005_2 = "Depuis que le chaos a envahi le pays, il y a des combattants partout. "
gameforge.locale.monster_chat_20005_3 = "Tout le monde veut se battre. "
gameforge.locale.monster_chat_20005_4 = "Je prie pour un monde calme et paisible. "
gameforge.locale.monster_chat_20005_5 = "Hé, vous là, prenez une minute pour vous relaxer et réfléchir... "
gameforge.locale.monster_chat_20006_1 = "Oh ciel, que dois-je faire? "
gameforge.locale.monster_chat_20006_2 = "Je ne trouve pas la moindre trace de mon frère pourtant il a promis d'être là. "
gameforge.locale.monster_chat_20006_3 = "Je ne vois que des porteurs d'épée partout. "
gameforge.locale.monster_chat_20006_4 = "Mon frère me manque. "
gameforge.locale.monster_chat_20006_5 = "Personne n'a vu mon frère? "
gameforge.locale.monster_chat_20007_1 = "Hm, c'est vraiment de la viande fraiche! "
gameforge.locale.monster_chat_20007_2 = "C'est bien que les gens mangent plus de viande maintenant. "
gameforge.locale.monster_chat_20007_3 = "Les affaires vont bien mais la viande est toujours rare. "
gameforge.locale.monster_chat_20007_4 = "Quand est-ce que la viande que nous avons commandée arrivera? "
gameforge.locale.monster_chat_20007_5 = "Si ça n'arrive pas à temps, il nous faudra partir chasser. "
gameforge.locale.monster_chat_20008_1 = "J'espère que les clients viendront en masse quand les beaux jours reviendront. "
gameforge.locale.monster_chat_20008_2 = "Le chien paresseux viendra sûrement bientôt réclamer à manger. "
gameforge.locale.monster_chat_20008_3 = "Oh mon dieu, quel pauvre homme! "
gameforge.locale.monster_chat_20008_4 = "Il était un grand guerrier autrefois. "
gameforge.locale.monster_chat_20008_5 = "Je me demande pourquoi il ne combat plus. "
gameforge.locale.monster_chat_20009_1 = "Les jeunes sont vraiment fougueux, ils rayonnent de vitalité. "
gameforge.locale.monster_chat_20009_2 = "Les guerriers ont bien plus fière allure quand ils portent leurs armes. "
gameforge.locale.monster_chat_20009_3 = "Cet esprit meurtrier, tout le monde l'a à présent. "
gameforge.locale.monster_chat_20009_4 = "Domine ton cœur, concentre-toi profondément et calme ton esprit. "
gameforge.locale.monster_chat_20009_5 = "Il existe une recette infaillible pour le bonheur, mais il faut se tenir à de strictes règles. "
gameforge.locale.monster_chat_20010_1 = "Je t'ai apporté un objet précieux! "
gameforge.locale.monster_chat_20010_2 = "Marchandises Exotiques ! Pièces rares et belles ! "
gameforge.locale.monster_chat_20010_3 = "Venez, venez tous! "
gameforge.locale.monster_chat_20010_4 = "Venez, venez acheter! Vous n'aurez plus d'occasion comme celle là! "
gameforge.locale.monster_chat_20010_5 = "Argh, pourquoi y a-t-il autant de voleurs par ici? "
gameforge.locale.monster_chat_20011_1 = "Tout est si sec ces temps-ci... "
gameforge.locale.monster_chat_20011_2 = "La récolte de grain n'est plus ce qu'elle était avant. "
gameforge.locale.monster_chat_20011_3 = "Pourquoi les récoltes sont-elles moins bonnes qu'avant? "
gameforge.locale.monster_chat_20011_4 = "Le grain est cassant et a un goût de pourriture. "
gameforge.locale.monster_chat_20011_5 = "C'est mauvais signe. L'agriculture est la base de notre vie. "
gameforge.locale.monster_chat_20012_1 = "Gâteaux de riz! Délicieux gâteaux de riz!! "
gameforge.locale.monster_chat_20012_2 = "Achetez mes gâteaux de riz sucrés! "
gameforge.locale.monster_chat_20012_3 = "C'est vrai, les affaires se portent plutôt mal. "
gameforge.locale.monster_chat_20012_4 = "Je devrais peut-être tenter ma chance dans une autre ville. "
gameforge.locale.monster_chat_20012_5 = "Mais il y a tellement de tigres... "
gameforge.locale.monster_chat_20013_1 = "Les jours comme aujourd'hui, la meilleure chose qui puisse t'arriver c'est d'être sur un bateau. "
gameforge.locale.monster_chat_20013_2 = "Temps idéal pour pêcher! "
gameforge.locale.monster_chat_20013_3 = "Encore un petit nœud... Aucun poisson ne m'arrachera cet hameçon ! "
gameforge.locale.monster_chat_20013_4 = "Ce satané ours a détruit ma canne à pêche! "
gameforge.locale.monster_chat_20013_5 = "Je devrais vraiment ramander mes filets. "
gameforge.locale.monster_chat_20014_1 = "Huh! "
gameforge.locale.monster_chat_20014_2 = "Pas d'inquiétude, c'est ma façon d'être. "
gameforge.locale.monster_chat_20014_3 = "Haaaaa! "
gameforge.locale.monster_chat_20014_4 = "Que faire pour qu'il... Oh...qu'est-ce que je viens de vous dire ? "
gameforge.locale.monster_chat_20014_5 = "Je ne peux pas du tout être courageux. "
gameforge.locale.monster_chat_20015_1 = "Cette hache m'a toujours bien servi pendant ces 20 dernières années. "
gameforge.locale.monster_chat_20015_2 = "Toujours tranchante, mais peut-être plus autant qu'avant. "
gameforge.locale.monster_chat_20015_3 = "Je veux la renforcer pour continuer à l'utiliser. "
gameforge.locale.monster_chat_20015_4 = "Mais j'ai besoin de pierres pleines de minéraux pour cela "
gameforge.locale.monster_chat_20015_5 = "Mais où pourrai-je les trouver? "
gameforge.locale.monster_chat_20016_1 = "Les clients viennent en masse. "
gameforge.locale.monster_chat_20016_2 = "Espérons que je puisse finir à temps. "
gameforge.locale.monster_chat_20016_3 = "Je tape sur l'enclume, ne me tapez pas sur les nerfs ! "
gameforge.locale.monster_chat_20016_4 = "Voilà le bruit que doit faire un marteau ! "
gameforge.locale.monster_chat_20016_5 = "Quelle chaleur dans cette forge. "
gameforge.locale.monster_chat_20017_1 = "Je ne me sens pas de jouer d'un instrument. "
gameforge.locale.monster_chat_20017_2 = "Mon instrument est dans un bien sale état. "
gameforge.locale.monster_chat_20017_3 = "Peut-être devrais-je acheter un nouvel instrument ? "
gameforge.locale.monster_chat_20017_4 = "Mon instrument est certes vieux et usé... Mais que voulez-vous, j'aime les antiquités ! "
gameforge.locale.monster_chat_20017_5 = "Il faut que je trouve rapidement des cordes pour remplacer celles qui ont cassé... "
gameforge.locale.monster_chat_20018_1 = "Je suis si occupé! "
gameforge.locale.monster_chat_20018_2 = "Chaque jour de nouveaux blessés arrivent. "
gameforge.locale.monster_chat_20018_3 = "Pas le temps de sortir et voir des gens! "
gameforge.locale.monster_chat_20018_4 = "Et les médicaments commencent à manquer. "
gameforge.locale.monster_chat_20018_5 = "Je voudrais bien trouver quelqu'un pour ramener les ingrédients pour fabriquer de nouveaux remèdes. "
gameforge.locale.monster_chat_20019_1 = "Depuis trente ans, je parcours les montagnes avec mon arc. "
gameforge.locale.monster_chat_20019_2 = "C'est comme ça que j'ai pu nourrir ma femme et élever trois enfants. "
gameforge.locale.monster_chat_20019_3 = "Ce n'est pas une vie facile mais ça en vaut la peine. "
gameforge.locale.monster_chat_20019_4 = "Mais maintenant la corde de mon arc est cassée et je ne peux plus chasser pour nourrir ma famille. "
gameforge.locale.monster_chat_20019_5 = "Si je ne peux pas réparer mon arc il faudra que j'arrête. "
gameforge.locale.monster_chat_20020_1 = "Hé, qu'est-ce que vous regardez comme ça?? "
gameforge.locale.monster_chat_20020_2 = "Allez vous-en! "
gameforge.locale.monster_chat_20020_3 = "Oh, combien de temps devrais-je rester caché? "
gameforge.locale.monster_chat_20020_4 = "Vais-je mourir au milieu de nulle part, oublié de tous? "
gameforge.locale.monster_chat_20020_5 = "Aaaah, j'espère tellement l'aide de quelqu'un... "
gameforge.locale.monster_chat_20021_1 = "Ce jour prendra fin comme tous les autres. "
gameforge.locale.monster_chat_20021_2 = "Que vais-je faire à présent hein? "
gameforge.locale.monster_chat_20021_3 = "Je n'aurais jamais cru que ça finirait ainsi. "
gameforge.locale.monster_chat_20021_4 = "Non! Il ne peut pas être le seul à avoir fini comme ça! "
gameforge.locale.monster_chat_20021_5 = "J'espère qu'il sera vengé! "
gameforge.locale.monster_chat_20022_1 = "Encore un jour formidable! "
gameforge.locale.monster_chat_20022_2 = "Le mari de Ariyoung a disparu. "
gameforge.locale.monster_chat_20022_3 = "Je voulais aider mais cette obstination... "
gameforge.locale.monster_chat_20022_4 = "Si elle m'avait épousé, elle n'aurait pas eu une vie aussi ennuyeuse. "
gameforge.locale.monster_chat_20022_5 = "Je n'ai pas su lui montrer mes sentiments... "
gameforge.locale.monster_chat_20023_1 = "Oh non! Que faire si ma femme apprend ça? "
gameforge.locale.monster_chat_20023_2 = "Si seulement je pouvais vivre de la lecture de mes livres! "
gameforge.locale.monster_chat_20023_3 = "Les livres sont si mystérieux. "
gameforge.locale.monster_chat_20023_4 = "Je ne pourrais pas vivre comme ça éternellement mais... "
gameforge.locale.monster_chat_20023_5 = "J'aimerais pouvoir faire quelque chose pour ma femme mais... "
gameforge.locale.monster_chat_20024_1 = "Allez, allez! Bouge un peu mieux que ça! "
gameforge.locale.monster_chat_20024_2 = "Qu'est-ce que tu fais? Tu ne peux pas te dépêcher un peu? "
gameforge.locale.monster_chat_20024_3 = "Imaginez-vous comment un guerrier manie son épée et faites pareil ! "
gameforge.locale.monster_chat_20024_4 = "Où est cet imbécile? Où est-il maintenant? "
gameforge.locale.monster_chat_20024_5 = "Chien! Tu n'est pas un homme. Ha! "
gameforge.locale.monster_chat_2091_1 = "Coupons, coupons... Cheveux et cous ! "
gameforge.locale.monster_chat_2091_2 = "J'ai la solution à vos problèmes de fatigue : Le sommeil éternel ! "
gameforge.locale.monster_chat_2091_3 = "Respirez profondément. Ne vous inquiétez pas, ce n'est que du poison ! "
gameforge.locale.monster_chat_2091_4 = "Je ne vais laisser que votre visage intact ! Cela fera une belle décoration... "
gameforge.locale.monster_chat_2091_5 = {}
gameforge.locale.monster_chat_2091_6 = "Une fois enroulés dans ma toile, vous ferez un appétissant petit-four pour plus tard. "
gameforge.locale.monster_chat_2092_1 = "Je ne peux plus supporter les caprices de ma femme. "
gameforge.locale.monster_chat_2092_2 = "Pourquoi je ne peux pas dormir? "
gameforge.locale.monster_chat_2092_3 = "Oh, on dirait que quelqu'un est pris dans la toile d'araignée. "
gameforge.locale.monster_chat_2092_4 = "Je suis plutôt pacifique, mais si quelqu'un m'attaque il le regrette très vite. "
gameforge.locale.monster_chat_2092_5 = "Ouiiiii, donne tout ce que tu as! "
gameforge.locale.monster_chat_2092_6 = "Personne ne peut me vaincre ! "
gameforge.locale.monster_chat_2104_1 = "Pourquoi les humains sont ils aussi humides à l'intérieur ? Ça fait rouiller mon épée ! "
gameforge.locale.monster_chat_2104_2 = "Le poison dans ton dard caudal a-t-il séché ? "
gameforge.locale.monster_chat_2104_3 = "Alors, et ce combat? "
gameforge.locale.monster_chat_2104_4 = "Déjà épuisé par la chaleur? "
gameforge.locale.monster_chat_2104_5 = "J'aime abandonner les gens dans le désert. "
gameforge.locale.monster_chat_2104_6 = "Pauvre de toi, enterré ainsi dans le désert. "
gameforge.locale.monster_chat_2105_1 = "Je vais partager un secret avec toi - je déteste la poussière. "
gameforge.locale.monster_chat_2105_2 = "Il fait très chaud, n'est-ce pas? "
gameforge.locale.monster_chat_2105_3 = "Cet endroit est dangereux. "
gameforge.locale.monster_chat_2105_4 = {}
gameforge.locale.monster_chat_2105_5 = "Je te toucherai entre les deux yeux. "
gameforge.locale.monster_chat_2106_1 = "(soupir) Quelque chose à attraper aujourd'hui? (soupir) "
gameforge.locale.monster_chat_2106_2 = "Oooh oui, voilà quelque chose à chasser.(rire) "
gameforge.locale.monster_chat_2106_3 = "(Rire) J'aime ça, j'aime beaucoup ça! "
gameforge.locale.monster_chat_2106_4 = "Celui-là est bien gras. Il est pour moi ! "
gameforge.locale.monster_chat_2106_5 = {}
gameforge.locale.monster_chat_2106_6 = "J'aime l'odeur du sang. "
gameforge.locale.monster_chat_2107_1 = "(soupir) Il n'y a pas grand chose à faire ces temps-ci.(soupir) "
gameforge.locale.monster_chat_2107_2 = "Hé, ceux-là sont vraiment gras! "
gameforge.locale.monster_chat_2107_3 = "Le Chef appréciera... "
gameforge.locale.monster_chat_2107_4 = {}
gameforge.locale.monster_chat_2107_5 = "Bougeons un peu nos jambes. "
gameforge.locale.monster_chat_2107_6 = "Et voilà la flèche! "
gameforge.locale.monster_chat_2108_1 = "Si j'en attrape un, je... "
gameforge.locale.monster_chat_2108_2 = "Hé, pourquoi regardes-tu derrière toi? "
gameforge.locale.monster_chat_2108_3 = "Tu penses pouvoir me battre facilement? Hein? "
gameforge.locale.monster_chat_2108_4 = "Pas de chance pour toi de me rencontrer dans le désert! "
gameforge.locale.monster_chat_2108_5 = "Ah, cette sensation, je ne m'en lasserai jamais! "
gameforge.locale.monster_chat_2108_6 = "Merci! "
gameforge.locale.monster_chat_2191_1 = "Ça sent l'humain - tellement délicieux! "
gameforge.locale.monster_chat_2191_2 = "Bien. Viens par ici, viens... "
gameforge.locale.monster_chat_2191_3 = {}
gameforge.locale.monster_chat_2191_4 = "Ça fait plaisir de rencontrer quelqu'un dans le désert! "
gameforge.locale.monster_chat_2191_5 = "Arrête de bouger! Kkkkkk! "
gameforge.locale.monster_chat_2191_6 = "Un petit morceau pour commencer... "
gameforge.locale.monster_chat_2202_1 = "Je ne vois vraiment pas pourquoi ça il fait si chaud. "
gameforge.locale.monster_chat_2202_2 = "Si seulement il y avait quelque chose de frais... "
gameforge.locale.monster_chat_2202_3 = "Je pourrais peut-être me rafraichir avec leur sang. "
gameforge.locale.monster_chat_2202_4 = "J'ai chaud, chaud! Leur sang me rafraîchira. "
gameforge.locale.monster_chat_2202_5 = "Donne-moi ton sang! "
gameforge.locale.monster_chat_2202_6 = "Deviens comme moi! "
gameforge.locale.monster_chat_2204_1 = "Personne n'entrera tant que je serai ici! "
gameforge.locale.monster_chat_2204_2 = "Je ne sais pas pourquoi mais tu commences à m'ennuyer. "
gameforge.locale.monster_chat_2204_3 = {}
gameforge.locale.monster_chat_2204_4 = "Kwuuh, sais-tu où tu es? "
gameforge.locale.monster_chat_2204_5 = {}
gameforge.locale.monster_chat_2204_6 = "Si maigre! (Rire) "
gameforge.locale.monster_chat_2205_1 = "Une épée n'est utile que dans les mains d'un guerrier. "
gameforge.locale.monster_chat_2205_2 = "Garde ton épée à la main. "
gameforge.locale.monster_chat_2205_3 = "La guerre se prépare. "
gameforge.locale.monster_chat_2205_4 = "Maintenant c'est un combat à mort! "
gameforge.locale.monster_chat_2205_5 = "Tu veux continuer à résister à la mort? "
gameforge.locale.monster_chat_2205_6 = "Oui, ne meurs pas trop vite. "
gameforge.locale.monster_chat_2206_1 = "C'est si bon de nager dans la lave. "
gameforge.locale.monster_chat_2206_2 = "J'aime me rafraichir dans un bain de lave. "
gameforge.locale.monster_chat_2206_3 = "Mais la lave n'est plus ce qu'elle était... "
gameforge.locale.monster_chat_2206_4 = "Fils de chien! Tu m'as dérangé! "
gameforge.locale.monster_chat_2206_5 = "Ne dérange pas mon intimité. "
gameforge.locale.monster_chat_2206_6 = "Sinon tu le regretteras... "
gameforge.locale.monster_chat_2291_1 = "Beau temps. Ce serait tellement bien de pouvoir simplement dormir. "
gameforge.locale.monster_chat_2291_2 = "Rien n'est comparable au pays de Doyyumhwan. "
gameforge.locale.monster_chat_2291_3 = "Pourquoi n'irais-je pas dormir? "
gameforge.locale.monster_chat_2291_4 = "Tu m'as dérangé. "
gameforge.locale.monster_chat_2291_5 = "Tu sembles regretter de m'avoir dérangé. "
gameforge.locale.monster_chat_2291_6 = "Trop tard pour fuir. Et ça ne servirait à rien. "
gameforge.locale.monster_chat_301_1 = "Je m'ennuie tellement. N'y a-t-il rien d'intéressant à faire? "
gameforge.locale.monster_chat_301_2 = "Il y a deux jours, un petite fille a été mangée par des bêtes sauvages, dans les environs. "
gameforge.locale.monster_chat_301_3 = "Les animaux sauvages deviennent de plus en plus agressifs. Nous devrions en tuer autant que possible. "
gameforge.locale.monster_chat_301_4 = "Pas de problème ! Héhé, être bien de sa personne ne suffit pas. "
gameforge.locale.monster_chat_301_5 = "Veux-tu cette épée ? Nous ne plaisantons pas. "
gameforge.locale.monster_chat_302_1 = "Hé ! Voilà un beau garçon! "
gameforge.locale.monster_chat_302_2 = "Je fais un régime, vous savez. "
gameforge.locale.monster_chat_302_3 = "J'ai envie d'aller faire des courses. "
gameforge.locale.monster_chat_302_4 = "C'était ma flèche! "
gameforge.locale.monster_chat_302_5 = "Tu la vois? Elle vise ton cœur. "
gameforge.locale.monster_chat_302_6 = "Oh ! En plein dans le mille! "
gameforge.locale.monster_chat_303_1 = "Nos enfants sont devenus trop paresseux maintenant. "
gameforge.locale.monster_chat_303_2 = "Que se passerait-il si les soldats venaient ici ? "
gameforge.locale.monster_chat_303_3 = "Pourquoi me regardes-tu comme ça? "
gameforge.locale.monster_chat_303_4 = "Oh, mon dieu, quel poltron! "
gameforge.locale.monster_chat_303_5 = "N'en prends pas trop à ton aise. "
gameforge.locale.monster_chat_303_6 = "Ce combat sera plus difficile que je ne le pensais. "
gameforge.locale.monster_chat_304_1 = "Vous les femmes vous êtes venues après que vos maris vous aient laissé tomber mais vous avez toujours besoin d'hommes. "
gameforge.locale.monster_chat_304_2 = "Il n'existe pas d'hommes bien. "
gameforge.locale.monster_chat_304_3 = "Vous me tapez sur les nerfs. "
gameforge.locale.monster_chat_304_4 = "Quel effet ça fait? Tu ne vois pas que la mort vous regarde? "
gameforge.locale.monster_chat_304_5 = "Oh, non ! Tu abandonnes déjà? "
gameforge.locale.monster_chat_304_6 = "La souffrance vient vite. "
gameforge.locale.monster_chat_305_1 = "Hé! Cette épée a l'air fantastique. "
gameforge.locale.monster_chat_305_2 = "Sois plus tenace et augmente ta puissance! "
gameforge.locale.monster_chat_305_3 = "Tu penses pouvoir devenir aussi fort que moi? "
gameforge.locale.monster_chat_305_4 = "Ok. Passons aux choses sérieuses. "
gameforge.locale.monster_chat_305_5 = "Oh mon dieu! Je ne suis qu'une faible femme! "
gameforge.locale.monster_chat_305_6 = "Tu as l'air fatigué. Pourquoi ne pas abandonner? Héhé. "
gameforge.locale.monster_chat_306_1 = "Que va faire Serangyi aujourd'hui, hm? "
gameforge.locale.monster_chat_306_2 = "Je m'ennuie tellement... Joue avec moi! "
gameforge.locale.monster_chat_306_3 = "Je dois dire que je préfère les Suras. "
gameforge.locale.monster_chat_306_4 = "Hé ! Pourquoi pas un combat? "
gameforge.locale.monster_chat_306_5 = "Ne me sous-estime pas sous prétexte que je suis une femme! "
gameforge.locale.monster_chat_306_6 = "La souffrance n'est que passagère. "
gameforge.locale.monster_chat_307_1 = "Je veux quitter cet endroit à tout prix. "
gameforge.locale.monster_chat_307_2 = "Comment sont sécurisées les habitations des membres individuels ? "
gameforge.locale.monster_chat_307_3 = "Taisez-vous les filles! "
gameforge.locale.monster_chat_307_4 = "Je te vaincrai en un seul coup. "
gameforge.locale.monster_chat_307_5 = "Ne pense même pas à t'enfuir! "
gameforge.locale.monster_chat_307_6 = "Bon, on commence? "
gameforge.locale.monster_chat_394_1 = "N'oubliez pas : tous les hommes sont des ennemis. "
gameforge.locale.monster_chat_394_2 = "Si une personne a un visage agréable, nous pouvons être moins méchantes. "
gameforge.locale.monster_chat_394_3 = "Quoi? Tu veux te battre? "
gameforge.locale.monster_chat_394_4 = "Ça ne prendra pas longtemps mais ça va faire mal. "
gameforge.locale.monster_chat_394_5 = "Oh, tu sembles vraiment souffrir. "
gameforge.locale.monster_chat_394_6 = "Adieu, va en enfer! "
gameforge.locale.monster_chat_401_1 = "Alors, que fais-tu dans le Serment Blanc? "
gameforge.locale.monster_chat_401_2 = "Où vas-tu? "
gameforge.locale.monster_chat_401_3 = "Hé, tu veux te battre? "
gameforge.locale.monster_chat_401_4 = "Héhé, tu esquives mes coups. "
gameforge.locale.monster_chat_401_5 = "Tu penses pouvoir m'échapper? "
gameforge.locale.monster_chat_401_6 = "Prends ça! "
gameforge.locale.monster_chat_402_1 = "Dieu que je suis fatigué ! "
gameforge.locale.monster_chat_402_2 = "Hé, belle armure! Elle a coûté combien? "
gameforge.locale.monster_chat_402_3 = "Mais le temps est au beau. "
gameforge.locale.monster_chat_402_4 = "Oh, tu t'es beaucoup entraîné, très bien ! "
gameforge.locale.monster_chat_402_5 = "Que penses-tu de mes capacités? Pas mal hein? "
gameforge.locale.monster_chat_402_6 = "Quoi? bien sur, c'est cette vitesse incroyable! "
gameforge.locale.monster_chat_403_1 = "Pourquoi n'irais-je pas chasser? "
gameforge.locale.monster_chat_403_2 = "Personne n'attaque notre village aujourd'hui. "
gameforge.locale.monster_chat_403_3 = "SI tu veux te battre alors bats-toi, sale chien! "
gameforge.locale.monster_chat_403_4 = "Héhé, tu n'es qu'un débutant. "
gameforge.locale.monster_chat_403_5 = "Que devrais-je viser en premier, ton bras ou ta jambe? "
gameforge.locale.monster_chat_403_6 = "Et voici ma flèche! "
gameforge.locale.monster_chat_404_1 = "Je dois aller au village m'entraîner bientôt. "
gameforge.locale.monster_chat_404_2 = "Je me sens plutôt rouillé. "
gameforge.locale.monster_chat_404_3 = "Qu'est-ce qu'on mange ce soir? "
gameforge.locale.monster_chat_404_4 = "Je surveille, alors crie tant que tu veux. "
gameforge.locale.monster_chat_404_5 = "Tu as attendu trop longtemps? Commence par faire tournoyer ton épée! "
gameforge.locale.monster_chat_404_6 = "L'épée semble intéressante! "
gameforge.locale.monster_chat_405_1 = "Cette épée est prête! "
gameforge.locale.monster_chat_405_2 = "Voyons qui sera tué le premier par cette épée. "
gameforge.locale.monster_chat_405_3 = "Hé, qu'est-ce que tu regarde comme ça? "
gameforge.locale.monster_chat_405_4 = "D'abord le ventre! "
gameforge.locale.monster_chat_405_5 = "Tu vas servir de fourreau à mon épée. "
gameforge.locale.monster_chat_405_6 = "Tu vas servir de fourreau à mon épée. "
gameforge.locale.monster_chat_406_1 = "Je suis affamé. "
gameforge.locale.monster_chat_406_2 = "Y a-t-il quelque chose à manger? "
gameforge.locale.monster_chat_406_3 = "Pourquoi ai-je encore faim après toute cette nourriture? "
gameforge.locale.monster_chat_406_4 = "Aaargh, meurs! "
gameforge.locale.monster_chat_406_5 = "Mon estomac décidera de l'issue de ce combat! "
gameforge.locale.monster_chat_406_6 = "C'est un choix entre le paradis et l'enfer! "
gameforge.locale.monster_chat_491_1 = "Je n'en peux plus! Mon corps me démange. "
gameforge.locale.monster_chat_491_2 = "Oh oui, il y a quelques porcs. "
gameforge.locale.monster_chat_491_3 = "Par terre? "
gameforge.locale.monster_chat_491_4 = "Un seul coup me suffira pour en finir avec toi. "
gameforge.locale.monster_chat_491_5 = "Ce me fera un beau souvenir ! "
gameforge.locale.monster_chat_491_6 = "Meurs! "
gameforge.locale.monster_chat_492_1 = "Mon corps s'est ramolli. "
gameforge.locale.monster_chat_492_2 = "Pourquoi est-ce que je ne m'entraîne plus? "
gameforge.locale.monster_chat_492_3 = "Tu as peur hein? "
gameforge.locale.monster_chat_492_4 = "Ah, une nouvelle victime pour mon épée tournoyante. "
gameforge.locale.monster_chat_492_5 = "Pouvez-vous voir ma force d'attaque ? Non ? Crevez ! "
gameforge.locale.monster_chat_492_6 = "Même la plus petite entaille n'est pas bonne pour la santé, tu sais? "
gameforge.locale.monster_chat_493_1 = "C'est si inconfortable d'avoir une telle main! "
gameforge.locale.monster_chat_493_2 = "Qu'est-ce que tu regardes? Tu n'a jamais vu une main comme celle-là? "
gameforge.locale.monster_chat_493_3 = "Vous me tapez tous sur les nerfs. "
gameforge.locale.monster_chat_493_4 = "Tu veux que je te mette une raclée? Sinon, sauve-toi. Compris? "
gameforge.locale.monster_chat_493_5 = "Je vais te montrer mon meilleur coup! "
gameforge.locale.monster_chat_493_6 = "Je sais que tu regardes ma main! "
gameforge.locale.monster_chat_494_1 = "Hé toi, si tu veux te battre, viens ici! "
gameforge.locale.monster_chat_494_2 = "Je pense que je suis plutôt époustouflant. "
gameforge.locale.monster_chat_494_3 = "Pourquoi est-ce qu'on n'organise pas quelque chose? "
gameforge.locale.monster_chat_494_4 = "Non, tu n'as pas le droit de partir, encore. Bats-toi! "
gameforge.locale.monster_chat_494_5 = "Tiens, voilà! "
gameforge.locale.monster_chat_494_6 = "Tu es presque mort! "
gameforge.locale.monster_chat_501_1 = "Joue avec moi! Ne t'en vas pas! "
gameforge.locale.monster_chat_501_2 = "Je pourrais faire de beaux bijoux avec vos os ! "
gameforge.locale.monster_chat_501_3 = "Nous ne nous sommes pas encore entraînés. "
gameforge.locale.monster_chat_501_4 = "Quelle armure fantastique! Je la veux. "
gameforge.locale.monster_chat_501_5 = "À partir de maintenant tu vas avoir du mal à ignorer mes attaques. "
gameforge.locale.monster_chat_501_6 = "Héhé, surpris? "
gameforge.locale.monster_chat_502_1 = "La beauté ne fait pas tout. "
gameforge.locale.monster_chat_502_2 = "Ne regarde pas bêtement, bats-toi! "
gameforge.locale.monster_chat_502_3 = "Je n'aime pas ta façon de me regarder. Bats-toi! "
gameforge.locale.monster_chat_502_4 = "C'est un honneur de mourir par mon épée! "
gameforge.locale.monster_chat_502_5 = {}
gameforge.locale.monster_chat_502_6 = "Très bien ! "
gameforge.locale.monster_chat_503_1 = "Hé là, ne touche pas! "
gameforge.locale.monster_chat_503_2 = "On dirait que la corde de mon arc est coupée. "
gameforge.locale.monster_chat_503_3 = "Minces, ces idiots étaient ici. "
gameforge.locale.monster_chat_503_4 = "Pas mal. Mais tu mourras quand même! "
gameforge.locale.monster_chat_503_5 = "Tu es d'accord? Parfait! "
gameforge.locale.monster_chat_503_6 = "Voilà la flèche! "
gameforge.locale.monster_chat_504_1 = "Venez tous par ici! "
gameforge.locale.monster_chat_504_2 = "La, lala, j'en chante une belle chanson... "
gameforge.locale.monster_chat_504_3 = "Quel temps magnifique! "
gameforge.locale.monster_chat_504_4 = {}
gameforge.locale.monster_chat_504_5 = "Ton désir de combattre me fait plaisir! "
gameforge.locale.monster_chat_504_6 = "Allez ! Tous par ici ! Je vous attends ! "
gameforge.locale.monster_chat_601_1 = "J'ai très faim! "
gameforge.locale.monster_chat_601_2 = "J'ai envie de manger des fruits. "
gameforge.locale.monster_chat_601_3 = "Bien, aussi utile qu'un poisson. À l'attaque ! "
gameforge.locale.monster_chat_601_4 = "Ne t'enfuis pas! Reste ici! "
gameforge.locale.monster_chat_601_5 = "J'ai gagné. C'est évident! "
gameforge.locale.monster_chat_601_6 = "Tu vois? Raté! "
gameforge.locale.monster_chat_601_7 = "Tu devrais faire attention, ah ah ! "
gameforge.locale.monster_chat_602_1 = "Où suis-je? "
gameforge.locale.monster_chat_602_2 = "Beau temps. "
gameforge.locale.monster_chat_602_3 = "Tu es venu me traquer? N'importe quoi! "
gameforge.locale.monster_chat_602_4 = "Tu es ma proie. "
gameforge.locale.monster_chat_602_5 = "Quel supplice, hein? Je vais t'achever rapidement. "
gameforge.locale.monster_chat_602_6 = "Regarde comme je suis fort : je peux balancer cette énorme batte ! "
gameforge.locale.monster_chat_603_1 = "Rasoir! "
gameforge.locale.monster_chat_603_2 = "Mon corps entier me démange. "
gameforge.locale.monster_chat_603_3 = "Qui que ce soit, allez-y. "
gameforge.locale.monster_chat_603_4 = "Tu m'as touché le premier? Tu ne vas pas faire long feu ici! "
gameforge.locale.monster_chat_603_5 = "Une fois entré, impossible d'en sortir. Aucune échappatoire possible! "
gameforge.locale.monster_chat_603_6 = "Ce sera mon dernier coup. Je vais t'achever! "
gameforge.locale.monster_chat_604_1 = "J'ai fabriqué un poison au goût sensationnel. Vous voulez y goûter ? "
gameforge.locale.monster_chat_604_2 = "Je veux voir comment il marche! Prends ça ! "
gameforge.locale.monster_chat_604_3 = "Hé toi, tu veux être mon cobaye?! "
gameforge.locale.monster_chat_604_4 = "Je vais t'empoisonner! "
gameforge.locale.monster_chat_604_5 = "Oh, très bien, un cobaye! "
gameforge.locale.monster_chat_604_6 = "Ce n'est pas marrant si tu abandonnes aussi vite!! Défends toi mauviette ! "
gameforge.locale.monster_chat_631_1 = "Un instant, je cherche quelque chose à chasser! "
gameforge.locale.monster_chat_631_2 = "Ça ne marche pas si tu es trop lent! "
gameforge.locale.monster_chat_631_3 = "J'ai si faim! "
gameforge.locale.monster_chat_631_4 = "C'est bien, j'ai trouvé quelque chose à chasser! "
gameforge.locale.monster_chat_631_5 = "N'espère pas survivre ! "
gameforge.locale.monster_chat_631_6 = "Je vais t'apprendre ce que souffrir veut dire ! "
gameforge.locale.monster_chat_632_1 = "Je m'ennuiiiiiiiiiie !! "
gameforge.locale.monster_chat_632_2 = "Y'a quelque chose qui puisse servir d'appât? "
gameforge.locale.monster_chat_632_3 = "Viens te battre si tu l'oses! "
gameforge.locale.monster_chat_632_4 = "Si tu es un guerrier, prouve-le moi ! "
gameforge.locale.monster_chat_632_5 = {}
gameforge.locale.monster_chat_632_6 = "Fuis et regagne le village si tu tiens à la vie! "
gameforge.locale.monster_chat_633_1 = "C'est douloureux d'être affamé. "
gameforge.locale.monster_chat_633_2 = "Oh, ils se dirigent droit sur nous! "
gameforge.locale.monster_chat_633_3 = "Que dirais-tu d'un duel? "
gameforge.locale.monster_chat_633_4 = "Ne me compare pas à ces fils de chiens! "
gameforge.locale.monster_chat_633_5 = "Voilà un coup rapide comme l'éclair ! "
gameforge.locale.monster_chat_633_6 = "Celui qui fuit n'est pas un vrai guerrier ! "
gameforge.locale.monster_chat_634_1 = "Cette fois-ci le poison devrait être plus efficace! "
gameforge.locale.monster_chat_634_2 = "Quand on a faim, les chauve-souris ont très bon goût. "
gameforge.locale.monster_chat_634_3 = "Ça te dit de te battre? "
gameforge.locale.monster_chat_634_4 = "Si tu es infecté par mon poison, il n'existe aucun remède. "
gameforge.locale.monster_chat_634_5 = "Ma magie brûlera tout, ah ah ah ! "
gameforge.locale.monster_chat_634_6 = "Brûle! Haha! "
gameforge.locale.monster_chat_635_1 = "N'oublie jamais d'affûter ton épée! Il y a toujours un autre combat qui t'attend. "
gameforge.locale.monster_chat_635_2 = "Panne? "
gameforge.locale.monster_chat_635_3 = "Meurs, comme ça tu pourras te réveiller en enfer ! "
gameforge.locale.monster_chat_635_4 = "Me vaincre? Vraiment? "
gameforge.locale.monster_chat_635_5 = "Bats-toi comme un guerrier! (vlan) "
gameforge.locale.monster_chat_635_6 = "Encore un petit peu. "
gameforge.locale.monster_chat_636_1 = "J'ai envie d'un bon combat. "
gameforge.locale.monster_chat_636_2 = "C'est amusant de se battre. (rire) "
gameforge.locale.monster_chat_636_3 = "Je voudrais passer ma vie à me battre! "
gameforge.locale.monster_chat_636_4 = "Ah c'est bon ! Essaie encore ! "
gameforge.locale.monster_chat_636_5 = "Regarde bien mon épée ! "
gameforge.locale.monster_chat_636_6 = "Tu vas regretter de m'avoir touché! "
gameforge.locale.monster_chat_636_7 = "Quelle faiblesse! Ridicule ! "
gameforge.locale.monster_chat_637_1 = "Je ne veux pas être dérangé. "
gameforge.locale.monster_chat_637_2 = "Va te coucher. Pour toujours ! "
gameforge.locale.monster_chat_637_3 = "Toujours pas endormi? "
gameforge.locale.monster_chat_637_4 = "Il y a beaucoup à faire ! Allons-y ! "
gameforge.locale.monster_chat_637_5 = "Ne sois pas étonné de ma puissance! "
gameforge.locale.monster_chat_637_6 = "Je vais te pulvériser! "
gameforge.locale.monster_chat_691_1 = "J'aime avoir un adversaire à la hauteur ! "
gameforge.locale.monster_chat_691_2 = "Quoi? De quoi j'ai l'air? "
gameforge.locale.monster_chat_691_3 = "Ne me comparez pas à ce débutant! "
gameforge.locale.monster_chat_691_4 = "Toi, je ne t'aime pas, lui non plus ne t'aime pas ! "
gameforge.locale.monster_chat_691_5 = "Battons-nous. "
gameforge.locale.monster_chat_691_6 = "Jusqu'à la mort! "
gameforge.locale.monster_chat_701_1 = "Regarde, un papillon ! Comme il est mignon ! "
gameforge.locale.monster_chat_701_2 = "Ne voyez-vous pas la vérité du ciel et de la terre? "
gameforge.locale.monster_chat_701_3 = "Après, il sera trop tard pour croire. Convertissez-vous maintenant! "
gameforge.locale.monster_chat_701_4 = "Embrassez la doctrine des mystiques! "
gameforge.locale.monster_chat_701_5 = "Ceux qui doutent périront! "
gameforge.locale.monster_chat_701_6 = "Que la mort vous éclaire! "
gameforge.locale.monster_chat_702_1 = "La vérité mystique ne peut être révélée qu'en comprenant l'ordre de toute chose sur la terre comme dans le ciel. "
gameforge.locale.monster_chat_702_2 = "La philosophie mystique est l'ordre cosmique! "
gameforge.locale.monster_chat_702_3 = "Vérité mystique; vérité du Dieu Tigre; Sabaha Sabaha. "
gameforge.locale.monster_chat_702_4 = "Tu oses douter de la vérité du ciel et de la terre? "
gameforge.locale.monster_chat_702_5 = {}
gameforge.locale.monster_chat_702_6 = "Voici mon épée ! "
gameforge.locale.monster_chat_703_1 = "Quand la vérité du Dieu Tigre sera chantée pour les siècles des siècles, toute souffrance disparaîtra. "
gameforge.locale.monster_chat_703_2 = "La vérité mystique ne peut être révélée qu'en comprenant l'ordre de toute chose sur la terre comme dans le ciel. "
gameforge.locale.monster_chat_703_3 = "Sabaha Sabaha. Accorde-nous la connaissance! "
gameforge.locale.monster_chat_703_4 = "Ne doute pas! "
gameforge.locale.monster_chat_703_5 = "Que la foudre des cieux s'abatte sur toi! "
gameforge.locale.monster_chat_703_6 = "La frappe du Jugement! "
gameforge.locale.monster_chat_704_1 = "La vérité mystique ne peut être révélée qu'en comprenant l'ordre de toute chose sur la terre comme dans le ciel. "
gameforge.locale.monster_chat_704_2 = "Vérité mystique; vérité du Dieu Tigre; Sabaha Sabaha. "
gameforge.locale.monster_chat_704_3 = "Tu penses que je suis un clown ? Plus pour longtemps ! "
gameforge.locale.monster_chat_704_4 = "Le chef de cette secte est un saint et il est supérieur à tous les êtres vivants ! "
gameforge.locale.monster_chat_704_5 = "Que les flammes de l'enfer t'emportent! "
gameforge.locale.monster_chat_704_6 = "Tu n'as jamais rien vu d'aussi gros avant n'est-ce pas ? "
gameforge.locale.monster_chat_705_1 = "Vérité mystique; vérité du Dieu Tigre; Sabaha Sabaha. "
gameforge.locale.monster_chat_705_2 = "La vérité mystique ne peut être révélée qu'en comprenant l'ordre de toute chose sur la terre comme dans le ciel. "
gameforge.locale.monster_chat_705_3 = "Ce n'est pas parce que je suis plus petit que mon frère que je vais tout supporter sans rien dire ! "
gameforge.locale.monster_chat_705_4 = "Souffrance éternelle et ténèbres vous attendent ! "
gameforge.locale.monster_chat_705_5 = {}
gameforge.locale.monster_chat_705_6 = "Ce n'est pas la fin. Ça sera mieux après. "
gameforge.locale.monster_chat_706_1 = "Nous demandons la paix et qu'on nous laisse tranquilles! S'il vous plaît! "
gameforge.locale.monster_chat_706_2 = "Si la peur domine, la vérité du Dieu Tigre nous aidera! "
gameforge.locale.monster_chat_706_3 = "La vérité mystique révèle l'ordre cosmique. "
gameforge.locale.monster_chat_706_4 = "Allons tous en enfer! "
gameforge.locale.monster_chat_706_5 = "Je ne te laisserai pas partir! "
gameforge.locale.monster_chat_706_6 = "Je vous donnerai tout ce que j'ai. "
gameforge.locale.monster_chat_707_1 = "Je vous en prie sauvez-nous! "
gameforge.locale.monster_chat_707_2 = "La vérité mystique ne peut être révélée qu'en comprenant l'ordre de toute chose sur la terre comme dans le ciel. "
gameforge.locale.monster_chat_707_3 = "Vérité mystique; vérité du Dieu Tigre; Sabaha Sabaha. "
gameforge.locale.monster_chat_707_4 = "(rire bruyant) Les portes de l'enfer sont grandes ouvertes. Meurs! "
gameforge.locale.monster_chat_707_5 = "Je te préviens ! Ne m'oblige pas à utiliser le tableau ! "
gameforge.locale.monster_chat_707_6 = "Je vois ta mort, déjà! "
