gameforge.main_quest_flame_lv100 = {}
gameforge.main_quest_flame_lv100._010_sendLetter = "Matière obscure "
gameforge.main_quest_flame_lv100._020_say = "Yak-Hwan vous demande. Allez le voir. "
gameforge.main_quest_flame_lv100._030_say = "Bienvenue. Yon-I-Walker a terminé ses recherches.[ENTER]Le phénomène que vous avez pu observer dans la[ENTER]Terre de Feu est appelé matière obscure. "
gameforge.main_quest_flame_lv100._040_say = "Elle rayonne depuis les entrailles de la[ENTER]forteresse et métamorphose les démons des[ENTER]environs. Plus ils sont soumis longtemps à son[ENTER]influence, plus ils deviennent forts et[ENTER]agressifs.[ENTER]Nous devons empêcher la matière obscure de se[ENTER]répandre encore plus ! "
gameforge.main_quest_flame_lv100._050_say = {}
gameforge.main_quest_flame_lv100._060_say = "Nous n'allons pas nous tourner les pouces pendant[ENTER]que l'alchimiste cherche une solution. Retournez[ENTER]dans la Terre de Feu et tuez 300 démons dans les[ENTER]environs de la forteresse. "
gameforge.main_quest_flame_lv100._070_say = "Bien entendu. "
gameforge.main_quest_flame_lv100._080_say = "Merci. Et maintenant, dépêchez-vous ! "
gameforge.main_quest_flame_lv100._090_notice = "Tuez 300 démons de la Terre de Feu aux alentours[ENTER]de la forteresse et à l'endroit où vous avez[ENTER]trouvé les herbes solaires. "
gameforge.main_quest_flame_lv100._100_sayTitle = "Dämonenhatz "
gameforge.main_quest_flame_lv100._110_say = "Erledige 300 Feuerland-Dämonen in der Nähe der Festung und dem Fundort der Sonnenkräuter. "
gameforge.main_quest_flame_lv100._120_say = "Je crains que vous n'ayez pas encore tué assez de[ENTER]démons. Retournez dans la Terre de Feu et[ENTER]continuez le combat. "
gameforge.main_quest_flame_lv100._130_notice = "Vous avez tué tous les démons.[ENTER]Faites votre rapport à Yak-Hwan. "
gameforge.main_quest_flame_lv100._140_say = "Rendez visite à Yak-Hwan. "
gameforge.main_quest_flame_lv100._150_say = "Très bien, voilà qui devrait avoir ralenti la[ENTER]propagation de la matière obscure. Reposez-vous[ENTER]pendant que je m'entretiens avec l'alchimiste. "
gameforge.main_quest_flame_lv100._160_sendLetter = "Teinture des nimbes "
gameforge.main_quest_flame_lv100._170_say = "L'alchimiste vous demande. Cela semble urgent ! "
gameforge.main_quest_flame_lv100._180_say = "Une véritable catastrophe ! J'avais pourtant bien[ENTER]demandé à Yak-Hwan d'agir prudemment. Et que[ENTER]fait-il ? Il nous précipite dans le chaos ! La[ENTER]matière obscure se propage plus vite lorsque[ENTER]périt l'hôte chez qui elle se tapit. Et[ENTER]j'apprends que vous avez déjà tué 300 démons[ENTER]infectés ! Ce n'est pas bien... "
gameforge.main_quest_flame_lv100._190_say = "Dans ces conditions, ma teinture des nimbes ne[ENTER]suffit naturellement plus à bannir la matière[ENTER]obscure. Il me faut, de toute urgence, des[ENTER]ingrédients me permettant d'en fabriquer plus. "
gameforge.main_quest_flame_lv100._200_say = {}
gameforge.main_quest_flame_lv100._210_say = "Ramenez-moi 10 pierres de fondation, 50 biles[ENTER]d'ours, 10 glandes venin d'araignée et 200[ENTER]fragments d'énergie. J'imagine qu'il est inutile[ENTER]d'expliquer à un guerrier aguerri comme vous[ENTER]comment trouver tous ces ingrédients, n'est-ce[ENTER]pas ? "
gameforge.main_quest_flame_lv100._220_say = "Je pars immédiatement. "
gameforge.main_quest_flame_lv100._230_say = "Parfait. Dépêchez-vous car chaque minute qui[ENTER]passe sans que nous n'entreprenions quoi que ce[ENTER]soit permet à la matière obscure de se propager[ENTER]d'avantage. "
gameforge.main_quest_flame_lv100._240_notice = "Besorge die Zutaten, mit denen der Alchemist die Nimbustinktur herstellen kann. "
gameforge.main_quest_flame_lv100._250_say = "Verbleibende Zutaten "
gameforge.main_quest_flame_lv100._260_sayTitle = "Teinture des nimbes "
gameforge.main_quest_flame_lv100._270_say = "Procurez-vous 10 pierres de fondation, 50 biles[ENTER]d'ours, 10 glandes venin d'araignée et 200[ENTER]fragments d'énergie. Rapportez ces ingrédients à[ENTER]l'alchimiste. "
gameforge.main_quest_flame_lv100._280_say = "Vous avez récupéré tous les ingrédients ![ENTER]Parfait, je vais pouvoir me mettre au travail. Je[ENTER]vous fais appeler sitôt que la teinture des[ENTER]nimbes est prête. "
gameforge.main_quest_flame_lv100._290_say = "Si nous n'arrêtons pas bientôt la matière[ENTER]obscure, les conséquences s'annoncent fort[ENTER]sinistres.[ENTER]Allez, ramenez-moi les ingrédients pour la[ENTER]teinture des nimbes. "
gameforge.main_quest_flame_lv100._300_notice = "Vous avez réuni tous les ingrédients. Retournez[ENTER]voir l'alchimiste. "
gameforge.main_quest_flame_lv100._310_say = "Parlez à l'alchimiste. "
gameforge.main_quest_flame_lv100._320_sendLetter = "Arrêter la matière obscure "
gameforge.main_quest_flame_lv100._330_say = "Quelle... pfff... Je me suis plaint auprès du[ENTER]forgeron que les pierres de fondation étaient[ENTER]moulues trop grossièrement. Et voilà qu'il est[ENTER]vexé et refuse de réduire en poudre le reste des[ENTER]pierres de fondation. Cette vieille tête de[ENTER]mule ![ENTER]Pourriez-vous aller lui parler ? "
gameforge.main_quest_flame_lv100._340_say = "Bien sûr. "
gameforge.main_quest_flame_lv100._350_say = "Merci. Apportez-lui donc ce panier de victuailles[ENTER]en guise d'excuse et persuadez-le de continuer à[ENTER]moudre finement les pierres de fondation. "
gameforge.main_quest_flame_lv100._360_sayTitle = "Apaiser le forgeron "
gameforge.main_quest_flame_lv100._370_say = "Apaisez le forgeron et persuadez-le de moudre les[ENTER]pierres de fondation en une poudre fine. "
gameforge.main_quest_flame_lv100._380_say = "Poudre de pierre de fondation : 1 sachet "
gameforge.main_quest_flame_lv100._390_say = "Que puis-je faire pour vous ? Affûter votre épée ?Polir votre armure ? "
gameforge.main_quest_flame_lv100._400_say = "Non, pas aujourd'hui. Je suis venu car[ENTER]l'alchimiste souhaite demander pardon pour ses[ENTER]mots durs. Il vous envoie ce panier de[ENTER]victuailles. "
gameforge.main_quest_flame_lv100._410_say = "Aider cet ingrat personnage me répugne. Mais[ENTER]puisque c'est vous qui le demandez, je vais[ENTER]continuer à moudre les pierres de fondations.[ENTER]Attendez un instant. "
gameforge.main_quest_flame_lv100._420_say = "Je vous remercie. "
gameforge.main_quest_flame_lv100._430_say = "Le forgeron s'entête-t-il ? Donnez-lui le panier[ENTER]et demandez-lui de l'aide. Si c'est vous qui[ENTER]demandez, il ne pourra pas refuser. "
gameforge.main_quest_flame_lv100._440_notice = "La poudre de pierres de fondation est prête.[ENTER]Ramenez-la à l'alchimiste. "
gameforge.main_quest_flame_lv100._450_say = "Oh, fein wie Babypuder, ausgezeichnet! Der alte Sturkopf kann es also doch, hehe. Nun kann ich die Nimbustinktur mischen. Ruhe dich etwas aus. Ich werde dich rufen, wenn sie fertig ist. "
gameforge.main_quest_flame_lv101 = {}
gameforge.main_quest_flame_lv101._010_sendLetter = "Forteresse mystérieuse "
gameforge.main_quest_flame_lv101._020_say = "Yak-Hwan vous demande. Allez le voir et découvrez[ENTER]ce qu'il vous veut. "
gameforge.main_quest_flame_lv101._030_say = "L'alchimiste m'a informé de l'erreur monumentale[ENTER]que j'ai commise. J'espère qu'il n'est pas trop[ENTER]tard pour la réparer. "
gameforge.main_quest_flame_lv101._040_say = "Je vais enduire votre arme de teinture des nimbes.Désormais, lorsque vous tuerez des démons[ENTER]infectés, la matière obscure qu'ils renferment[ENTER]sera exorcisée et ne pourra plus se propager. "
gameforge.main_quest_flame_lv101._050_say = "Vous pouvez maintenant vous risquer dans[ENTER]l'enceinte de la forteresse mystérieuse. Vous[ENTER]possédez déjà le ticket nécessaire pour y entrer.[ENTER]Peut-être y découvrirez un indice sur les[ENTER]agissements des serviteurs de l'Obscurité et sur[ENTER]ce qu'il est advenu de mon éclaireur. "
gameforge.main_quest_flame_lv101._060_say = {}
gameforge.main_quest_flame_lv101._070_say = "Je me mets tout de suite en route. "
gameforge.main_quest_flame_lv101._080_say = "Danke. Ich wünsche dir das Beste. "
gameforge.main_quest_flame_lv101._090_notice = "Introduisez-vous dans la forteresse et combattez[ENTER]les démons que vous y trouverez.[ENTER]Soyez à l'affût d'indices importants ainsi que de[ENTER]l’éclaireur disparu. "
gameforge.main_quest_flame_lv101._100_sayTitle = "Festung erkunden "
gameforge.main_quest_flame_lv101._110_say = "Introduisez-vous dans la forteresse et combattez[ENTER]les démons que vous y trouverez.[ENTER]Recherchez l'éclaireur disparu ainsi que des[ENTER]indices sur les agissements des serviteurs. "
gameforge.main_quest_flame_lv101._120_sayReward = "Du kannst diesen Dungeon nicht alleine betreten. Bilde eine Gruppe, um passieren zu dürfen. Du erhältst keinen weiteren Passierschein. "
gameforge.main_quest_flame_lv101._130_say = "Avez-vous déjà été dans l'enceinte de la[ENTER]forteresse ? Il doit être possible d'y trouver[ENTER]des indices sur les agissements des serviteurs de[ENTER]l'obscurité ainsi que sur ce qu'il est advenu de[ENTER]mon éclaireur. Retournez dans la Terre de Feu et[ENTER]explorez la forteresse. "
gameforge.main_quest_flame_lv101._140_say = "Der Späher ist schon eine Weile tot. In seiner Tasche findest du einen Brief. Überbringe ihn Yak-Hwan. "
gameforge.main_quest_flame_lv101._150_notice = "Vous avez découvert l'éclaireur de Yak-Hwan. "
gameforge.main_quest_flame_lv101._160_say = "Hast du Neuigkeiten? "
gameforge.main_quest_flame_lv101._170_say = {}
gameforge.main_quest_flame_lv101._180_say = "Et elles ne sont pas bonnes, si j'en crois votre[ENTER]mine défaite... c'est bien ce que je craignais.[ENTER]Il portant une lettre ? Montrez-moi ça ! La[ENTER]forteresse doit également abriter des indices sur[ENTER]les agissements de serviteurs de l'Obscurité.[ENTER]Retournez-y et cherchez-en. Nous lirons cette[ENTER]lettre plus tard. "
gameforge.main_quest_flame_lv101._190_notice = "Vous avez découvert un collier clouté des[ENTER]serviteurs de l'Obscurité. Montrez-le à Yak-Hwan. "
gameforge.main_quest_flame_lv101._200_say = "C'est intéressant. Ce collier clouté appartient[ENTER]aux serviteurs de l'Obscurité. Peut-être[ENTER]contient-il des indices sur leurs agissements ![ENTER]Et toujours aucune trace de mon éclaireur ? Je[ENTER]vous en prie, continuez à le chercher. Il doit se[ENTER]trouver dans la forteresse. "
gameforge.main_quest_flame_lv101._210_notice = "Vous avez découvert un collier clouté ainsi que[ENTER]l'éclaireur. Allez faire votre rapport à[ENTER]Yak-Hwan. "
gameforge.main_quest_flame_lv101._220_say = "Informez Yak-Hwan. "
gameforge.main_quest_flame_lv101._230_say = "Ainsi, vous avez réussi ! Ce collier clouté[ENTER]appartient aux serviteurs de l'Obscurité. Il[ENTER]contient sans doute un indice sur leurs projets.[ENTER]Et qu'est-ce que vous avez là ? Une lettre ?[ENTER]Montrez-moi ça ! "
gameforge.main_quest_flame_lv101._240_say = {}
gameforge.main_quest_flame_lv101._250_say = "Ein Brief ... den mir mein Späher hinterlassen hat? Ich bin erschüttert. Lass uns gemeinsam seine letzten Worte lesen. "
gameforge.main_quest_flame_lv101._260_sayTitle = "Mots griffonnés à la hâte : "
gameforge.main_quest_flame_lv101._270_say = "Yak-Hwan, je me fais aucune illusion sur mes[ENTER]chances de rentrer vivant, mais j’espère que[ENTER]cette lettre vous parviendra. Lors de mon[ENTER]exploration de la Terre de Feu, je suis tombé sur[ENTER]une forteresse dont aucune carte ne fait mention.[ENTER]Son maître est un puissant démon de la Terre de[ENTER]Feu et il est enveloppé par une aura étrange, de[ENTER]même que tous les démons sous ses ordres.[ENTER]Ses hurlements sont assourdissants et il semble[ENTER]protéger quelque chose. Malheureusement, je n'ai[ENTER]pas réussi à découvrir de quoi il s'agit.[ENTER]Veuillez pardonner cet échec... "
gameforge.main_quest_flame_lv101._280_say = "Il nous faut découvrir ce que cette forteresse[ENTER]abrite.[ENTER]Regardez, une inscription est gravée sur la paroi[ENTER]intérieure du collier. Je vais demander conseil à[ENTER]Yon-I-Walker. Il vous fera appeler sitôt qu'il[ENTER]aura besoin de votre aide. "
gameforge.main_quest_flame_lv101._290_sendLetter = "Gravur entschlüsseln "
gameforge.main_quest_flame_lv101._300_say = "Yon-I-Walker vous demande. Allez le voir. "
gameforge.main_quest_flame_lv101._310_say = "Willkommen, ich habe dich bereits erwartet. Ich brauche etwas Hilfe dabei, die Gravur in diesem Nietenhalsband zu entschlüsseln. "
gameforge.main_quest_flame_lv101._320_say = {}
gameforge.main_quest_flame_lv101._330_say = "Les caractères recouvrant la paroi intérieure du[ENTER]collier clouté proviennent d'une culture antique,[ENTER]ayant prospéré il y a bien longtemps. Afin de la[ENTER]déchiffrer, j'ai besoin de sang du roi flamme.[ENTER]C'est un véritable torrent de lave qui coule dans[ENTER]les veines de ce démon ! "
gameforge.main_quest_flame_lv101._340_say = "Je m'en occupe. "
gameforge.main_quest_flame_lv101._350_say = "Wenn du das Blut erhalten hast, musst du so schnell wie möglich zu mir zurückkehren. Denn selbst dieses Gefäß kann seine Hitze nur für eine begrenzte Zeit ohne Schaden überstehen. "
gameforge.main_quest_flame_lv101._360_notice = "Récupérez le sang du roi flamme. "
gameforge.main_quest_flame_lv101._370_say = "Sans la moindre goutte de sang du roi flamme, il[ENTER]nous est impossible de déchiffrer l'inscription. "
gameforge.main_quest_flame_lv101._380_notice = "Vous avez obtenu le sang du roi flamme. Retournez[ENTER]voir Yon-I-Walker. "
gameforge.main_quest_flame_lv101._390_say = "Parlez à Yon-I-Walker. "
gameforge.main_quest_flame_lv101._400_say = "Ah, très bien. Ce sang va me permettre de[ENTER]déchiffrer l'inscription. Toutefois, cela va[ENTER]prendre un peu de temps. Revenez demain. "
gameforge.main_quest_flame_lv101._410_say = "Patientez encore %d heure(s) et %d minute(s). "
gameforge.main_quest_flame_lv101._420_say = "Il me faut encore un peu de temps. Revenez plus[ENTER]tard ! "
gameforge.main_quest_flame_lv101._430_sendLetter = "L'inscription "
gameforge.main_quest_flame_lv101._440_say = "Vous arrivez au bon moment ! J'ai réussi à[ENTER]déchiffrer l'inscription du collier clouté. "
gameforge.main_quest_flame_lv101._450_say = "Sie verrät uns, dass die Diener der Dunkelheit einen der mächtigesten Feuerdämonen entsandt haben, um die Siegel der Tore zu schmelzen, hinter denen die Sechs Drachen an einem verborgenen Ort gefangen gehalten werden. Es wird auch ein spezieller Schmelzofen erwähnt. Ich habe alles in diesem Schreiben an Yak-Hwan festgehalten. Bringe es bitte zu ihm! "
gameforge.main_quest_flame_lv101._460_say = {}
gameforge.main_quest_flame_lv101._470_notice = "Überbringe Yak-Hwan den Brief von Yon-I-Walker. "
gameforge.main_quest_flame_lv101._480_sayTitle = "Post für Yak-Hwan "
gameforge.main_quest_flame_lv101._490_say = "Überbringe Yak-Hwan den Brief von Yon-I-Walker. "
gameforge.main_quest_flame_lv101._500_say = "Konnte Yon-I-Walker die Gravur lesen? Oh, ein Brief mit allen Informationen von ihm? Sehr gut, zeig her! "
