gameforge.main_quest_lv7._10_targetVid = "Une autre faveur "
gameforge.main_quest_lv7._110_npcChat = "Le Forgeron a envoyé cette nouveauté ! "
gameforge.main_quest_lv7._120_say = "Merci. Je peux me débarrasser de cette vieille[ENTER]mais néanmoins jolie canne. Je ne gagne pas ma[ENTER]vie rien qu'en pêchant, mais c'est mon plus grand[ENTER]plaisir. Avez-vous quelques connaissances en[ENTER]pêche ? "
gameforge.main_quest_lv7._130_say = "D'abord, prenez la canne à pêche dans votre main.[ENTER]Mettez une amorce sur le crochet. Jetez-le alors[ENTER]utilisant votre compétence de pêche. Si vous[ENTER]voyez qu'un poisson a mordu (une bulle[ENTER]l'indique), utilisez votre compétence de pêche de[ENTER]nouveau. Parfois ça marche, parfois il faut[ENTER]retenter à nouveau. Il y a toutes sortes de[ENTER]poissons que vous pouvez attraper. Le poisson est[ENTER]bon pour votre santé et parfois, vous pêchez des[ENTER]trésors de valeur. Essayez-le donc quand vous[ENTER]avez du temps à perdre ! "
gameforge.main_quest_lv7._140_say = "Oh oui, voici l'argent pour payer la canne à[ENTER]pêche. S'il te plaît, donne-la au forgeron. "
gameforge.main_quest_lv7._150_sayItem = "Paiement de la canne à pêche "
gameforge.main_quest_lv7._160_sayReward = "Tu as donné la canne à pêche au pêcheur. Retourne[ENTER]voir le forgeron avec l'argent et tu recevras ta[ENTER]récompense. "
gameforge.main_quest_lv7._170_say = "Avez-vous trouvé le pêcheur ?[ENTER]Formidable. Vous m'avez fait gagner beaucoup de[ENTER]temps en faisant cette course. En récompense, je[ENTER]vous donne des Yangs et 1 500 points[ENTER]d'expérience. "
gameforge.main_quest_lv7._180_sendLetter = "Un bon pour une amélioration "
