gameforge.main_quest_lv7._200_sayItem = "Bon pour le raffinage gratuit d'un objet "
gameforge.main_quest_lv7._20_sayReward = "Allez voir le forgeron. Il a besoin de quelqu'un[ENTER]pour faire une livraison pour lui. "
gameforge.main_quest_lv7._210_sayTitle = "Conseil : "
gameforge.main_quest_lv7._220_sayReward = "1) Le bon ne sera pas visible dans l'inventaire.[ENTER]2) Le succès du raffinage – comme tout raffinage[ENTER]– n'est pas garanti à 100 %. "
gameforge.main_quest_lv7._230_sayTitle = "Le bon a déjà été utilisé. "
gameforge.main_quest_lv7._240_sayReward = "Vous avez déjà utilisé votre bon pour un[ENTER]raffinage gratuit. Il n'est valable qu'une seule[ENTER]fois. "
gameforge.main_quest_lv7._30_say = "Hé, aventurier ! Pouvez-vous me rendre une autre[ENTER]faveur ? Habituellement, je ne demande pas à[ENTER]d'autre personne de faire des choses pour moi,[ENTER]mais tu semble être une personne de confiance.[ENTER]C'est tout à ton honneur ! "
gameforge.main_quest_lv7._40_say = "J'ai fait une canne à pêche pour le pêcheur; mais[ENTER]je suis très occupé. Je n'ai pas le temps de la[ENTER]lui donner moi-même. Peux-tu le faire pour moi? "
gameforge.main_quest_lv7._50_sayItem = "Une canne à pêche très flexible "
gameforge.main_quest_lv7._60_say = "Le pêcheur devrait être quelque part à côté de la[ENTER]rivière. Si tu donnes ce colis au pêcheur, je[ENTER]raffinerai un objet de ton équipement[ENTER]gratuitement. C'est un bon deal, tu ne trouves[ENTER]pas ? "
gameforge.main_quest_lv7._70_sendLetter = "Trouvez le Pêcheur "
gameforge.main_quest_lv7._80_sayReward = "Le forgeron a fait une canne à pêche pour le[ENTER]pêcheur. Trouve le pêcheur près de la rivière, et[ENTER]donne-lui sa nouvelle canne à pêche. "
gameforge.main_quest_lv7._90_npcChat = "Pour qui est la canne à pêche ? "
