gameforge.main_quest_lv72._100_say = "Le lien magique qui unit la résine d'arbre sacré,[ENTER]l'essence de cristal bleu et le quartz de l'enfer[ENTER]rouge donne à la larme du ciel son rayonnement[ENTER]parfait.[ENTER]L'essence de cristal bleu vous sera fournie par[ENTER]le seigneur de la Terre Glaciaire. "
gameforge.main_quest_lv72._10_sendLetter = "Pierre effritée "
