gameforge.upgrade_refine_scroll._20_say = "Les parchemins de Guerre sont des objets[ENTER]mystérieux qui augmentent les objets de +0 à + 3[ENTER]avec un taux de succès de 100%. Par contre, ils[ENTER]ne marchent pas sur les objets +4 ou supérieurs. "
gameforge.upgrade_refine_scroll._210_say = "Pour le fabriquer, vous avez besoin d'une pierre[ENTER]du forgeron. "
gameforge.upgrade_refine_scroll._220_sayItem = "Pierre du forgeron "
gameforge.upgrade_refine_scroll._230_say = "Pour fabriquer cela, vous avez besoin d'un[ENTER]Parchemin de Bénédiction. "
gameforge.upgrade_refine_scroll._240_say = "Avez-vous tous les matériaux nécessaires? Donc,[ENTER]vous voulez fabriquer un Manuel du forgeron qui[ENTER]se transmet dans ma famille de génération en[ENTER]génération? "
gameforge.upgrade_refine_scroll._250_say = "Vous vous êtes fabriqué un Manuel du forgeron![ENTER]Vous connaissez donc tout sur la Dynastie des[ENTER]forgerons. Utilisez-le avec sagesse ! Ah ah ah ! "
gameforge.upgrade_refine_scroll._260_sayItem = "Manuel du forgeron "
gameforge.upgrade_refine_scroll._270_say = "Vous ne pouvez pas créer de Manuel du forgeron[ENTER]car vous ne portez pas sur vous tous les[ENTER]matériaux nécessaires. "
gameforge.upgrade_refine_scroll._30_say = "Pour fabriquer cela, vous avez besoin de cuivre[ENTER]magique. "
gameforge.upgrade_refine_scroll._40_sayItem = "Cuivre magique "
gameforge.upgrade_refine_scroll._50_say = "En plus, vous avez besoin d'un parchemin de[ENTER]bénédiction. "
gameforge.upgrade_refine_scroll._60_sayItem = "Parchemin de Bénédiction "
gameforge.upgrade_refine_scroll._70_say = "Avez-vous les matériaux nécessaires ?[ENTER]Vous avez aussi besoin de 200 000 Yangs.[ENTER]Si vous avez le parchemin de bénédiction, du[ENTER]cuivre magique et les 200 000 Yangs vous pouvez[ENTER]fabriquer un parchemin de guerre.[ENTER]Voulez-vous en fabriquer un ? "
gameforge.upgrade_refine_scroll._80_say = "Vous avez produit un Parchemin. C'est facile de[ENTER]créer un parchemin de guerre si vous avez du[ENTER]Cuivre magique![ENTER]J'aime ça! Ha, ha ha! "
gameforge.upgrade_refine_scroll._90_sayItem = "Parchemin de guerre "
gameforge.warehouse = {}
gameforge.warehouse._100_say = "Soyez le bienvenu ! Savez-vous à quoi servent les[ENTER]lingots ?[ENTER]Si vous vous promenez avec des masses de Yangs,[ENTER]c'est comme si vous disiez : Tuez-moi, s'il vous[ENTER]plaît ![ENTER]C'est pourquoi j'échange vos Yangs contre des[ENTER]lingots d'or et les conserve pour vous. "
gameforge.warehouse._10_npcChat = "Voulez-vous utiliser l'entrepôt? "
gameforge.warehouse._110_say = "Certes, je demande beaucoup plus que le prix[ENTER]usuel d'un lingot d'or. Mais c'est justifié pour[ENTER]un tel service,n'est-ce pas ? Pas de raison de[ENTER]vous sentir trahi. Comme vous le savez, c'est[ENTER]dangereux par ici. "
gameforge.warehouse._120_npcChat = "Ouvrir l'entrepôt de la boutique "
gameforge.warehouse._130_say = "Humm...[ENTER]Les mines sont toutes épuisées.[ENTER]Il est très dur de trouver de l'or ou de[ENTER]l'argent. Tant qu'on ne trouvera pas de nouveaux[ENTER]filons, il n'y aura pas de nouveaux lingots d'or[ENTER]ou d'argent. "
gameforge.warehouse._140_say = "Bienvenue ! Vous voulez probablement visiter[ENTER]votre entrepôt de la boutique ? Pourquoi[ENTER]venez-vous donc me voir ? Regardez simplement[ENTER]dans votre inventaire, vous y trouverez un accès[ENTER]direct. "
gameforge.warehouse._150_say = "Cliquez tout simplement sur ce symbole qui vous[ENTER]permet d'atteindre n'importe quand et depuis[ENTER]n'importe où votre entrepôt de la boutique. Le[ENTER]mot de passe est 000000, si vous ne l'avez pas[ENTER]encore modifié. "
gameforge.warehouse._20_sayTitle = "Magasinier : "
gameforge.warehouse._30_say = "Voulez-vous utiliser l'entrepôt ? Vous devez[ENTER]payer des Yangs pour pouvoir l'utiliser. Je suis[ENTER]de bonne Humeur aujourd'hui : vous pouvez[ENTER]l'utiliser pour 500 Yangs seulement.[ENTER]Allez, venez, c'est pas cher ! "
gameforge.warehouse._40_say = "Quoi ? Vous n'avez pas assez de Yangs ?[ENTER]Revenez quand vous en aurez assez ! "
gameforge.warehouse._50_say = "Votre entrepôt est ouvert. Si vous voulez[ENTER]l'utiliser, il vous suffit de me parler.[ENTER]Le mot de passe pour votre entrepôt est: "
gameforge.warehouse._60_sayReward = "000000 "
gameforge.warehouse._70_say = "Vous devriez le changer pour des raisons de[ENTER]sécurité.[ENTER]Peut être pensez vous que 45 cases ne sont pas[ENTER]assez. Mais qui sait, peut être je te donnerais[ENTER]de l'espace supplémentaire si notre contrat[ENTER]s'éternise ! "
gameforge.warehouse._80_say = "Mais je n'oblige personne. Si vous n'avez pas[ENTER]envie maintenant, revenez quand vous voudrez. "
gameforge.warehouse._90_npcChat = "Ouvrir l'entrepôt "
