gameforge.main_quest_lv6._160_sayTitle = "Amministratore dei Magazzini: "
gameforge.main_quest_lv6._170_say = "Benvenuto, sono l'amministratore dei magazzini di[ENTER]questo villaggio.[ENTER]Se sei porti troppi oggetti con te, puoi[ENTER]depositarli in magazzino.[ENTER]Risulta molto più comodo.  "
gameforge.main_quest_lv6._180_say = "Vuoi per caso... Ah, ti manda il fabbro per il[ENTER]naso di maiale! "
