gameforge.main_quest_lv91._310_say = "Ah, sì! Vieni per la lettera. Angmur mi ha già[ENTER]detto. "
gameforge.main_quest_lv91._320_say = "Non sono ancora riuscito a decodificare la[ENTER]lettera. Sembra trattarsi della grafia della[ENTER]tribù dei Crostaciani della Baia Nefrite. "
gameforge.main_quest_lv91._330_say = "Per interpretare questi segni ho bisogno dei[ENTER]Manoscritti di Carapax della tribù dei[ENTER]Crostaciani.[ENTER]Riusciresti a procurarmene dieci pezzi? "
