gameforge.main_quest_lv93._050_say = "D'accordo. "
gameforge.main_quest_lv93._060_say = "Ah, ecco il mio salvatore![ENTER]La Luce interiore, oh sì... Il mio energizzante[ENTER]farà di te un avversario degno dei Servitori[ENTER]dell'Oscurità. "
gameforge.main_quest_lv93._070_say = "Per questo motivo ho bisogno della Luce interiore[ENTER]dei Ciclopi e dei Giganti. Solo i rispettivi capi[ENTER]tribù la possiedono. Occupati prima dei Ciclopi. "
gameforge.main_quest_lv93._080_say = "D'accordo. "
gameforge.main_quest_lv93._090_sayTitle = "La Luce interiore I "
gameforge.main_quest_lv93._100_say = "Sconfiggi Polifemo, Re dei Ciclopi, e rubagli la[ENTER]Luce interiore. "
