gameforge.main_quest_lv93._120_1_notice = "Hai trovato la Luce interiore. Portala a[ENTER]Camminatore Yon-I. "
gameforge.main_quest_lv93._130_say = "Parla con Camminatore Yon-I. "
gameforge.main_quest_lv93._140_say = "Così questa sarebbe la Luce interiore... prima[ENTER]che io possa lavorarla deve essere purificata. Ci[ENTER]vorrà del tempo. Ti faccio sapere. "
