gameforge.main_quest_lv93._160_say = "Camminatore Yon-I ti cerca. "
gameforge.main_quest_lv93._170_say = "Bene, la Luce interiore di Polifemo è purificata.[ENTER]Ora abbiamo bisogno di quella dei Giganti. Per[ENTER]questo dovrai sconfiggere il Re Wobba. "
gameforge.main_quest_lv93._180_say = "Mi avvio subito. "
gameforge.main_quest_lv93._190_sayTitle = "La Luce interiore II "
gameforge.main_quest_lv93._200_say = "Sconfiggi il Re Wobba e sottraigli la Luce[ENTER]interiore. "
gameforge.main_quest_lv93._210_say = "Vieni a mani vuote? Mi stupirebbe parecchio se tu[ENTER]non fossi all'altezza di questo compito. Su,[ENTER]avviati e riprova! "
gameforge.main_quest_lv93._220_1_notice = "Hai in mano la Luce interiore dei Giganti. "
gameforge.main_quest_lv93._220_2_notice = "Portala a Camminatore Yon-I. "
gameforge.main_quest_lv93._230_say = "Parla con Camminatore Yon-I. "
gameforge.main_quest_lv93._240_say = "La Luce interiore del Re Wobba possiede degli[ENTER]enormi poteri. Credo che ciò sia causato dalla[ENTER]mole.[ENTER]Ora devo pulirla e mi ci vorrà del tempo. "
gameforge.main_quest_lv93._250_sendLetter = "Ricarica forza III "
gameforge.main_quest_lv93._260_say = "Camminatore Yon-I ti cerca. "
gameforge.main_quest_lv93._270_say = "Molto bene, sei arrivato. Ho bisogno del tuo[ENTER]aiuto.[ENTER]Affinché il mio elisir energizzante possa avere[ENTER]la massima efficacia ho bisogno di un ulteriore[ENTER]componente: l'Essenza millenium. "
gameforge.main_quest_lv93._280_say = "Essenza millenium? Non ne ho mai sentito parlare. "
gameforge.main_quest_lv93._290_say = "Questa è una miscela efficacissima generata[ENTER]dall'essenza di varie erbe.[ENTER]Conosci Octavio? Vive sulla terraferma ed è[ENTER]l'unico in grado di produrre l'Essenza millenium.[ENTER]Vai da lui. Spero ci possa essere d'aiuto. A[ENTER]volte è un po' cocciuto... "
