gameforge.new_quest_lv28._010_target = "Yu-Hwan: "
gameforge.new_quest_lv28._011_say = "Il musicista Yu-Hwan ha bisogno del tuo aiuto.[ENTER]Vai subito a vedere cosa è successo! "
gameforge.new_quest_lv28._015_npcChat = "Le preoccupazioni di Ah-Yu "
gameforge.new_quest_lv28._016_say = "A quanto pare Ah-Yu ha delle preoccupazioni, ma[ENTER]sembra che non si voglia confidare con Yu-Hwan.[ENTER]Vai da lei, magari con te si apre un po' di più. "
gameforge.new_quest_lv28._017_say = "Ah-Yu si trova a %s. "
gameforge.new_quest_lv28._018_select = "Parlerò subito con Ah-Yu! "
gameforge.new_quest_lv28._019_say_title = "Ah-Yu: "
gameforge.new_quest_lv28._020_say_title = "Yu-Hwan: "
gameforge.new_quest_lv28._021_say = "Conosci Ah-Yu? Raccoglie le erbe per le mie[ENTER]pozioni. Finora è sempre stata di buonumore, ma[ENTER]da ieri c'è qualcosa che la turba. Non ha voluto[ENTER]dirmi cosa la affligge, forse perché lavora per[ENTER]me? Per favore, vai da lei e cerca di capire come[ENTER]sta. Chissà che non si confidi con te! "
gameforge.new_quest_lv28._022_say = "È davvero molto gentile da parte di Yu-Hwan[ENTER]preoccuparsi per me. Sì, in effetti c'è qualcosa[ENTER]che non va... a dire il vero non volevo parlarne,[ENTER]ma non riesco più a tenermi dentro questa[ENTER]sofferenza! Si tratta di Yang-Shin, il[ENTER]cacciatore. Da quando ha scoperto che mio marito[ENTER]Nakajima è un disertore, non fa altro che[ENTER]ricattarmi. Yang-Shin minaccia di raccontare[ENTER]tutto! E se così fosse, mio marito verrà[ENTER]ucciso... "
gameforge.new_quest_lv28._023_say = "In cambio del suo silenzio, questa volta mi ha[ENTER]chiesto di procurargli una zampa d'orso. Ma io[ENTER]non posso allontanarmi da qui per andare a[ENTER]caccia. Te la senti di aiutarmi e di portare a[ENTER]Yang-Shin quello che mi ha chiesto? "
gameforge.new_quest_lv28._024_set_title = "Caccia agli orsi "
gameforge.new_quest_lv28._025_notice = "E ora a caccia! La miglior soluzione per ottenere[ENTER]le zampe d'orso consiste nell'uccidere Bera. "
gameforge.new_quest_lv28._026_notice = "Sono riuscito a prendere una zampa d'orso! "
gameforge.new_quest_lv28._027_notice = "Dovrebbe esserci tutto. E adesso vai da Yang-Shin! "
gameforge.new_quest_lv28._028_notice = "La zampa di questo orso era inutilizzabile. "
gameforge.new_quest_lv28._029_say = "Devi procurare ancora %s zampa/e d'orso. "
gameforge.new_quest_lv28._031_say_reward = "Devi cacciare: "
gameforge.new_quest_lv28._032_say = "Ah-Yu ti ha chiesto di esaudire la richiesta di[ENTER]Yang-Shin. "
gameforge.new_quest_lv28._032_say_reward = "Devi procurare ancora %s zampa/e d'orso.[ENTER]Per ottenerle devi uccidere Bera. "
gameforge.new_quest_lv28._035_target = "Yang-Shin: "
gameforge.new_quest_lv28._036_say = "Hai procurato tutte le zampe d'orso. Ora portale[ENTER]a Yang-Shin e riscatta il debito di Ah-Yu. "
gameforge.new_quest_lv28._037_say = "Yang-Shin si trova a %s. "
gameforge.new_quest_lv28._038_say_title = "Yang-Shin: "
gameforge.new_quest_lv28._040_npcChat = "Il ricatto "
gameforge.new_quest_lv28._041_say_title = "Zampe d'orso "
gameforge.new_quest_lv28._042_say = "Mi stai portando delle zampe d'orso? Non puoi[ENTER]pensare male di me! Stiamo attraversando momenti[ENTER]difficili, e devo cercare di cavarmela in qualche[ENTER]modo. Soprattutto adesso che cacciare è diventato[ENTER]così difficile: da quando ci sono le pietre[ENTER]Metin, gli animali si sono trasformati in bestie[ENTER]assassine. La vita da cacciatore è diventata una[ENTER]sfida davvero molto pericolosa. "
gameforge.new_quest_lv28._043_say = "Ti prego, riferisci a Ah-Yu che non deve più aver[ENTER]paura. Ci può davvero contare. Almeno fino a[ENTER]quanto non avrò bisogno di qualcos'altro...[ENTER]hehehe! "
gameforge.new_quest_lv28._044_say = "Non vorrai liquidarmi in questo modo!? Avevo[ENTER]chiesto delle zampe d'orso fresche! "
gameforge.new_quest_lv28._045_say_reward = "È davvero gentile da parte tua aiutare Ah-Yu.[ENTER]Questo però significa che devi fare quello che ti[ENTER]chiedo. Vai a caccia, e torna da me solo quando[ENTER]avrai procurato due zampe d'orso! "
gameforge.new_quest_lv28._046_say = "Riferisci a Ah-Yu che, grazie al tuo aiuto,[ENTER]Yang-Shin smetterà di ricattarla. "
gameforge.new_quest_lv28._051_say = "Sei il nostro eroe! Ti sono infinitamente grata![ENTER]Ecco, prendi questi Yang. Non sono molti, ma te[ENTER]li sei davvero meritati. "
gameforge.new_quest_lv28._055_1_select = "Grazie mille, potrebbe sempre servirmi! "
gameforge.new_quest_lv28._055_2_select = "No, non posso accettare. "
gameforge.new_quest_lv28._056_say = "Ecco, questo è tutto quello che ho. Ma ne vale la[ENTER]pena, se serve per sbarazzarsi di questo disumano[ENTER]usuraio. "
gameforge.new_quest_lv28._057_say = "Oh, grazie mille! Ti sei guadagnato un posto[ENTER]vicino al Dio Drago! "
gameforge.new_quest_lv28._058_say_reward = "Ottieni %s Yang. "
gameforge.new_quest_lv28._059_say_reward = "Ottieni %s punti esperienza. "
gameforge.new_quest_lv28._070_say = "Ho bisogno di: "
gameforge.new_quest_lv29 = {}
gameforge.new_quest_lv29._010_target = "Yonah: "
gameforge.new_quest_lv29._015_npcChat = "La porcellana frantumata "
gameforge.new_quest_lv29._020_say_title = "Yonah: "
gameforge.new_quest_lv29._021_say = "La mia amata porcellana Seladon! Da tre[ENTER]generazioni accompagna la mia famiglia, ed ora[ENTER]tre dei preziosissimi vasi sono rotti. Poco fa ho[ENTER]visto quel furfante di Taurean fuggire di[ENTER]soppiatto dal magazzino. È stato sicuramente lui! "
gameforge.new_quest_lv29._022_say = "Non è la prima volta che fa una stupidaggine, ma[ENTER]questa volta ha davvero esagerato. Qualcuno[ENTER]dovrebbe dargli una bella lezione! Hai tempo di[ENTER]farlo? "
gameforge.new_quest_lv29._023_1_select = "Con grande piacere! "
gameforge.new_quest_lv29._023_2_select = "La cosa non mi riguarda. "
gameforge.new_quest_lv29._024_say = "Non ho tempo di insegnare l'educazione ai bambini[ENTER]maleducati. Dovrai acchiapparlo da solo.[ENTER]Tuttavia, dubito che tu ce la possa fare... "
gameforge.new_quest_lv29._030_target = "Taurean: "
gameforge.new_quest_lv29._031_say = "Tre vasi di porcellana Seladon, che facevano[ENTER]parte dell'eredità di famiglia di Yonah, sono[ENTER]stati trovati rotti. Yonah è convinto che sia[ENTER]colpa di Taurean. Vai da Taurean e fatti dire[ENTER]come sono andate le cose. "
gameforge.new_quest_lv29._032_say = "Taurean si trova a %s. "
gameforge.new_quest_lv29._033_say_title = "Taurean: "
gameforge.new_quest_lv29._034_say_reward = "Perché mi guardi in quel modo? Non ho fatto[ENTER]niente di male... "
gameforge.new_quest_lv29._035_say_reward = "Cosa hai fatto con i vasi di Yonah? "
gameforge.new_quest_lv29._036_say_reward = "Vasi? Yonah? Non so proprio di cosa tu stia[ENTER]parlando! "
gameforge.new_quest_lv29._037_say_reward = "E allora spiegami perché questi tre sono rotti. "
gameforge.new_quest_lv29._038_say_reward = "Ti giuro che oggi non mi sono proprio avvicinato[ENTER]ai vasi, né tanto meno ho visto Yonah. Ho fatto[ENTER]tutto il giorno la lotta con Harang. Pensa che mi[ENTER]addirittura rinchiuso un paio di ore nel fienile![ENTER]Prova a chiederglielo! Sto dicendo la verità! "
gameforge.new_quest_lv29._040_target = "Harang: "
gameforge.new_quest_lv29._041_say = "Taurean sostiene di essere innocente: dice di[ENTER]avere giocato tutto il giorno con Harang e di non[ENTER]essere stato lui a rompere i vasi. Vai da Harang[ENTER]e chiedile se è vero. "
gameforge.new_quest_lv29._042_say_reward = "Harang, hai visto Taurean oggi? "
gameforge.new_quest_lv29._043_say_title = "Harang: "
gameforge.new_quest_lv29._044_say_reward = "Taurean? Sì, abbiamo giocato assieme tutto il[ENTER]giorno. È successo qualcosa? "
gameforge.new_quest_lv29._045_say_reward = "Stando a quanto dice Yonah, Taurean oggi avrebbe[ENTER]rotto tre dei suoi vasi. "
gameforge.new_quest_lv29._046_say_reward = "Yonah è davvero maleducato. Io e Taurean non[ENTER]facciamo nemmeno a tempo ad avvicinarci, che lui[ENTER]ci sta già scacciando. Ha paura per la sua[ENTER]porcellana. Solo il suo amico stalliere può[ENTER]toccare i suoi preziosissimi vasi. Magari è stato[ENTER]proprio lui a romperli! "
gameforge.new_quest_lv29._047_say = "Harang si trova a %s. "
gameforge.new_quest_lv29._050_target = "Stalliere: "
gameforge.new_quest_lv29._051_say = "Harang ha confermato che Taurean è innocente. Ma[ENTER]può darsi che i due si siano solo messi[ENTER]d'accordo. Prima di emettere la tua sentenza,[ENTER]forse dovresti interrogare anche l'amico di[ENTER]Yonah, lo stalliere. "
gameforge.new_quest_lv29._052_say = "Ah! Il povero vecchio Yonah. Si preoccupa della[ENTER]sua porcellana e sospetta dei bambini del[ENTER]villaggio. Non si rende conto che in realtà la[ENTER]colpa è delle Rocce Metin: da quando stanno[ENTER]contaminando il nostro paese, le nostre risorse[ENTER]non sono più le stesse. Persino la porcellana è[ENTER]diventata più fragile. Ma Yonah si rifiuta di[ENTER]crederci... "
gameforge.new_quest_lv29._053_say = "Se tu gli procurassi un po' di fieno, potrebbe[ENTER]imbottire meglio i suoi vasi nei bauli. In questo[ENTER]modo, inoltre, potresti farlo rinsavire. "
gameforge.new_quest_lv29._054_say = "Lo stalliere si trova a[ENTER]%s o a %s. "
gameforge.new_quest_lv29._061_say = "Non è stato Taurean a rompere i vasi di Yonah.[ENTER]Sono sicuro che è colpa degli influssi negativi[ENTER]delle Rocce Metin, che hanno reso il materiale[ENTER]più fragile.[ENTER]Procura del fieno per Yonah, in modo che possa[ENTER]custodire meglio la sua porcellana! Sicuramente[ENTER]gli abitanti del villaggio saranno disposti a[ENTER]dartene un po'. "
gameforge.new_quest_lv29._062_say = "Alle porte del villaggio si trovano %s e %s.[ENTER]Indossano dei cappelli di paglia, dai quali si[ENTER]potrà ricavare sicuramente una buona quantità di[ENTER]fieno. "
gameforge.new_quest_lv29._063_notice = "Da qui posso ricavare un bel mazzetto di fieno.[ENTER]Adesso me ne mancano altri %s. "
gameforge.new_quest_lv29._064_notice = "Questo cappello non è stato realizzato con la[ENTER]paglia, bensì con dei filamenti di cocco. Non me[ENTER]ne faccio niente. "
gameforge.new_quest_lv29._065_1_say = "Vedo che hai parecchio fieno con te. Portane un[ENTER]po' a Yonah, sicuramente si tranquillizzerà. "
gameforge.new_quest_lv29._065_notice = "Il cappello non è stato realizzato con la paglia,[ENTER]bensì con dei filamenti di cocco. Inoltre è[ENTER]piuttosto rovinato... "
gameforge.new_quest_lv29._066_say = "Puoi eliminare anche i Fanti Selvaggi o i Forti[ENTER]Fanti Selvaggi. Dai loro cappelli si può ricavare[ENTER]una buona quantità di fieno. "
gameforge.new_quest_lv29._067_say_reward = "Finora hai procurato %s di %s mazzetti di fieno. "
gameforge.new_quest_lv29._068_notice = "Hai raccolto un mazzetto di fieno. "
gameforge.new_quest_lv29._070_npcChat = "Il fienile "
gameforge.new_quest_lv29._071_say_reward = "Taurean, prima mi hai detto di essere stato nel[ENTER]fienile, giusto? Hai preso per caso un po' di[ENTER]fieno? "
gameforge.new_quest_lv29._072_say_reward = "Hihi, aspetta... ne avevo dappertutto, persino[ENTER]tra i capelli e nelle scarpe. Vedi? Ce n'è ancora[ENTER]qualche filo. Tieni! "
gameforge.new_quest_lv29._073_notice = "Questo fieno non è molto, ma dovrebbe bastare per[ENTER]un mazzetto. Adesso ne mancano altri %s. "
gameforge.new_quest_lv29._074_say_reward = "Davvero, non sono stato io! Ma Harang non ti ha[ENTER]detto che abbiamo fatto la lotta nel fienile? "
gameforge.new_quest_lv29._075_say_reward = "Prima sei stato nel fienile, vero? Hai preso per[ENTER]caso un po' di fieno? "
gameforge.new_quest_lv29._076_say_reward = "Come fai a sapere del fienile? Mi è rimasto del[ENTER]fieno tra i vestiti? Infatti, eccone alcuni fili! "
gameforge.new_quest_lv29._077_say_reward = "Posso prenderlo? "
gameforge.new_quest_lv29._078_say_reward = "Per me... "
gameforge.new_quest_lv29._079_say_reward = "Adesso non ho proprio tempo. Devo andare da[ENTER]Taurean e cercare di tranquillizzarlo per la[ENTER]storia dei vasi! "
gameforge.new_quest_lv29._080_npcChat = "Fieno per Yonah "
gameforge.new_quest_lv29._081_say_reward = "Dovrei portare del fieno a Yonah, ce n'è ancora[ENTER]nella stalla? "
gameforge.new_quest_lv29._082_say_title = "Stalliere: "
gameforge.new_quest_lv29._083_say_reward = "Non ci resta molto fieno. Gli animali selvatici e[ENTER]i banditi al di fuori del villaggio ci[ENTER]impediscono di tagliarne dell'altro. "
gameforge.new_quest_lv29._084_say_reward = "Però te ne posso dare un po'! "
gameforge.new_quest_lv29._085_notice = "Non è molto, ma per un mazzo basta. Adesso ne[ENTER]servono ancora %s. "
gameforge.new_quest_lv29._086_say_reward = "Purtroppo non posso fare niente per te. "
gameforge.new_quest_lv29._090_send_letter = "Fieno al posto delle botte "
gameforge.new_quest_lv29._091_say = "Hai procurato abbastanza fieno per Yonah.[ENTER]Vai a %s e portaglielo. "
gameforge.new_quest_lv29._101_say_reward = "Taurean è innocente. La porcellana è diventata[ENTER]più fragile a causa dell'energia negativa[ENTER]sprigionata dalle Rocce Metin. "
gameforge.new_quest_lv29._102_say_title = "Yonah: "
gameforge.new_quest_lv29._103_say_reward = "Ma cosa stai dicendo? È un'assurdità! Quel[ENTER]piccolo buono a nulla ti ha mentito! "
gameforge.new_quest_lv29._104_say_reward = "Oggi Taurean e Harang hanno giocato tutto il[ENTER]giorno nel fienile. "
gameforge.new_quest_lv29._105_say_reward = "Assieme ai saluti del tuo amico stalliere, ti[ENTER]porto anche un po' di fieno: con esso potrai[ENTER]proteggere meglio i tuoi vasi. "
gameforge.new_quest_lv29._106_say_reward = "Lo ammetto, un po' mi vergogno per la mia[ENTER]reazione. Ma devi sapere che sono molto[ENTER]affezionato alla mia porcellana, perché è un[ENTER]ricordo della mia famiglia. Se uno dei vasi si[ENTER]rompe, per me è molto più che un oggetto[ENTER]smarrito... "
gameforge.new_quest_lv29._107_say_reward = "Devo proprio scusarmi con te e Taurean. Questa è[ENTER]una piccola ricompensa per i tuoi sforzi! "
gameforge.new_quest_lv29._108_notice = "Ottieni un bonus di %s punti esperienza! "
gameforge.new_quest_lv29._109_say_reward = "Assieme ai saluti del tuo amico stalliere, ti[ENTER]porto anche un po' di fieno: con esso potrai[ENTER]proteggere meglio i tuoi vasi. "
gameforge.new_quest_lv29._110_say_reward = "È davvero gentile da parte tua, ma potresti[ENTER]procurarmi altri %s mazzetti di fieno? Così i[ENTER]miei vasi saranno davvero al sicuro. "
gameforge.new_quest_lv29._111_say_reward = "Credo proprio che sarà difficile trovare[ENTER]dell'altro fieno. Ma per me i miei vasi sono[ENTER]davvero sacri. Te ne sarei molto grato, e ti[ENTER]darei anche qualche Yang in cambio. "
gameforge.new_quest_lv29._112_say_reward = "Quella marmocchia di Harang non può che essersi[ENTER]messa d'accordo con Taurean. "
gameforge.new_quest_lv29._113_say_reward = "A quanto pare nessuno mi crede quando dico che,[ENTER]qualche minuto prima, ho visto il ragazzo[ENTER]aggirarsi nei pressi del magazzino. "
gameforge.new_quest_lv29._114_say_reward = "Va bene così. Questa è una piccola ricompensa per[ENTER]i tuoi sforzi. "
gameforge.new_quest_lv29._115_say_reward = "Ecco, %s Yang. "
