gameforge.new_quest_lv42._026_select = "Vado a cercare il pescatore! "
gameforge.new_quest_lv42._030_target = "Pescatore: "
gameforge.new_quest_lv42._031_say = "Le tranquille giornate in mare aperto sono ormai[ENTER]solo un ricordo! Ogni giorno spariscono centinaia[ENTER]di pesci, è davvero terribile! Da quando le Rocce[ENTER]Metin sono cadute in mare, le pacifiche balene si[ENTER]sono trasformate in feroci mostri. E pescare è[ENTER]diventato davvero pericoloso. "
gameforge.new_quest_lv42._032_say = "Le nostre lance non sono più in grado di ferire[ENTER]le balene. La loro pelle è diventata più dura di[ENTER]quella di un Ogre! "
gameforge.new_quest_lv42._033_say = "Forse Yonah sa dirci cosa fare. È sempre andato[ENTER]in mare da solo ed ha cacciato balene per[ENTER]tantissimi anni. "
gameforge.new_quest_lv42._034_say_title = "Pescatore: "
gameforge.new_quest_lv42._035_say = "Octavio sta aspettando il pesce d'alto mare.[ENTER]Chiedi al pescatore per quale motivo la consegna[ENTER]è così in ritardo. "
