gameforge.new_quest_lv54._010_target = "Comandante "
gameforge.new_quest_lv54._020_say_title = "Il rifornimento "
gameforge.new_quest_lv54._030_say = "In questo momento non è facile tenere alta la[ENTER]motivazione delle nostre truppe, soprattutto ora[ENTER]che vi sono dei notevoli ritardi nella consegna[ENTER]dei rifornimenti. Da alcune settimane la[ENTER]bottegaia, che è la nostra principale fornitrice,[ENTER]ci consegna solo una piccola parte delle pozioni[ENTER]che ci servono. "
gameforge.new_quest_lv54._031_say_reward = "Vai dalla bottegaia e cerca di scoprire qual è il[ENTER]problema. "
gameforge.new_quest_lv54._032_select = "Vi procurerò le bevande! "
gameforge.new_quest_lv54._040_say_title = "Bottegaia: "
gameforge.new_quest_lv54._040_set_title = "Rifornimento "
gameforge.new_quest_lv54._050_target = "Bottegaia: "
gameforge.new_quest_lv54._051_say = "Sì, ultimante sono un po' preoccupato per i[ENTER]rifornimenti. Le rotte commerciali sono diventate[ENTER]piuttosto insicure, i briganti e le bestie[ENTER]selvagge aumentano a vista d'occhio. I mercanti[ENTER]ambulanti, che sono i miei principali fornitori,[ENTER]non hanno alcuna chance contro le tigri. "
gameforge.new_quest_lv54._052_say = "Forse il comandante e i suoi soldati potrebbero[ENTER]aiutarci. Vai da lui, e chiedigli se può metterci[ENTER]a disposizione alcuni soldati per proteggere i[ENTER]mercanti. "
gameforge.new_quest_lv54._054_say = "Vai dalla bottegaia e cerca di scoprire per quale[ENTER]motivo i rifornimenti non sono ancora arrivati. "
gameforge.new_quest_lv54._055_say = "La bottegaia si trova a %s. "
gameforge.new_quest_lv54._056_say_title = "Comandante "
gameforge.new_quest_lv54._060_say = "Rendere più sicure le rotte commerciali? Sì... lo[ENTER]farei volentieri. Ma in questo momento non ho[ENTER]abbastanza soldati per poter proteggere anche i[ENTER]mercanti provenienti dagli altri regni. "
gameforge.new_quest_lv54._061_say = "Inoltre, noi soldati non possiamo assolutamente[ENTER]cacciare le tigri. Solo i cacciatori e la gilda[ENTER]dei cacciatori hanno il permesso di farlo.[ENTER]Violare questa regola vorrebbe dire mettere a[ENTER]repentaglio la tranquillità del villaggio. "
gameforge.new_quest_lv54._062_say_reward = "Dovresti rivolgerti a Yang-Shin. Fa parte della[ENTER]gilda dei cacciatori. Forse lui potrebbe aiutarti[ENTER]a risolvere i problemi con le tigri. "
gameforge.new_quest_lv54._064_say = "Vai dal comandante e cerca di convincerlo a[ENTER]difendere le rotte commerciali dalle tigri. "
gameforge.new_quest_lv54._065_say = "Il capitano si trova a %s. "
gameforge.new_quest_lv54._070_target = "Yang-Shin: "
gameforge.new_quest_lv54._071_say = "Molti dei miei cacciatori sono stati arruolati[ENTER]come combattenti e sono morti in battaglia.[ENTER]Adesso è diventato pressoché impossibile[ENTER]mantenere in equilibrio la popolazione degli[ENTER]animali selvatici. In particolar modo i felini[ENTER]stanno espandendo sempre più il loro territorio,[ENTER]diventando così una vera e propria minaccia per i[ENTER]viaggiatori. "
gameforge.new_quest_lv54._072_say = "Se ci aiuterai in questa faccenda, come[ENTER]ricompensa ti darò questo prezioso Scudo di[ENTER]Tigre+3: era di Mi-Song, uno dei mie compagni[ENTER]morti. Per averlo devi decimare la popolazione[ENTER]delle tigri. "
gameforge.new_quest_lv54._073_say = "Sono soprattutto le Tigri Bianche Maledette ad[ENTER]essersi moltiplicate a vista d'occhio! "
gameforge.new_quest_lv54._074_say = "Portami quanto segue a titolo di prova: "
gameforge.new_quest_lv54._075_say = "Devi cacciare: "
gameforge.new_quest_lv54._076_say = "Cerca Yang-Shin e convincilo a proteggere le[ENTER]rotte commerciali dalle tigri. "
gameforge.new_quest_lv54._077_say_title = "Yang-Shin: "
gameforge.new_quest_lv54._078_say = "Yang-Shin si trova a %s. "
gameforge.new_quest_lv54._080_set_title = "La pelle di tigre "
gameforge.new_quest_lv54._081_notice = "Wow, che magnifico esemplare. Me ne servono altri[ENTER]%s! "
gameforge.new_quest_lv54._082_notice = "Fatto! E adesso vai subito da Yang-Shin. "
gameforge.new_quest_lv54._083_notice = "Purtroppo questa pelle di tigre è inutilizzabile. "
gameforge.new_quest_lv54._084_say = "Ti servono ancora %s Pelli di tigre brillante. "
gameforge.new_quest_lv54._085_say = "Fai bottino delle pelli di tigre brillanti delle[ENTER]Tigri Bianche Maledette. "
gameforge.new_quest_lv54._090_npcChat = "Ho le pelli di tigre. "
gameforge.new_quest_lv54._091_say = "Questi esemplari non bastano. Come faccio a[ENTER]capire se le strade sono di nuovo sicure con così[ENTER]poche pellicce? "
gameforge.new_quest_lv54._092_say = "Finalmente! Con queste pelli farò un sacco di[ENTER]soldi... ehm! ma sei ancora qui? Grazie a te le[ENTER]rotte commerciali sono di nuovo sicure! Sono[ENTER]sicuro che il comandante saprà ricompensarti[ENTER]adeguatamente. "
gameforge.new_quest_lv54._093_say = "E adesso vai. Devo pulire la pelle. "
gameforge.new_quest_lv54._094_say = "Cosa, il comandante non ti da nessuna ricompensa?[ENTER]Non è un mio problema. E se cerchi di diffamarmi[ENTER]per la questione delle pelli: scordatelo, non ho[ENTER]intenzione di restituirle! "
gameforge.new_quest_lv54._095_say_reward = "Ma ti ho promesso uno scudo. E quindi lo avrai.[ENTER]Tanto di questo vecchio aggeggio tutto sporco non[ENTER]me ne faccio più niente. "
gameforge.new_quest_lv54._096_say = "Ricompensa: "
