gameforge.new_quest_lv75._010_target = "Heuk-Young: "
gameforge.new_quest_lv75._020_set_title = "Diario di una Leggenda "
gameforge.new_quest_lv75._021_say = "Ho trovato i resti di un guerriero. Non so se si[ENTER]tratti del Sura che Heuk-Young sta cercando, ma[ENTER]sicuramente le interesseranno. "
gameforge.new_quest_lv75._022_say = "Sicuramente qui è morto più di un Sura. Ho[ENTER]trovato i loro resti. E se questo leggendario[ENTER]guerriero fosse uno di loro? Forse dovrei andare[ENTER]a dirlo a Heuk-Young. "
gameforge.new_quest_lv75._023_say = "Ho trovato un vecchio pezzo di carta congelato in[ENTER]un Golem Ghiaccio degli Abissi. Si legge a[ENTER]malapena, ma potrebbe contenere degli indizi[ENTER]interessanti per Heuk-Young. "
gameforge.new_quest_lv75._024_say = "Nel frattempo ho trovato qualcosa che potrebbe[ENTER]interessare a Heuk-Young. Dovrei andare da lei e[ENTER]portarle questo pezzo di carta. E una volta lì[ENTER]potrei raccontarle anche del cadavere. "
gameforge.new_quest_lv75._025_say = "Heuk-Young mi ha chiesto di aprire bene gli occhi[ENTER]e di captare qualsiasi indizio su questo[ENTER]leggendario Sura. Purtroppo non ho proprio idea[ENTER]di cosa io stia cercando... "
gameforge.new_quest_lv75._030_say = "Vai alla ricerca del leggendario guerriero Akuma,[ENTER]il Sura più famoso di tutti i tempi. Si dice che[ENTER]fosse un guerriero invincibile, e che che abbia[ENTER]sfidato persino l'imperatore. A quanto pare,[ENTER]Akuma scomparve proprio qui tantissimi secoli fa.[ENTER]Cerca attentamente qualsiasi indizio che possa[ENTER]testimoniare il suo passaggio da queste parti. "
gameforge.new_quest_lv75._040_say = "dummy "
gameforge.new_quest_lv75._050_npcChat = "Esamina il Guerriero Caduto "
gameforge.new_quest_lv75._060_say = "Si tratta dei resti di un guerriero. Però il[ENTER]braccio sinistro sembra tutt'altro che umano...[ENTER]deve essere un Sura. Devo riferirlo a Heuk-Young. "
gameforge.new_quest_lv75._070_notice = "La pagina di un libro... congelata nei resti di[ENTER]un Golem Ghiaccio degli Abissi... "
gameforge.new_quest_lv75._090_say = "Neanche io ti so dire con precisione che cosa[ENTER]stiamo cercando esattamente. Guardati in giro e[ENTER]torna da me quando avrai trovato qualcosa. "
gameforge.new_quest_lv75._100_say = "Cosa hai lì con te? Fammi vedere! "
gameforge.new_quest_lv75._105_say = "Delle annotazioni... che scrittura è? Devono[ENTER]essere molto vecchie. Può darsi che ti sia[ENTER]imbattuto in qualcosa di davvero importante. In[ENTER]qualche modo mi sembra di conoscere già le cose[ENTER]che ci sono scritte. Sembra quasi la pagina di un[ENTER]vecchio diario. "
gameforge.new_quest_lv75._109_say = "Se trovassimo più pagine, la cosa avrebbe un[ENTER]senso. Tieni gli occhi aperti, potrebbero esserci[ENTER]altri indizi! "
gameforge.new_quest_lv75._110_say = "Ah, eccoti qui. Hai trovato qualcosa? Racconta! "
gameforge.new_quest_lv75._115_say = "Un Sura morto... può darsi che si tratti di lui,[ENTER]oppure di uno dei suoi seguaci. Ma per essere[ENTER]sicuri ci servono altre prove. Cerca altri indizi[ENTER]che ci permettano di ricostruire la sua identità.[ENTER]Armi, gioielli... annotazioni o cose del genere. "
gameforge.new_quest_lv75._120_say = "Il cadavere di un Sura e la pagina di un libro?[ENTER]Fammi dare un'occhiata alla pergamena... hmm, a[ENTER]quanto pare hai proprio trovato i resti di Akuma.[ENTER]Tuttavia ci servono degli altri indizi. Prova a[ENTER]vedere se riesci a trovare altre pagine del[ENTER]libro. "
gameforge.new_quest_lv75._130_say = "Ho già trovato un indizio, però non posso dire[ENTER]con certezza se questo leggendario Sura sia stato[ENTER]davvero qui. Mi servono maggiori informazioni. "
gameforge.new_quest_lv75._140_1_select = "Bene "
gameforge.new_quest_lv75._140_2_select = "Creatura di ghiaccio "
gameforge.new_quest_lv75._140_3_select = "Ai limiti della disperazione "
gameforge.new_quest_lv75._140_4_select = "Terrore oscuro "
