gameforge.new_quest_premium_lv4._110_say = "Cerca il mercante d'armi e chiedigli per quale[ENTER]motivo la sera non canta più sotto la finestra[ENTER]della bottegaia. "
gameforge.new_quest_premium_lv4._120_qSetTitle = "Raccogli lillà "
