gameforge.new_quest_premium_lv4._150_notice = "Purtroppo questo Ramo di Lillà è già appassito. "
gameforge.new_quest_premium_lv4._160_say = "Ti serve ancora la seguente quantità di Rami di[ENTER]Lillà: %s "
gameforge.new_quest_premium_lv4._170_say = "Non è abbastanza. Questo lillà basta a malapena[ENTER]per un paio di gocce!! "
gameforge.new_quest_premium_lv4._180_say = "Oh, il lillà! Però mi servirebbe anche uno[ENTER]speciale olio al muschio. Si può comprare solo in[ENTER]un negozio speciale. Una moneta roteante ti[ENTER]indicherà la strada. Ti servono dei Buoni del[ENTER]Drago - sicuramente ne hai ancora qualcuno da[ENTER]parte, no?[ENTER]Te ne sarei davvero grato! Puoi ritirare l'ordine[ENTER]dall'amministratore dei magazzini. A quel punto[ENTER]avrei finalmente tutti gli ingredienti! "
gameforge.new_quest_premium_lv4._190_say = "Per favore, affrettati e portami l'olio al[ENTER]muschio. In cambio otterrai una ricompensa! "
gameforge.new_quest_premium_lv4._200_say_reward = "Continua a cacciare fino a quando non avrai[ENTER]procurato %s ramoscelli di lillà! "
gameforge.new_quest_premium_lv4._20_sayTitle = "Il mio tesoro "
gameforge.new_quest_premium_lv4._210_say = "Uccidi i Lupi Blu per ottenerli. "
gameforge.new_quest_premium_lv4._220_say = "Ecco fatto. E ora torna dal mercante d'armi. "
gameforge.new_quest_premium_lv4._230_qSetTitle = "L'olio al muschio "
gameforge.new_quest_premium_lv4._240_say = "Ti ringrazio davvero di cuore. Ora posso[ENTER]finalmente produrre un nuovo olio profumato. "
gameforge.new_quest_premium_lv4._250_say = "Riferisci al mio passerotto che oggi canterò[ENTER]sotto la sua finestra. Se ne rallegrerà. "
gameforge.new_quest_premium_lv4._260_say = "Ti sei meritato una ricompensa: "
gameforge.new_quest_premium_lv4._270_say_reward = "Yang: %d "
gameforge.new_quest_premium_lv4._280_say = "Senza l'Olio al Muschio non posso produrre il mio[ENTER]olio profumato. Fatti guidare dalla moneta[ENTER]roteante e portamene un esemplare! "
gameforge.new_quest_premium_lv4._290_say_reward = "Torna con %s unità di Olio al Muschio! "
gameforge.new_quest_premium_lv4._300_say = "Devo portare l'olio al muschio al mercante[ENTER]d'armi. Devo seguire la moneta roteante, ordinare[ENTER]una pozione e poi andarla a prendere[ENTER]dall'amministratore del magazzino. "
gameforge.new_quest_premium_lv4._30_say = "Mi manca il mio tesoro, il mercante d'armi. Per[ENTER]settimane intere ogni sera ha cantato sotto la[ENTER]mia finestra, ma da alcuni giorni non ho più sue[ENTER]notizie. Forse teme la collera di mio padre. Te[ENTER]la senti di scoprire cosa gli è successo? "
gameforge.new_quest_premium_lv4._310_say_title = "Il mercante d'armi "
gameforge.new_quest_premium_lv4._320_say = "Oh, grazie mille. Quindi dici che era solo[ENTER]impossibilitato? La cosa mi tranquillizza. Questa[ENTER]sera lo aspetterò. "
gameforge.new_quest_premium_lv4._330_say_reward = "Per il tuo impegno ottieni: %s EXP "
gameforge.new_quest_premium_lv4._340_say = "Torna subito dalla bottegaia. "
gameforge.new_quest_premium_lv4._350_npcChat = "Lillà "
gameforge.new_quest_premium_lv4._35_qSetTitle = "Parla con il Mercante d'Armi "
gameforge.new_quest_premium_lv4._360_npcChat = "L'olio al muschio "
gameforge.new_quest_premium_lv4._40_qSetTitle = "Un nuovo Profumo "
gameforge.new_quest_premium_lv4._45_qSetTitle = "Caccia i Lupi Blu "
gameforge.new_quest_premium_lv4._50_targetVid = "Mercante di armi: "
gameforge.new_quest_premium_lv4._55_qSetTitle = "Torna del Mercante d'Armi "
gameforge.new_quest_premium_lv4._60_sayTitle = "Il briccone "
gameforge.new_quest_premium_lv4._65_qSetTitle = "Porta l'Olio al Muschio al Mercante d'Armi "
gameforge.new_quest_premium_lv4._70_say = "È vero, non canto più sotto la sua finestra[ENTER]perché...uffa! Un po' mi imbarazza parlarne![ENTER]Insomma...col passare del tempo l'odore del mio[ENTER]corpo non è più così gradevole. Sì, mi lavo[ENTER]spesso, ma l'odore rimane. Ho intenzione di[ENTER]produrre un olio dal profumo aspro e[ENTER]affascinante, con il quale sedurre la bottegaia,[ENTER]il mio passerotto. È una donna che tiene molto[ENTER]all'aspetto. "
gameforge.new_quest_premium_lv4._75_qSetTitle = "Vai dalla Bottegaia "
gameforge.new_quest_premium_lv4._80_say = "Devo produrre assolutamente dell'olio profumato.[ENTER]Solo così potrò eliminare il cattivo odore del[ENTER]mio corpo. "
gameforge.new_quest_premium_lv4._90_say = "Per ottenerlo devo mischiare diversi ingredienti.[ENTER]Innanzitutto questi: "
gameforge.notify_test_server = {}
