gameforge.subquest_37._280_npcChat = "Ho il libro "
gameforge.subquest_37._290_say = "Evviva! Finalmente il mio nuovo libro! Grazie[ENTER]mille. Ci sono così tanti libri che ancora non ho[ENTER]letto...[ENTER]Ti ho scritto tutti i passaggi per produrre le[ENTER]porcellane Celadon. Seguile alla lettera, e ne[ENTER]produrrai di fantastiche![ENTER]Ora lasciami leggere.[ENTER]Alla prossima. "
gameforge.subquest_37._300_makequestbutton = "Torna da Yonah "
gameforge.subquest_37._30_say = "Uhm...non va bene...e nemmeno questa...[ENTER]Oh ciao! Guarda, produco porcellane[ENTER]ininterrottamente, ma molte si rompono troppo[ENTER]facilmente. Penso sia tempo di ricercarne[ENTER]qualcuna di più resistente fattura... "
