gameforge.subquest_37._330_say = "Oh!!! C'è scritto davvero tutto!Con questi[ENTER]appunti le produrrò in un batter d'occhio.[ENTER]Cambierò la storia delle porcellane. Te ne potrò[ENTER]mostrare alcune molto presto, puoi contarci! Se[ENTER]ne parlerà per secoli! "
gameforge.subquest_37._340_sayReward = "Hai ricevuto 2.500.000 punti esperienza "
gameforge.subquest_37._350_sayReward = "Hai ricevuto 35.000 Yang "
