gameforge.fortune_telling._10_npcChat = "Ouvi dizer que prevês a sorte. "
gameforge.fortune_telling._110_say = "És demasiado novo para suspirares dessa maneira![ENTER]Queres dar uma vista de olhos no teu futuro?[ENTER]Devias saber que há por aí poderes que podem[ENTER]dar-te as respostas que precisas para recuperar[ENTER]alguma esperança. "
gameforge.fortune_telling._120_select = "Diz-me a minha sorte "
gameforge.fortune_telling._130_select = "Não preciso que me digam a minha sorte "
