gameforge.goldbar._120_say = "Diz-me se estiveres interessado. "
gameforge.goldbar._130_say = "A sério? Então temos acordo? Simplesmente diz-me[ENTER]e eu vou buscar-te uma! "
gameforge.goldbar._140_npcChat = "Quero comprar uma Barra d'Ouro. "
gameforge.goldbar._150_say = "Do que falas? Não sei nada acerca do que estás a[ENTER]dizer. "
gameforge.goldbar._160_say = "O quê? Queres comprar Barras d'Ouro? Não, agora[ENTER]não! O Clã Vento Negro parece andar desconfiado.[ENTER]Se não tiver cuidado irei arriscar a minha vida![ENTER]Vou tentar de novo quando a situação estiver mais[ENTER]calma. "
gameforge.goldbar._170_sayReward = "De momento não podes comprar Barras d'Ouro. "
gameforge.goldbar._180_say = "Ainda não consegui arranjar uma Barra d'Ouro. "
gameforge.goldbar._190_say = "Temos de esperar %s %s "
gameforge.goldbar._200_say = "Trouxe uma Barra d'Ouro. Custa 2.2 Milhões de[ENTER]Yang. 200.000 Yang são o meu pagamento. E que[ENTER]tal? Estás interessado? "
gameforge.goldbar._20_sayTitle = "Traidor Balso: "
gameforge.goldbar._210_say = "Aqui tens uma Barra d'Ouro! Fizeste uma decisão[ENTER]sábia. É sempre uma boa ideia fazer[ENTER]investimentos. "
gameforge.goldbar._220_say = "Não tens Yang suficiente! Custa 2.2 Milhões de[ENTER]Yang. "
gameforge.goldbar._230_say = "Porque estás aqui se não queres comprar nada?[ENTER]Fizeste-me perder tempo. Desaparece! "
gameforge.goldbar._240_npcChat = "GM: Reiniciar tempo de venda Barra d'Ouro "
gameforge.goldbar._250_say = "O tempo para a venda de uma Barra d'Ouro foi[ENTER]reiniciado. "
gameforge.goldbar._260_npcChat = "Vender uma Barra d'Ouro "
gameforge.goldbar._270_say = "Do que falas? Não sei nada acerca do que estás a[ENTER]dizer. "
