gameforge.locale.old_pirate.do_you_mean_this_medicine = "Aqui está a cura! "
gameforge.locale.old_pirate.etc_shop_start_say = "Loja Geral:[ENTER]O Velho Pirata? Claro que o conheço! Estás a[ENTER]falar do tipo que está sempre a dizer asneiras e[ENTER]a coçar-se? Ele costuma pedir-me remédios. Ele[ENTER]até gastou vários milhares de Yangs para[ENTER]conseguir comprar Poções Vermelhas! "
gameforge.locale.old_pirate.etc_shop_start_say2 = "Negociadora da Loja Geral:[ENTER]Onde raio ele consegue tanto Yang se ele nem anda[ENTER]a trabalhar? Já lhe disse inúmeras vezes que ele[ENTER]devia deixar um doutor examiná-lo, mas ele não[ENTER]liga ao que eu digo.[ENTER]A cura vermelha apenas alivia os sintomas, mas[ENTER]não cura a doença. Penso que isto não vai acabar[ENTER]bem para ele. "
gameforge.locale.old_pirate.etc_shop_talk_about_old_pirate = "Conheces o Velho Pirata? "
gameforge.locale.old_pirate.order_accept = "Irei ajudar-te. "
gameforge.locale.old_pirate.order_accept_answer = "Velho Pirata:[ENTER]Ainda bem! Boa Sorte![ENTER]Penso que o médico esteja na vila. "
gameforge.locale.old_pirate.order_find_medicine_label = "Remédio para o Velho Pirata "
gameforge.locale.old_pirate.order_find_medicine_text_1 = "Obtém o Remédio para o Velho Pirata. "
gameforge.locale.old_pirate.order_find_medicine_text_2 = "Procura o Baek-Go na vila e pergunta-lhe sobre o[ENTER]Remédio para o Velho Pirata. "
gameforge.locale.old_pirate.order_refuse = "Desculpa, mas não consigo. "
gameforge.locale.old_pirate.order_refuse_answer = "Velho Pirata:[ENTER]Bom, então não tenho mais nada para te dizer! "
gameforge.locale.old_pirate.order_say1 = "Velho Pirata:[ENTER]Raios, o que queres de mim? Bolas! Aquela mulher[ENTER]chata da Loja Geral contou-te sobre mim, não foi?[ENTER]Sim, tudo o que ela disse é verdade. Mas não[ENTER]tenho tempo para ir ver um médico. Tenho tantos[ENTER]inimigos! "
gameforge.locale.old_pirate.order_say2 = "Velho Pirata:[ENTER]Nunca se sabe se um desses patifes não te aparece[ENTER]à frente do nada para te atacar! Mas esta[ENTER]comichão é horrível; não aguento mais! Podes ir[ENTER]ter com o médico e pedir-lhe um remédio? Que tal?[ENTER]Se o fizeres irei dar-te uma boa recompensa. "
gameforge.locale.old_pirate.order_too_late = "Velho Pirata:[ENTER]Outra pessoa trouxe-me o Remédio primeiro,[ENTER]portanto... não irás receber a recompensa! "
gameforge.locale.old_pirate.start_find_old_pirate_label = "Vai falar com o Velho Pirata. "
gameforge.locale.old_pirate.start_find_old_pirate_text_1 = "Vai falar com o Velho Pirata. "
gameforge.locale.old_pirate.start_find_old_pirate_text_2 = "Vai perguntar ao Velho Pirata a razão de não ir[ENTER]ver um médico. "
gameforge.locale.old_pirate.start_say = "Velho Pirata:[ENTER]Tenho comichão em todo o lado! Eu nunca deveria[ENTER]ter dado um encontrão naquele homem! Nós quase[ENTER]que nem nos tocamos! Desde esse momento o local[ENTER]do encontrão ulcerou e tenho comichão em todo o[ENTER]meu corpo! Parece que estou a apodrecer vivo! "
gameforge.locale.old_pirate.success_thanks = "Velho Pirata:[ENTER]O quê? Fui infectado pela doença misteriosa?! E[ENTER]este é o Remédio necessário? Muito, muito[ENTER]obrigado! Provavelmente salvaste a minha vida.[ENTER]Fico agradecido. "
gameforge.locale.old_pirate.talk = "És o Velho Pirata? "
gameforge.locale.old_pirate.talk_about_medicine = "Porque é que não vai ver um médico? "
