gameforge.main_quest_lv10._130_sayTitle = "Yu-Hwan: "
gameforge.main_quest_lv10._140_say = "A vida é um sonho, tal como tudo o resto. De onde[ENTER]vimos e para onde vamos? O mundo está envolto em[ENTER]conflito. O trabalho honesto por si só não chega.[ENTER]Estás aqui para ouvir a minha música? "
