gameforge.main_quest_lv92._070_say = "Eles estão unidos por um objectivo em comum: ouro.O acampamento dos Ladrões Rubros não fica longe[ENTER]daqui. Derrota o Ladrão Rubro Executor e o[ENTER]General Negro. Um deles está na posse da carta. "
gameforge.main_quest_lv92._080_say = "No entanto eles foram avisados atempadamente[ENTER]sobre as nossas intenções e separaram a carta em[ENTER]quatro partes. Temos de encontrá-las todas. "
gameforge.main_quest_lv92._090_say = "Entendido. "
gameforge.main_quest_lv92._100_say = "Obrigado! "
