gameforge.main_quest_lv92._120_say = "Luta contra o Ladrão Rubro Executor e o General[ENTER]Negro. Encontra todos os quatro pedaços da carta. "
gameforge.main_quest_lv92._130_say = "A carta ainda não está completa. Encontra todos[ENTER]os pedaços. "
gameforge.main_quest_lv92._140_1_notice = "Encontraste todas as partes da Carta dos Ladrões[ENTER]Rubros. "
gameforge.main_quest_lv92._140_2_notice = "Mostra-a ao Almirante Angmur. "
gameforge.main_quest_lv92._150_say = "Fala com o Almirante Angmur. "
gameforge.main_quest_lv92._160_say = "Bom trabalho. Aceita isto como sinal da minha[ENTER]gratidão. "
gameforge.main_quest_lv92._170_sendLetter = "A Carta dos Ladrões Rubros "
gameforge.main_quest_lv92._180_say = "O Almirante Angmur precisa da tua ajuda. "
gameforge.main_quest_lv92._190_say = "Ainda bem que aqui estás. Temos um problema. "
gameforge.main_quest_lv92._200_say = "O que se passa? "
gameforge.main_quest_lv92._210_say = "Na realidade, existe um quinto pedaço da Carta[ENTER]dos Ladrões Rubros. Sem esse pedaço, o resto da[ENTER]carta de nada nos vale. Já enviei o Bae-Yong, um[ENTER]dos meus homens mais corajosos, mas deve ter[ENTER]acontecido algo. Ele já deveria ter regressado há[ENTER]muito tempo. Podes procurá-lo? "
gameforge.main_quest_lv92._220_say = "Claro! Fazes ideia de onde ele possa estar agora? "
gameforge.main_quest_lv92._230_say = "Tenho uma ideia, sim. Da última vez que[ENTER]comunicámos, ele estava a pensar em ir até à Baía[ENTER]de Nephrite. Mas ninguém tem a certeza se ele[ENTER]terá ou não chegado lá. "
gameforge.main_quest_lv92._240_say = "Vou pôr-me já a caminho! "
gameforge.main_quest_lv92._250_sayTitle = "O Soldado Desaparecido "
gameforge.main_quest_lv92._260_say = "Encontra o quinto pedaço da carta. Para isso,[ENTER]procura pelo Bae-Yong na Baía de Nephrite. "
gameforge.main_quest_lv92._270_say = "Ainda não encontraste o Bae-Yong? Isto é uma[ENTER]questão de vida ou morte! Por favor continua a[ENTER]procurar. "
gameforge.main_quest_lv92._280_say = "*tosse*[ENTER]Obrigado por me encontrares. Fui gravemente[ENTER]ferido em combate e acho que não me sobra muito[ENTER]mais tempo...[ENTER]Toma, consegui obter isto de um Ladrão Rubro.[ENTER]Leva-o ao Almirante. Vai! Eu fico b...[ENTER](Ele morre nos teus braços.) "
gameforge.main_quest_lv92._290_1_notice = "Antes de Bae-Yong morrer, ele deu-te o pedaço da[ENTER]carta que faltava. "
gameforge.main_quest_lv92._290_2_notice = "Regressa ao Almirante Angmur. "
gameforge.main_quest_lv92._300_say = "Estás de volta! Encontraste o Bae-Yong? "
gameforge.main_quest_lv92._310_say = "Sim, mas... "
gameforge.main_quest_lv92._320_say = "Mas?[ENTER]Oh não... Ele está morto? "
gameforge.main_quest_lv92._330_say = "Não podia faz mais por ele. Os meus pêsames. Ele[ENTER]era um homem corajoso  ele defendeu o último[ENTER]pedaço da carta com a sua vida. Aqui está. "
gameforge.main_quest_lv92._340_say = "A perda do Bae-Yong irá afectar-nos imenso. Mas[ENTER]tenho imenso orgulho nele. Ele completou a sua[ENTER]missão  temos agora todos os pedaços da carta. "
gameforge.main_quest_lv92._350_say = "Agradeço-te por tudo. Toma, aceita isto como[ENTER]recompensa.[ENTER]Vou agora acender algum incenso no templo pelo[ENTER]Bae-Yong. Mandarei chamar-te mais tarde. "
gameforge.main_quest_lv92._360_say = "Vai ter com o Almirante Angmur. "
gameforge.main_quest_lv92._370_sendLetter = "Descodifica a Carta dos Ladrões Rubros "
gameforge.main_quest_lv92._380_say = "O Almirante Angmur tem informações importantes[ENTER]sobre a Carta dos Ladrões Rubros. Ouve o que ele[ENTER]tem para te dizer. "
gameforge.main_quest_lv92._390_say = "Ah, aqui estás tu. Os Servos das Trevas estão a[ENTER]dar-me uma dor de cabeça. Esta carta também se[ENTER]encontra encriptada. Por favor leva-a ao[ENTER]Yon-I-Walker. "
gameforge.main_quest_lv92._400_say = "Entendido. "
gameforge.main_quest_lv92._410_say = "Leva a carta encriptada ao Yon-I-Walker. "
gameforge.main_quest_lv92._420_say = "Argh! Tens sempre de te esgueirar até mim dessa[ENTER]forma?! O que posso fazer por ti? "
gameforge.main_quest_lv92._430_say = "Aqui está a Carta dos Ladrões Rubros. Tal como as[ENTER]outras, esta está encriptada. "
gameforge.main_quest_lv92._440_say = "Deixa-me dar uma vista de olhos. Hmm, isto[ENTER]parecem ser os caracteres dos Tritões. Não deverá[ENTER]ser muito difícil descodificá-los. "
gameforge.main_quest_lv92._450_say = "Excelente. Durante quanto tempo precisas dela? "
gameforge.main_quest_lv92._460_say = "Não tenhas tanta pressa. A descodificação não é[ENTER]particularmente complicada, mas sem mais recursos[ENTER]isto não irá funcionar. "
gameforge.main_quest_lv92._470_say = "Mas é claro que não... Isso seria demasiado fácil. "
gameforge.main_quest_lv92._480_say = "Vejo que não perdeste o teu sentido de humor,[ENTER]hehe.[ENTER]Para descodificar a carta preciso de Escamas de[ENTER]Oin. Dos membros da tribo dos Tritões, apenas o[ENTER]General Kappa e o próprio Tritão podem usar estas[ENTER]raras escamas. Por favor obtém dez. "
gameforge.main_quest_lv92._490_say = "Vou já pôr-me a caminho. "
gameforge.main_quest_lv92._500_sayTitle = "Encontra Escamas de Oin! "
gameforge.main_quest_lv92._510_say = "Apenas o Tritão, Rei dos Tritões, e o General[ENTER]Kappa usam Escamas de Oin. "
gameforge.main_quest_lv92._520_say = "Não consigo descodificar a carta sem as escamas.[ENTER]Preciso delas urgentemente. "
gameforge.main_quest_lv92._530_1_notice = "Encontraste as Escamas de Oin tritónicas. "
gameforge.main_quest_lv92._530_2_notice = "Leva-as rapidamente ao Yon-I-Walker. "
gameforge.main_quest_lv92._540_say = "Fizeste um óptimo trabalho. Assim que tiver[ENTER]descodificado a carta irei informar o Almirante[ENTER]Angmur. Devias descansar um pouco.[ENTER]Toma, aceita isto como sinal do meu apreço. "
gameforge.main_quest_lv92._550_say = "O Yon-I-Walker gostaria de falar contigo. "
gameforge.main_quest_lv92._560_sendLetter = "Planos Sinistros "
gameforge.main_quest_lv92._570_say = "A carta dos Servos das Trevas foi descodificado.[ENTER]O Almirante Angmur quer falar contigo sobre os[ENTER]seus planos sinistros. "
gameforge.main_quest_lv92._580_say = "Conseguimos descodificar completamente a mensagem[ENTER]secreta. Lembras-te da carta enviada aos Gnoll?[ENTER]Ela já falava de dragões, adormecidos nas[ENTER]profundezas da ilha, assim como de um método de[ENTER]os acordar e enraivecer. Agora descobrimos a[ENTER]razão pela qual os Servos das Trevas estão tão[ENTER]interessados nisto. "
gameforge.main_quest_lv92._590_say = "Se matares um dragão nesse momento, quando a sua[ENTER]mente se encontra num estado tão negro quanto as[ENTER]mandíbulas do Inferno, ele irá cuspir uma Pérola[ENTER]da Noite. Com a ajuda desta pérola, os poderes[ENTER]incomensuráveis de Sung Mahi poderão ser[ENTER]invocados para o nosso mundo. Esse é o seu plano[ENTER]sinistro! "
gameforge.main_quest_lv92._600_say = "Os nossos próximos passos precisam de ser bem[ENTER]ponderados. Assim que tiver novidades para ti,[ENTER]mandarei chamar-te. Obrigado por tudo. "
gameforge.main_quest_lv92._610_say = "A aventura continuará assim que tiveres atingido[ENTER]o nível 93. "
