gameforge.new_christmas._040_say = "Se tiveres mais questões não hesites em[ENTER]perguntar-me. Feliz Natal! "
gameforge.new_christmas._050_npcChat = "Gostarias de um pouco de Vinho Quente? "
gameforge.new_christmas._060_say = "Ho ho o! Certamente sabes o que isso é, correcto?[ENTER]Não existe bebida melhor para o Inverno! Misturas[ENTER]um bom vinho, uma pitada de canela e um pouco de[ENTER]cravo... mmmmm! "
