gameforge.new_quest_lv29._035_say_reward = "O que fizeste aos vasos do Yonah? "
gameforge.new_quest_lv29._036_say_reward = "Vasos? Yonah? Não sei do que estás a falar! "
gameforge.new_quest_lv29._037_say_reward = "Então fazes o favor de me explicar de novo porque[ENTER]é que três dos vasos estão partidos? "
