gameforge.new_quest_lv42._042_say = "É preciso uma técnica especial para endurecer[ENTER]lanças para caça às baleias. A única pessoa que[ENTER]consegue fazer este tipo de lança é um Ferreiro. "
gameforge.new_quest_lv42._043_say_reward = "Dirige-te ao Ferreiro "
