gameforge.subquest_23._40_say = "Estou preocupada com ele porque nunca mais recebi[ENTER]notícias. Desculpa, mas podes procurar o meu[ENTER]irmão de novo? O Yu-Hwan pode saber alguma coisa[ENTER]dele. "
gameforge.subquest_23._50_select = "Recusar "
